mapper.select:
		select  org.org_city_id,
		org.org_city_name,
		t.doctor_type_id,
		count(1) num
		FROM jsres_recruit_info T
		left join sys_org org
		on t.org_flow = org.org_flow
		WHERE T.RECORD_STATUS = 'Y'

		and t.org_flow in(
		SELECT ORG_FLOW
        FROM SYS_ORG
        WHERE SYS_ORG.RECORD_STATUS='Y'

			AND ORG_PROV_ID = #{orgProvId}


			AND ORG_CITY_ID = #{orgCityId}


			AND ORG_FLOW = #{orgFlow}


			AND ORG_LEVEL_ID = #{orgLevel}


			AND ORG_TYPE_ID = #{orgTypeId}

		)

			and t.ORG_FLOW in
			#{item}


			and t.DOCTOR_TYPE_ID in
			#{item}


			and t.SESSION_NUMBER in
			#{item}


			and t.SESSION_NUMBER = #{sessionNumber}


			and t.CAT_SPE_ID = #{trainingTypeId}


			and t.SPE_ID = #{trainingSpeId}


			and t.ORG_FLOW = #{orgFlow}


			and t.GRADUATION_YEAR=#{graduationYear}

		and t.session_number is not null
		AND t.DOCTOR_TYPE_ID IN ('Company', 'CompanyEntrust', 'Social', 'Graduate')
		group by org_city_id,
		org_city_name,
		doctor_type_id
		order by org_city_id

		select  org.org_city_id,
		org.org_city_name,
		t.SPE_ID,
		count(1) num
		FROM jsres_recruit_info T
		left join sys_org org
		on t.org_flow = org.org_flow
		WHERE T.RECORD_STATUS = 'Y'

		and t.org_flow in(
		SELECT ORG_FLOW
        FROM SYS_ORG
        WHERE SYS_ORG.RECORD_STATUS='Y'

			AND ORG_PROV_ID = #{orgProvId}


			AND ORG_CITY_ID = #{orgCityId}


			AND ORG_FLOW = #{orgFlow}


			AND ORG_LEVEL_ID = #{orgLevel}


			AND ORG_TYPE_ID = #{orgTypeId}

		)

			and t.ORG_FLOW in
			#{item}


			and t.DOCTOR_TYPE_ID in
			#{item}


			and t.SESSION_NUMBER in
			#{item}


			and t.SESSION_NUMBER = #{sessionNumber}


			and t.CAT_SPE_ID = #{trainingTypeId}


			and t.SPE_ID = #{trainingSpeId}


			and t.ORG_FLOW = #{orgFlow}


			and t.GRADUATION_YEAR=#{graduationYear}

		and t.session_number is not null
		group by org_city_id,
		org_city_name,
		SPE_ID
		order by org_city_id

SELECT ORG.JOINT_ORG_FLOW, ORG.ORG_FLOW, org.org_city_id, ed.DOCTOR_TYPE_ID, COUNT(1) num FROM jsres_recruit_doc_info T LEFT JOIN jsres_recruit_info ed ON t.doctor_flow = ed.doctor_flow AND t.session_number = ed.session_number left join (SELECT JIORG.JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and org.org_level_id = 'CountryOrg' UNION SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where record_status='Y'and org.org_level_id in ( 'CountryOrg','ProvinceOrg') UNION SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where record_status='Y'and org.org_flow not in ( SELECT JIORG.JOINT_ORG_FLOW FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW LEFT JOIN SYS_ORG SO ON SO.ORG_FLOW = JIORG.JOINT_ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and so.record_status='Y' and org.org_level_id = 'CountryOrg') and org.org_flow not in ( SELECT ORG.ORG_FLOW FROM SYS_ORG ORG where record_status='Y' and org.org_level_id in ( 'CountryOrg','ProvinceOrg'))) ORG on t.org_flow = org.JOINT_ORG_FLOW WHERE T.RECORD_STATUS = 'Y' AND ORG.JOINT_ORG_FLOW IN (SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y'
AND ORG_PROV_ID = #{orgProvId}
AND ORG_CITY_ID = #{orgCityId}
AND ORG_FLOW = #{orgFlow}
AND ORG_LEVEL_ID = #{orgLevel}
AND ORG_TYPE_ID = #{orgTypeId} )
and t.ORG_FLOW in #{item}
and ed.DOCTOR_TYPE_ID in #{item}
and t.SESSION_NUMBER in #{item}
and t.SESSION_NUMBER = #{sessionNumber}
and t.CAT_SPE_ID = #{trainingTypeId}
and t.SPE_ID = #{trainingSpeId}
and t.ORG_FLOW = #{orgFlow}
and t.GRADUATION_YEAR=#{graduationYear}
and t.session_number is not null AND ed.DOCTOR_TYPE_ID IN ('Company', 'CompanyEntrust', 'Social', 'Graduate') AND t.TRAIN_YEAR IN ('OneYear', 'TwoYear', 'ThreeYear') AND t.AUDIT_STATUS_ID = 'Passed' group by ORG.JOINT_ORG_FLOW, ORG.ORG_FLOW,org.org_city_id, ed.DOCTOR_TYPE_ID order by org.org_city_id, ed.DOCTOR_TYPE_ID

SELECT ORG.JOINT_ORG_FLOW, ORG.ORG_FLOW, T.DOCTOR_TYPE_ID, COUNT(1) num FROM jsres_recruit_info T left join (SELECT JIORG.JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and org.org_level_id = 'CountryOrg' UNION SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME FROM SYS_ORG ORG where record_status='Y'and org.org_level_id in ( 'CountryOrg','ProvinceOrg') UNION SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME FROM SYS_ORG ORG where record_status='Y' and org.org_flow not in ( SELECT JIORG.JOINT_ORG_FLOW FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW LEFT JOIN SYS_ORG SO ON SO.ORG_FLOW = JIORG.JOINT_ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and so.record_status='Y' and org.org_level_id = 'CountryOrg') and org.org_flow not in ( SELECT ORG.ORG_FLOW FROM SYS_ORG ORG where record_status='Y' and org.org_level_id in ( 'CountryOrg','ProvinceOrg'))) ORG on t.org_flow = org.JOINT_ORG_FLOW WHERE T.RECORD_STATUS = 'Y'
and t.ORG_FLOW IN #{item}
and t.DOCTOR_TYPE_ID in #{item}
and t.SESSION_NUMBER in #{item}
and t.SESSION_NUMBER = #{sessionNumber}
and t.CAT_SPE_ID = #{trainingTypeId}
and t.SPE_ID = #{trainingSpeId}
and t.ORG_FLOW = #{orgFlow}
and t.GRADUATION_YEAR=#{graduationYear}
and t.session_number is not null group by ORG.JOINT_ORG_FLOW, ORG.ORG_FLOW, T.DOCTOR_TYPE_ID order by ORG.JOINT_ORG_FLOW, ORG.ORG_FLOW,T.DOCTOR_TYPE_ID

