SELECT T.CAT_SPE_ID, T.DOCTOR_TYPE_ID, T.SPE_ID, COUNT(1) num FROM jsres_recruit_info T LEFT JOIN RES_DOCTOR_RECRUIT RDR ON RDR.doctor_flow = t.doctor_flow left join (SELECT JIORG.JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and org.org_level_id = 'CountryOrg' UNION SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where record_status='Y'and org.org_level_id in ( 'CountryOrg','ProvinceOrg') UNION SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where record_status='Y'and org.org_flow not in ( SELECT JIORG.JOINT_ORG_FLOW FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW LEFT JOIN SYS_ORG SO ON SO.ORG_FLOW = JIORG.JOINT_ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and so.record_status='Y' and org.org_level_id = 'CountryOrg') and org.org_flow not in ( SELECT ORG.ORG_FLOW FROM SYS_ORG ORG where record_status='Y' and org.org_level_id in ( 'CountryOrg','ProvinceOrg'))) ORG on t.org_flow = org.JOINT_ORG_FLOW WHERE T.RECORD_STATUS = 'Y' AND RDR.RECORD_STATUS = 'Y' AND RDR.AUDIT_STATUS_ID = 'Passed' AND RDR.DOCTOR_STATUS_ID NOT IN ('Passed','NotPassed','Auditing','OrgAuditing')
AND rdr.doctor_status_id = #{statusId}
AND ORG.JOINT_ORG_FLOW IN (SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y'
AND ORG_PROV_ID = #{orgProvId}
AND ORG_CITY_ID = #{orgCityId}
AND ORG_FLOW = #{orgFlow}
AND ORG_LEVEL_ID = #{orgLevel}
AND ORG_TYPE_ID = #{orgTypeId} )
and t.ORG_FLOW in #{item}
and t.DOCTOR_TYPE_ID in #{item}
and t.SESSION_NUMBER in #{item}
and t.SESSION_NUMBER = #{sessionNumber}
and t.CAT_SPE_ID = #{trainingTypeId}
and t.SPE_ID = #{trainingSpeId}
and t.ORG_FLOW = #{orgFlow}
and t.GRADUATION_YEAR=#{graduationYear}
and t.session_number is not null group by T.CAT_SPE_ID, T.DOCTOR_TYPE_ID, T.SPE_ID order by T.CAT_SPE_ID, T.DOCTOR_TYPE_ID, T.SPE_ID

SELECT T.CAT_SPE_ID, rd.DOCTOR_TYPE_ID, T.SPE_ID, COUNT(1) num FROM RES_DOCTOR_RECRUIT T LEFT JOIN RES_DOCTOR RD ON RD.doctor_flow = t.doctor_flow left join (SELECT JIORG.JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and org.org_level_id = 'CountryOrg' UNION SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where record_status='Y'and org.org_level_id in ( 'CountryOrg','ProvinceOrg') UNION SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where record_status='Y'and org.org_flow not in ( SELECT JIORG.JOINT_ORG_FLOW FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW LEFT JOIN SYS_ORG SO ON SO.ORG_FLOW = JIORG.JOINT_ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and so.record_status='Y' and org.org_level_id = 'CountryOrg') and org.org_flow not in ( SELECT ORG.ORG_FLOW FROM SYS_ORG ORG where record_status='Y' and org.org_level_id in ( 'CountryOrg','ProvinceOrg'))) ORG on t.org_flow = org.JOINT_ORG_FLOW WHERE T.RECORD_STATUS = 'Y' AND RD.RECORD_STATUS = 'Y' AND T.AUDIT_STATUS_ID = 'Passed' AND T.DOCTOR_STATUS_ID NOT IN ('Passed','NotPassed','Auditing','OrgAuditing')
AND rd.doctor_status_id = #{statusId}
AND rd.doctor_status_id in ('20','21','22')
AND ORG.JOINT_ORG_FLOW IN (SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y'
AND ORG_PROV_ID = #{orgProvId}
AND ORG_CITY_ID = #{orgCityId}
AND ORG_FLOW = #{orgFlow1}
AND ORG_LEVEL_ID = #{orgLevel}
AND ORG_TYPE_ID = #{orgTypeId} )
and t.ORG_FLOW in #{item}
and RD.DOCTOR_TYPE_ID in #{item}
and t.SESSION_NUMBER in #{item}
and t.SESSION_NUMBER = #{sessionNumber}
and t.CAT_SPE_ID = #{trainingTypeId}
and t.SPE_ID = #{trainingSpeId}
and t.ORG_FLOW = #{orgFlow}
and (t.ORG_FLOW = #{orgFlow} or t.JOINT_ORG_FLOW= #{orgFlow} )
and t.GRADUATION_YEAR=#{graduationYear}
and t.session_number is not null group by T.CAT_SPE_ID, RD.DOCTOR_TYPE_ID, T.SPE_ID order by T.CAT_SPE_ID, RD.DOCTOR_TYPE_ID, T.SPE_ID

SELECT DISTINCT ORG.JOINT_ORG_FLOW, ORG.JOINT_ORG_NAME, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM ( SELECT JIORG.JOINT_ORG_FLOW, SO.ORG_NAME JOINT_ORG_NAME, ORG.ORG_FLOW, ORG.ORG_NAME, SO.ORG_CITY_ID, SO.ORG_CITY_NAME FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW LEFT JOIN SYS_ORG SO ON SO.ORG_FLOW = JIORG.JOINT_ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and so.record_status='Y' and org.org_level_id = 'CountryOrg' AND SO.ORG_PROV_ID = '320000' AND SO.ORG_TYPE_ID = 'Hospital' UNION ALL SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_NAME JOINT_ORG_NAME, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where record_status='Y'and org.org_level_id in ( 'CountryOrg','ProvinceOrg') AND ORG.ORG_PROV_ID = '320000' AND ORG.ORG_TYPE_ID = 'Hospital' UNION ALL SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_NAME JOINT_ORG_NAME, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where record_status='Y'and org.org_flow not in ( SELECT JIORG.JOINT_ORG_FLOW FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW LEFT JOIN SYS_ORG SO ON SO.ORG_FLOW = JIORG.JOINT_ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and so.record_status='Y' and org.org_level_id = 'CountryOrg') and org.org_flow not in ( SELECT ORG.ORG_FLOW FROM SYS_ORG ORG where record_status='Y' and org.org_level_id in ( 'CountryOrg','ProvinceOrg')) AND ORG.ORG_PROV_ID = '320000' AND ORG.ORG_TYPE_ID = 'Hospital' ) ORG WHERE ORG.JOINT_ORG_FLOW IN ( SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y'
and SYS_ORG.ORG_FLOW in #{item}
) order by ORG.ORG_CITY_ID,JOINT_ORG_FLOW

SELECT sd.DICT_ID spe_id ,case when jrdi.count is not null then jrdi.count else 0 end count FROM SYS_DICT sd LEFT JOIN ( SELECT info.SPE_ID,COUNT(info.SPE_ID) count FROM jsres_recruit_doc_info info LEFT JOIN RES_DOCTOR_RECRUIT RDR ON RDR.RECRUIT_FLOW = INFO.RECRUIT_FLOW INNER JOIN RES_DOCTOR RD ON RD.DOCTOR_FLOW = RDR.DOCTOR_FLOW WHERE info.RECORD_STATUS = 'Y' AND RD.RECORD_STATUS = 'Y' AND RDR.RECORD_STATUS = 'Y' AND info.AUDIT_STATUS_ID = 'Passed' AND info.CAT_SPE_ID = 'DoctorTrainingSpe'
AND ( info.org_flow = #{orgFlow} OR info.org_flow IN ( select JOINT_ORG_FLOW from RES_JOINT_ORG where RECORD_STATUS = 'Y' and ORG_FLOW = #{orgFlow} ) )
AND info.SESSION_NUMBER = #{sessionNumber}
AND rdr.doctor_status_id = #{statusId}
and rd.DOCTOR_TYPE_ID in #{item}
AND info.org_flow IN ( SELECT ORG_FLOW FROM SYS_ORG WHERE RECORD_STATUS = 'Y' AND ORG_LEVEL_ID = 'CountryOrg' AND ORG_TYPE_ID = 'Hospital' AND ORG_PROV_ID = '320000' union SELECT JOINT_ORG_FLOW FROM RES_JOINT_ORG WHERE RECORD_STATUS = 'Y' )
GROUP BY info.spe_id ) jrdi on sd.DICT_ID = jrdi.spe_id where sd.RECORD_STATUS = 'Y' and sd.DICT_TYPE_ID = 'DoctorTrainingSpe' ORDER BY ORDINAL

SELECT sd.DICT_ID spe_id ,case when jrdi.count is not null then jrdi.count else 0 end count FROM SYS_DICT sd LEFT JOIN ( SELECT info.SPE_ID,COUNT(info.SPE_ID) count FROM jsres_recruit_doc_info info LEFT JOIN RES_DOCTOR_RECRUIT RDR ON RDR.RECRUIT_FLOW = INFO.RECRUIT_FLOW INNER JOIN RES_DOCTOR RD ON RD.DOCTOR_FLOW = RDR.DOCTOR_FLOW WHERE info.RECORD_STATUS = 'Y' AND RD.RECORD_STATUS = 'Y' AND RDR.RECORD_STATUS = 'Y' AND info.AUDIT_STATUS_ID = 'Passed' AND info.CAT_SPE_ID = 'AssiGeneral'
AND ( info.org_flow = #{orgFlow} OR info.org_flow IN ( select JOINT_ORG_FLOW from RES_JOINT_ORG where RECORD_STATUS = 'Y' and ORG_FLOW = #{orgFlow} ) )
AND info.SESSION_NUMBER = #{sessionNumber}
AND rdr.doctor_status_id = #{statusId}
and rd.DOCTOR_TYPE_ID in #{item}
AND info.org_flow IN ( SELECT ORG_FLOW FROM SYS_ORG WHERE RECORD_STATUS = 'Y' AND ORG_LEVEL_ID = 'CountryOrg' AND ORG_TYPE_ID = 'Hospital' AND ORG_PROV_ID = '320000' union SELECT JOINT_ORG_FLOW FROM RES_JOINT_ORG WHERE RECORD_STATUS = 'Y' )
GROUP BY info.spe_id ) jrdi on sd.DICT_ID = jrdi.spe_id where sd.RECORD_STATUS = 'Y' and sd.DICT_TYPE_ID = 'AssiGeneral' ORDER BY ORDINAL
