SELECT sd.DICT_ID spe_id ,case when jrdi.count is not null then jrdi.count else 0 end count FROM SYS_DICT sd LEFT JOIN ( SELECT info.SPE_ID,COUNT(info.SPE_ID) count FROM jsres_recruit_doc_info info LEFT JOIN RES_DOCTOR_RECRUIT RDR ON RDR.RECRUIT_FLOW = INFO.RECRUIT_FLOW INNER JOIN RES_DOCTOR RD ON RD.DOCTOR_FLOW = RDR.DOCTOR_FLOW WHERE info.RECORD_STATUS = 'Y' AND RD.RECORD_STATUS = 'Y' AND RDR.RECORD_STATUS = 'Y' AND info.AUDIT_STATUS_ID = 'Passed' AND info.CAT_SPE_ID = 'DoctorTrainingSpe'
AND ( info.org_flow = #{orgFlow} OR info.org_flow IN ( select JOINT_ORG_FLOW from RES_JOINT_ORG where RECORD_STATUS = 'Y' and ORG_FLOW = #{orgFlow} ) )
AND info.SESSION_NUMBER = #{sessionNumber}
AND rdr.doctor_status_id = #{statusId}
and rd.DOCTOR_TYPE_ID in #{item}
AND info.org_flow IN ( SELECT ORG_FLOW FROM SYS_ORG WHERE RECORD_STATUS = 'Y' AND ORG_LEVEL_ID = 'CountryOrg' AND ORG_TYPE_ID = 'Hospital' AND ORG_PROV_ID = '320000' union SELECT JOINT_ORG_FLOW FROM RES_JOINT_ORG WHERE RECORD_STATUS = 'Y' )
GROUP BY info.spe_id ) jrdi on sd.DICT_ID = jrdi.spe_id where sd.RECORD_STATUS = 'Y' and sd.DICT_TYPE_ID = 'DoctorTrainingSpe' ORDER BY ORDINAL

SELECT sd.DICT_ID spe_id ,case when jrdi.count is not null then jrdi.count else 0 end count FROM SYS_DICT sd LEFT JOIN ( SELECT info.SPE_ID,COUNT(info.SPE_ID) count FROM jsres_recruit_doc_info info LEFT JOIN RES_DOCTOR_RECRUIT RDR ON RDR.RECRUIT_FLOW = INFO.RECRUIT_FLOW INNER JOIN RES_DOCTOR RD ON RD.DOCTOR_FLOW = RDR.DOCTOR_FLOW WHERE info.RECORD_STATUS = 'Y' AND RD.RECORD_STATUS = 'Y' AND RDR.RECORD_STATUS = 'Y' AND info.AUDIT_STATUS_ID = 'Passed' AND info.CAT_SPE_ID = 'AssiGeneral'
AND ( info.org_flow = #{orgFlow} OR info.org_flow IN ( select JOINT_ORG_FLOW from RES_JOINT_ORG where RECORD_STATUS = 'Y' and ORG_FLOW = #{orgFlow} ) )
AND info.SESSION_NUMBER = #{sessionNumber}
AND rdr.doctor_status_id = #{statusId}
and rd.DOCTOR_TYPE_ID in #{item}
AND info.org_flow IN ( SELECT ORG_FLOW FROM SYS_ORG WHERE RECORD_STATUS = 'Y' AND ORG_LEVEL_ID = 'CountryOrg' AND ORG_TYPE_ID = 'Hospital' AND ORG_PROV_ID = '320000' union SELECT JOINT_ORG_FLOW FROM RES_JOINT_ORG WHERE RECORD_STATUS = 'Y' )
GROUP BY info.spe_id ) jrdi on sd.DICT_ID = jrdi.spe_id where sd.RECORD_STATUS = 'Y' and sd.DICT_TYPE_ID = 'AssiGeneral' ORDER BY ORDINAL
