select a.org_flow, a.org_name, count(1) COUNT_NUM from (select org.org_flow, org.org_name, rddt.session_number AS session_Number, rddt.training_spe_name AS train_Spe from sys_org org left join res_docotr_delay_teturn rddt on rddt.org_flow = org.org_flow where org.record_status = 'Y' and rddt.record_status='Y'
and rddt.TRAINING_SPE_ID=#{resTrainingSpeId}
and rddt.type_id = 'ReturnTraining' AND rddt.audit_status_id = '1'
and org.org_flow in
#{orgFlow}
) a where 1=1
and a.session_number=#{year}
AND a.train_Spe like CONCAT(CONCAT('%', #{speName}),'%')
group by a.org_flow, a.org_name

select a.org_flow, a.org_name, count(1) COUNT_NUM from (select org.org_flow, org.org_name, rddt.session_number AS session_Number, rddt.training_spe_name AS train_Spe from sys_org org left join res_docotr_delay_teturn rddt on rddt.org_flow = org.org_flow where org.record_status = 'Y' and rddt.record_status='Y' and rddt.type_id = 'ReturnTraining'
and org.org_flow in
#{orgFlow}
) a where 1=1
and a.session_number=#{year}
AND a.train_Spe like CONCAT(CONCAT('%', #{speName}),'%')
group by a.org_flow, a.org_name

select a.* from ( select rddt.ORG_FLOW ORG_FLOW, rddt.ORG_NAME ORG_NAME, rddt.DOCTOR_NAME DOCTOR_NAME, rddt.DOCTOR_FLOW DOCTOR_FLOW, rddt.REASON_NAME REASON_NAME, rddt.REASON_ID REASON_ID, rddt.POLICY_NAME POLICY_NAME, rddt.POLICY_ID POLICY_ID, rddt.REASON REASON, rddt.POLICY POLICY, rddt.DISPOSITON DISPOSITON, rddt.REMARK REMARK, rddt.GRADUATION_YEAR, rddt.DELAY_FILE_PATH, rddt.SESSION_NUMBER SESSION_NUMBER, rddt.TRAINING_SPE_NAME TRAINING_SPE_NAME, d.WORK_ORG_ID, d.WORK_ORG_NAME, rddt.DOCTOR_TYPE_ID, rddt.DOCTOR_TYPE_NAME, rddt.TRAINING_TYPE_ID, rddt.TRAINING_TYPE_NAME, rddt.CREATE_TIME, rddt.MODIFY_TIME from res_docotr_delay_teturn rddt left join RES_DOCTOR d on rddt.DOCTOR_FLOW = d.DOCTOR_FLOW where rddt.record_status='Y' AND d.RECORD_STATUS = 'Y'
and rddt.type_id = #{docotrDelayTeturn.typeId}
and rddt.AUDIT_STATUS_ID in ('0','1')
and rddt.ORG_FLOW = #{docotrDelayTeturn.orgFlow}
and rddt.DOCTOR_NAME like CONCAT(CONCAT('%', #{docotrDelayTeturn.doctorName}),'%')
and d.SCHOOL = #{resDoctor.school}
) a where 1=1
and (
OR A.work_org_id = #{resDoctor.workOrgId} OR A.work_org_name = #{resDoctor.workOrgName}
) and a.DOCTOR_TYPE_ID = 'Graduate'
and A.TRAINING_SPE_NAME like CONCAT(CONCAT('%', #{docotrDelayTeturn.trainingSpeName}),'%')
and A.TRAINING_TYPE_ID = #{docotrDelayTeturn.trainingTypeId}
and A.SESSION_NUMBER = #{docotrDelayTeturn.sessionNumber}
and A.REASON_ID = #{docotrDelayTeturn.reasonId}
and A.POLICY_ID = #{docotrDelayTeturn.policyId}
order by A.create_time desc

SELECT * FROM ( SELECT DISTINCT su.user_flow AS user_flow,rddt.record_Flow as record_Flow,rddt.modify_Time as modify_Time, su.user_name AS user_name, su.id_no AS id_no, rd.session_number AS session_number,rddt.GLOBAL_CHECK_FILE as GLOBAL_CHECK_FILE, rddt.CREATE_TIME AS CREATE_TIME,rddt.org_Name as org_Name,rddt.training_Years as training_Years, rddt.graduation_Year as graduation_Year,rddt.delayreason as delayreason,rddt.audit_Status_Name as audit_Status_Name, rddt.audit_Opinion as audit_Opinion,rddt.delay_File_Path as delay_File_Path,rddt.recruit_Flow as recruit_Flow , ROW_NUMBER() OVER (PARTITION BY su.user_flow ORDER BY rddt.CREATE_TIME DESC) AS mm FROM sys_user su LEFT JOIN res_doctor rd ON rd.doctor_flow = su.user_flow LEFT JOIN Res_Docotr_Delay_Teturn rddt ON rddt.doctor_flow = rd.doctor_flow WHERE rd.record_status = 'Y' AND su.record_status = 'Y' AND rddt.record_status = 'Y' AND rddt.TYPE_ID = 'Delay'
and rd.DOCTOR_TYPE_ID in #{item}
AND rddt.AUDIT_STATUS_ID in #{item}
and rd.DOCTOR_NAME like #{doctorName}
and rd.SESSION_NUMBER = #{doctor.sessionNumber}
and rd.ORG_FLOW = #{doctor.orgFlow}
and rddt.ORG_FLOW in #{item}
ORDER BY rddt.CREATE_TIME DESC ) WHERE mm = 1

SELECT *
FROM (
SELECT DISTINCT su.user_flow AS user_flow, su.user_name AS user_name, su.id_no AS id_no, rddt.session_number AS session_number,rddt.GLOBAL_CHECK_FILE as GLOBAL_CHECK_FILE,
rddt.CREATE_TIME AS CREATE_TIME,rddt.training_Spe_Name training_Spe_Name,rddt.reason_Name reason_Name,rddt.modify_Time as modify_Time,
rddt.reason reason,rddt.policy_Name policy_Name,rddt.policy policy,rddt.dispositon dispositon,rddt.org_Name as org_Name,rddt.recruit_Flow as recruit_Flow,
rddt.remark remark,rddt.audit_Status_Name as audit_Status_Name,rddt.record_Flow as record_Flow,rddt.audit_Opinion as audit_Opinion
, ROW_NUMBER() OVER (PARTITION BY su.user_flow ORDER BY rddt.CREATE_TIME DESC) AS mm
