LEFT JOIN res_doctor rd ON rd.doctor_flow = su.user_flow
LEFT JOIN Res_Docotr_Delay_Teturn rddt ON rddt.doctor_flow = rd.doctor_flow
WHERE rddt.record_status = 'Y'
AND rddt.TYPE_ID = 'ReturnTraining'

    and rd.DOCTOR_TYPE_ID in
    #{item}


    AND rddt.AUDIT_STATUS_ID in
    #{item}




        and rd.DOCTOR_NAME like #{doctorName}




        AND rddt.REASON_ID = #{backTrainDoc.reasonId}


