mapper.select:
select oda.TICKET_NUMBER, oda.DOCTOR_FLOW, oda.DOCTOR_NAME, rd.ORG_FLOW, rd.ORG_NAME, rd.WORK_ORG_ID, rd.WORK_ORG_NAME, oss.CLINICAL_FLOW, rd.TRAINING_SPE_ID SPE_ID, rd.TRAINING_SPE_NAME SPE_NAME, oss.SUBJECT_FLOW, oss.SUBJECT_NAME, rd.TRAINING_TYPE_ID, rd.TRAINING_TYPE_NAME, a.STATION_FLOW, a.STATION_NAME, a.EXAM_SCORE, oda.IS_PASS, oda.IS_PASS_NAME from OSCA_DOCTOR_ASSESSMENT oda left join SYS_USER u on u.USER_FLOW=oda.DOCTOR_FLOW left join RES_DOCTOR rd on rd.DOCTOR_FLOW = oda.DOCTOR_FLOW left join OSCA_SKILLS_ASSESSMENT oss on oda.CLINICAL_FLOW = oss.CLINICAL_FLOW left join (select osrd.CLINICAL_FLOW,osrd.DOCTOR_FLOW, to_char(wm_concat(osrd.STATION_FLOW)) STATION_FLOW, to_char(wm_concat(osrd.STATION_NAME)) STATION_NAME, to_char(wm_concat(nvl(osrd.EXAM_SCORE,'*'))) EXAM_SCORE from OSCA_SKILL_ROOM_DOC osrd where osrd.RECORD_STATUS = 'Y'
and osrd.CLINICAL_FLOW = #{clinicalFlow}
group by osrd.DOCTOR_FLOW,osrd.CLINICAL_FLOW) a on oda.DOCTOR_FLOW = a.DOCTOR_FLOW left join (select osrd.DOCTOR_FLOW, sum(osrd.EXAM_SCORE) SCORESUM from OSCA_SKILL_ROOM_DOC osrd where osrd.RECORD_STATUS = 'Y'
and osrd.CLINICAL_FLOW = #{clinicalFlow}
group by osrd.DOCTOR_FLOW) c on c.DOCTOR_FLOW = oda.DOCTOR_FLOW where oda.RECORD_STATUS='Y'
and oda.AUDIT_STATUS_ID = #{auditStatusId}
and oss.CLINICAL_FLOW = #{clinicalFlow}
and oss.CLINICAL_FLOW in #{item,jdbcType=VARCHAR}
and oda.TICKET_NUMBER like #{name}
and oda.DOCTOR_NAME like #{name}
and oda.DOCTOR_FLOW = #{doctorFlow}
and rd.TRAINING_TYPE_ID = #{trainCategoryId}
and oss.ORG_FLOW = #{orgFlow}
and oss.ORG_FLOW = #{orgFlow2}
and rd.ORG_NAME like #{name}
and oda.IS_PASS = #{resultId}
and(oss.IS_GRADE_RELEASED = 'S' or oss.IS_GRADE_RELEASED = 'C' or oss.IS_GRADE_RELEASED = 'Y')
order by nvl(c.SCORESUM,0) ASC,oda.TICKET_NUMBER
order by nvl(c.SCORESUM,0) DESC,oda.TICKET_NUMBER
order by oda.TICKET_NUMBER

select oda.TICKET_NUMBER, oda.DOCTOR_FLOW, oda.DOCTOR_NAME, rd.ORG_FLOW, rd.ORG_NAME, rd.WORK_ORG_ID, rd.WORK_ORG_NAME, oss.CLINICAL_FLOW, oss.CLINICAL_NAME, rd.TRAINING_SPE_ID SPE_ID, rd.TRAINING_SPE_NAME SPE_NAME, oss.SUBJECT_FLOW, oss.SUBJECT_NAME, rd.TRAINING_TYPE_ID, rd.TRAINING_TYPE_NAME, a.STATION_FLOW, a.STATION_NAME, a.EXAM_SCORE, oda.IS_PASS, oda.IS_PASS_NAME from OSCA_DOCTOR_ASSESSMENT oda left join SYS_USER u on u.USER_FLOW=oda.DOCTOR_FLOW left join RES_DOCTOR rd on rd.DOCTOR_FLOW = oda.DOCTOR_FLOW left join OSCA_SKILLS_ASSESSMENT oss on oda.CLINICAL_FLOW = oss.CLINICAL_FLOW left join (select osrd.CLINICAL_FLOW,osrd.DOCTOR_FLOW, to_char(wm_concat(osrd.STATION_FLOW)) STATION_FLOW, to_char(wm_concat(osrd.STATION_NAME)) STATION_NAME, to_char(wm_concat(nvl(osrd.EXAM_SCORE,'*'))) EXAM_SCORE from OSCA_SKILL_ROOM_DOC osrd where osrd.RECORD_STATUS = 'Y' group by osrd.DOCTOR_FLOW,osrd.CLINICAL_FLOW) a on oda.DOCTOR_FLOW = a.DOCTOR_FLOW and a.CLINICAL_FLOW=oda.clinical_flow left join (select osrd.CLINICAL_FLOW,osrd.DOCTOR_FLOW, sum(osrd.EXAM_SCORE) SCORESUM from OSCA_SKILL_ROOM_DOC osrd where osrd.RECORD_STATUS = 'Y' group by osrd.DOCTOR_FLOW,osrd.CLINICAL_FLOW) c on c.DOCTOR_FLOW = oda.DOCTOR_FLOW and c.CLINICAL_FLOW=oda.clinical_flow where oda.RECORD_STATUS='Y' and oss.RECORD_STATUS='Y' and u.RECORD_STATUS='Y' and rd.RECORD_STATUS='Y' and oda.AUDIT_STATUS_ID ='Passed'
and oss.clinical_year=#{clinicalYear}
and oss.is_local=#{isLocal}
and oss.ORG_FLOW = #{orgFlow}
and oss.ORG_FLOW in #{item}
and(oss.IS_GRADE_RELEASED = 'S' or oss.IS_GRADE_RELEASED = 'C' or oss.IS_GRADE_RELEASED = 'Y')
order by oss.CLINICAL_NAME,oda.TICKET_NUMBER

SELECT DISTINCT SU.USER_NAME,ODA.DOCTOR_FLOW, ODA.DOCTOR_NAME, OSA.SPE_ID,OSA.SPE_NAME OSA_NAME, RD.TRAINING_SPE_NAME AS SPE_NAME, RD.TRAINING_TYPE_ID , RD.TRAINING_TYPE_NAME,OSA.CLINICAL_YEAR,ODA.IS_PASS,ODA.IS_PASS_NAME,ODA.CLINICAL_NAME,OSA.IS_LOCAL,OSA.CLINICAL_FLOW,OSA.IS_SHOW_FROOM,sum(doc.exam_score) ALL_SCORE, oda.EXAM_START_TIME,ODA.EXAM_END_TIME FROM OSCA_DOCTOR_ASSESSMENT oda LEFT JOIN RES_DOCTOR rd ON rd.DOCTOR_FLOW = oda.DOCTOR_FLOW LEFT JOIN SYS_USER SU ON RD.DOCTOR_FLOW=SU.USER_FLOW LEFT JOIN OSCA_SKILLS_ASSESSMENT osa ON oda.CLINICAL_FLOW = osa.CLINICAL_FLOW LEFT JOIN OSCA_SKILL_ROOM_DOC DOC on doc.clinical_flow=oda.CLINICAL_FLOW and oda.doctor_flow=doc.doctor_flow and doc.record_status='Y' WHERE oda.RECORD_STATUS = 'Y' AND rd.RECORD_STATUS = 'Y' AND osa.RECORD_STATUS = 'Y' AND osa.IS_SHOW = 'Y'
and oda.doctor_Flow = #{doctorFlow}
and oda.AUDIT_STATUS_ID = #{auditStatusId}
and oda.SIGIN_STATUS_ID = #{signStatusId}
group by SU.USER_NAME,ODA.DOCTOR_FLOW, ODA.DOCTOR_NAME, OSA.SPE_ID,OSA.SPE_NAME , RD.TRAINING_SPE_NAME, RD.TRAINING_TYPE_ID , RD.TRAINING_TYPE_NAME,OSA.CLINICAL_YEAR,ODA.IS_PASS,ODA.IS_PASS_NAME,ODA.CLINICAL_NAME,OSA.IS_LOCAL,OSA.CLINICAL_FLOW,OSA.IS_SHOW_FROOM, oda.EXAM_START_TIME,ODA.EXAM_END_TIME order by osa.SPE_ID
