mapper.select:
SELECT rd.doctor_flow, rd.doctor_name, su.user_head_img AS doctor_img, sar.sch_start_date, sar.sch_end_date, rdsp.start_date, rdsp.end_date, rdsp.sch_score AS score, rdsp.sch_flag, rdsp.is_current_flag, sar.result_flow, sar.sch_dept_name, rdsp.process_flow FROM pdsci.res_doctor_sch_process rdsp LEFT JOIN res_power_cfg rpc on 'res_doctor_web_' || rdsp.user_flow = rpc.cfg_code INNER JOIN pdsci.sch_arrange_result sar ON sar.record_status = 'Y' AND sar.result_flow = rdsp.sch_result_flow INNER JOIN pdsci.res_doctor rd ON rd.record_status = 'Y' AND rdsp.user_flow = rd.doctor_flow INNER JOIN pdsci.sys_user su ON su.record_status = 'Y' AND su.user_flow = rd.doctor_flow WHERE rdsp.record_status = 'Y' and (rdsp.sch_flag ='Y' or rdsp.is_current_flag='Y')
and rpc.record_status = 'Y' and rpc.cfg_value = 'Y' and ( (rpc.power_start_time between rdsp.sch_start_date and rdsp.sch_end_date ) or (rpc.power_end_time between rdsp.sch_start_date and rdsp.sch_end_date) or (rdsp.sch_start_date >= rpc.power_start_time and rdsp.sch_end_date <= rpc.power_end_time) )
and rdsp.TEACHER_USER_FLOW = #{teacherUserFlow}
and su.USER_NAME like #{name}
and exists( select null from res_rec rr where rr.RECORD_STATUS='Y' and rr.PROCESS_FLOW = rdsp.PROCESS_FLOW and rr.AUDIT_STATUS_ID is null AND RR.REC_TYPE_ID IN
#{item}
)
ORDER BY sar.sch_start_date DESC

if.if:
select rr.oper_user_flow, rr.oper_user_name, a.process_flow, a.rec_type_id, count(rr.rec_flow) num from (
SELECT #{processFlow} PROCESS_FLOW ,#{item} REC_TYPE_ID FROM DUAL
)a left join res_rec rr on rr.process_flow=a.process_flow and rr.rec_type_id = a.rec_type_id and rr.record_status='Y' where rr.oper_user_flow=#{docFlow} and rr.AUDIT_STATUS_ID is null group by rr.oper_user_flow, rr.oper_user_name, a.process_flow, a.rec_type_id

mapper.select:
select a.activity_way_INfo INFO_KEY,count(1) NUM from (
SELECT to_char(rec_content) AS rec_content,xmltype(rec_content).extract('//activity_way/text()').getstringval() activity_way,
substr(xmltype(rec_content).extract('//activity_way').getstringval(),1,21) activity_way_INfo
FROM res_rec
WHERE record_status = 'Y'
AND rec_type_id =#{id}
AND process_flow = #{processFlow}
) a
where a.activity_way_INfo is not null
group by a.activity_way_INfo
