select u.USER_NAME user_name,dr.TRAINING_TYPE_NAME training_Type_name,dr.TRAINING_SPE_NAME training_spe_name, dr.SESSION_NUMBER session_number,dr.DOCTOR_Flow doctor_flow,rdr.graduation_year graduation_year,dr.org_name org_name from RES_DOCTOR dr inner join SYS_USER u on dr.DOCTOR_FLOW = u.USER_FLOW left join ( select a.* , ROW_NUMBER() OVER ( PARTITION BY doctor_flow order by create_time desc) as rn from RES_DOCTOR_RECRUIT a where AUDIT_STATUS_ID='Passed' and RECORD_STATUS= 'Y' ) rdr on dr.DOCTOR_FLOW = rdr.DOCTOR_FLOW and rn=1 where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y' and rdr.record_status='Y' AND NOT EXISTS ( SELECT 1 FROM RES_DOCOTR_DELAY_TETURN T WHERE T.RECORD_STATUS = 'Y' AND T.TYPE_ID = 'ReturnTraining' AND T.AUDIT_STATUS_ID = '1' AND T.SESSION_NUMBER = rdr.SESSION_NUMBER AND T.DOCTOR_FLOW = DR.DOCTOR_FLOW )
and exists (select null from res_doctor_sch_process p where p.RECORD_STATUS = 'Y' and p.user_flow = dr.doctor_flow AND (EXISTS (SELECT NULL FROM SYS_USER_DEPT sud WHERE sud.RECORD_STATUS = 'Y' AND sud.USER_FLOW = #{kzrFlow} AND p.DEPT_FLOW = sud.DEPT_FLOW) OR p.DEPT_FLOW = #{deptFlow}))
and exists (select null from res_doctor_sch_process p where p.RECORD_STATUS = 'Y' and p.user_flow = dr.doctor_flow AND (EXISTS (SELECT NULL FROM SYS_USER_DEPT sud WHERE sud.RECORD_STATUS = 'Y' AND sud.USER_FLOW = #{kzrFlow} AND p.DEPT_FLOW = sud.DEPT_FLOW) OR p.DEPT_FLOW in #{item.deptFlow}))
and u.USER_NAME like CONCAT(CONCAT('%', #{userName}),'%')
and dr.TRAINING_TYPE_ID = #{trainingTypeId}
and dr.TRAINING_SPE_ID = #{trainingSpeId}
and dr.DOCTOR_TYPE_ID in #{item}
and dr.SESSION_NUMBER = #{sessionNumber}
and rdr.graduation_year = #{graduationYear}
and dr.SECOND_ORG_FLOW IN #{item}
and (rdr.ORG_FLOW in #{item} or rdr.JOINT_ORG_FLOW in #{item})
order by dr.SESSION_NUMBER desc,dr.TRAINING_Type_ID,dr.TRAINING_SPE_ID,u.user_flow

