dr.DOCTOR_FLOW = ar.DOCTOR_FLOW
and (

    OR dr.work_org_id = #{workOrgId}
    OR dr.work_org_name = #{workOrgName}

)

    and dr.DOCTOR_TYPE_ID = #{doctorTypeId}


