mapper.update:
update sch_arrange_result r set record_status='N' where doctor_flow=#{doctorFlow} and record_status in ('Y') and is_rotation='Y' and session_number=#{sessionNumber} and ( ( exists ( select 1 from sch_rotation_dept d left join sch_rotation_group g on d.group_flow=g.group_flow where d.rotation_flow =#{rotationFlow} and d.record_status ='N' and d.org_flow=#{orgFlow} and g.record_status='Y' and d.sch_dept_flow=r.sch_dept_flow and r.standard_group_flow=g.standard_group_flow and d.standard_dept_id=r.standard_dept_id and d.session_number=r.session_number ) or not exists ( select 1 from sch_rotation_dept d left join sch_rotation_group g on d.group_flow=g.group_flow where d.rotation_flow =#{rotationFlow} and d.org_flow=#{orgFlow} and g.record_status='Y' and d.sch_dept_flow=r.sch_dept_flow and r.standard_group_flow=g.standard_group_flow and d.standard_dept_id=r.standard_dept_id and d.session_number=r.session_number ) )
or ( exists ( select 1 from sch_rotation_dept d left join sch_rotation_group g on d.group_flow=g.group_flow where d.rotation_flow =#{secondRotationFlow} and d.record_status ='N' and d.org_flow=#{orgFlow} and g.record_status='Y' and d.sch_dept_flow=r.sch_dept_flow and r.standard_group_flow=g.standard_group_flow and d.standard_dept_id=r.standard_dept_id and d.session_number=r.session_number ) or not exists( select 1 from sch_rotation_dept d left join sch_rotation_group g on d.group_flow=g.group_flow where d.rotation_flow =#{secondRotationFlow} and d.org_flow=#{orgFlow} and g.record_status='Y' and d.sch_dept_flow=r.sch_dept_flow and r.standard_group_flow=g.standard_group_flow and d.standard_dept_id=r.standard_dept_id and d.session_number=r.session_number ) )
) and not exists( select 1 from res_doctor_sch_process p where p.user_flow=#{doctorFlow} and p.record_status='Y' and p.sch_result_flow=r.result_flow and (p.sch_flag='Y' or p.is_external='Y') )

mapper.select:
select ar.*
from SCH_ARRANGE_RESULT ar
where session_number=#{sessionNumber}
  and is_rotation='Y'
  and rotation_flow=#{rotationFlow}
  and doctor_flow=#{doctorFlow}
  and sch_dept_flow=#{schDeptFlow}
  and standard_group_flow=#{standardGroupFlow}
  and standard_dept_id=#{standardDeptId}
  and org_flow=#{orgFlow}
order by create_time desc

SELECT U.USER_NAME, R.DEPT_NAME, R.SESSION_NUMBER, R.SCH_START_DATE, R.SCH_END_DATE, RD.TRAINING_TYPE_NAME, RD.TRAINING_SPE_NAME FROM SCH_ARRANGE_RESULT R LEFT JOIN SYS_USER U ON U.USER_FLOW=R.DOCTOR_FLOW LEFT JOIN RES_DOCTOR RD ON RD.DOCTOR_FLOW = R.DOCTOR_FLOW WHERE R.RECORD_STATUS = 'Y' and rd.DOCTOR_STATUS_ID in ('Training','20') AND SCH_START_DATE <= #{schEndDate} AND SCH_START_DATE >= #{schStartDate}
AND R.DEPT_FLOW=#{deptFlow}
AND (R.DOCTOR_FLOW IN (SELECT DOCTOR_FLOW FROM RES_DOCTOR WHERE RECORD_STATUS = 'Y'
AND SESSION_NUMBER = #{sessionNumber}
AND SCH_FLAG = 'Y' AND (ORG_FLOW = #{orgFlow} OR ORG_FLOW IN (SELECT ORG_FLOW FROM SCH_DEPT_EXTERNAL_REL WHERE RECORD_STATUS = 'Y' AND REL_ORG_FLOW = #{orgFlow}))) OR R.ORG_FLOW = #{orgFlow}
AND R.SESSION_NUMBER = #{sessionNumber}
AND R.DOCTOR_FLOW IN (SELECT DOCTOR_FLOW FROM RES_DOCTOR WHERE RECORD_STATUS = 'Y' AND SCH_FLAG = 'Y')) AND (NOT EXISTS (SELECT NULL FROM RES_DOCTOR_SCH_PROCESS WHERE RECORD_STATUS = 'Y' AND SCH_RESULT_FLOW = R.RESULT_FLOW) OR EXISTS (SELECT NULL FROM RES_DOCTOR_SCH_PROCESS WHERE RECORD_STATUS = 'Y' AND SCH_RESULT_FLOW = R.RESULT_FLOW AND (TEACHER_USER_FLOW IS NULL OR HEAD_USER_FLOW IS NULL))) ORDER BY SCH_START_DATE,SCH_END_DATE

SELECT U.USER_NAME, P.DEPT_NAME, RD.SESSION_NUMBER, P.SCH_START_DATE, P.SCH_END_DATE, RD.TRAINING_TYPE_NAME, RD.TRAINING_SPE_NAME, CASE WHEN RR.REC_FLOW IS NULL THEN '学员未提交出科小结' WHEN RR.REC_FLOW IS NOT NULL AND RR.AUDIT_USER_FLOW IS NULL THEN '带教老师未审核出科小结' WHEN RR.REC_FLOW IS NOT NULL AND RR.HEAD_AUDIT_USER_FLOW IS NULL THEN '科主任未审核出科小结' ELSE '' END REASON FROM RES_DOCTOR_SCH_PROCESS P LEFT JOIN SYS_USER U ON U.USER_FLOW=P.USER_FLOW LEFT JOIN RES_DOCTOR RD ON RD.DOCTOR_FLOW = P.USER_FLOW LEFT JOIN RES_SCH_PROCESS_EXPRESS RR ON RR.RECORD_STATUS = 'Y' AND RR.PROCESS_FLOW = P.PROCESS_FLOW AND RR.REC_TYPE_ID = 'AfterSummary' WHERE P.RECORD_STATUS = 'Y' and rd.DOCTOR_STATUS_ID in ('Training','20') AND P.ORG_FLOW = #{orgFlow}
AND P.DEPT_FLOW=#{deptFlow}
AND SCH_END_DATE <= #{schEndDate} AND SCH_END_DATE >= #{schStartDate} AND IS_CURRENT_FLAG = 'Y' AND EXISTS ( SELECT 1 FROM RES_DOCTOR WHERE DOCTOR_FLOW=P.USER_FLOW AND RECORD_STATUS='Y'
AND SESSION_NUMBER = #{sessionNumber}
) AND ( NOT EXISTS ( SELECT 1 FROM RES_SCH_PROCESS_EXPRESS RR WHERE RR.RECORD_STATUS = 'Y' AND RR.PROCESS_FLOW = P.PROCESS_FLOW AND RR.REC_TYPE_ID = 'AfterSummary') OR EXISTS ( SELECT 1 FROM RES_SCH_PROCESS_EXPRESS RR WHERE RR.RECORD_STATUS = 'Y' AND RR.PROCESS_FLOW = P.PROCESS_FLOW AND ( AUDIT_USER_FLOW IS NULL OR HEAD_AUDIT_USER_FLOW IS NULL) AND RR.REC_TYPE_ID = 'AfterSummary') ) AND EXISTS ( SELECT 1 FROM RES_DOCTOR WHERE DOCTOR_FLOW=P.USER_FLOW AND RECORD_STATUS='Y'
AND SESSION_NUMBER = #{sessionNumber}
) ORDER BY SCH_START_DATE,SCH_END_DATE

SELECT U.USER_NAME, P.DEPT_NAME, RD.SESSION_NUMBER, P.SCH_START_DATE, P.SCH_END_DATE, RD.TRAINING_TYPE_NAME, RD.TRAINING_SPE_NAME, CASE WHEN RR.REC_FLOW IS NULL THEN '带教老师未审核出科考核表' WHEN RR.REC_FLOW IS NOT NULL AND RR.HEAD_AUDIT_USER_FLOW IS NULL THEN '科主任未审核出科考核表' ELSE '' END REASON FROM RES_DOCTOR_SCH_PROCESS P LEFT JOIN SYS_USER U ON U.USER_FLOW=P.USER_FLOW LEFT JOIN RES_DOCTOR RD ON RD.DOCTOR_FLOW = P.USER_FLOW LEFT JOIN RES_SCH_PROCESS_EXPRESS RR ON RR.RECORD_STATUS = 'Y' AND RR.PROCESS_FLOW = P.PROCESS_FLOW AND RR.REC_TYPE_ID = 'AfterEvaluation' WHERE P.RECORD_STATUS = 'Y' and rd.DOCTOR_STATUS_ID in ('Training','20') AND P.ORG_FLOW = #{orgFlow}
AND P.DEPT_FLOW=#{deptFlow}
AND SCH_END_DATE <= #{schEndDate} AND SCH_END_DATE >= #{schStartDate} AND IS_CURRENT_FLAG = 'Y' AND EXISTS ( SELECT 1 FROM RES_DOCTOR WHERE DOCTOR_FLOW=P.USER_FLOW AND RECORD_STATUS='Y'
AND SESSION_NUMBER = #{sessionNumber}
) AND ( NOT EXISTS ( SELECT 1 FROM RES_SCH_PROCESS_EXPRESS RR WHERE RR.RECORD_STATUS = 'Y' AND RR.PROCESS_FLOW = P.PROCESS_FLOW AND RR.REC_TYPE_ID = 'AfterEvaluation') OR EXISTS ( SELECT 1 FROM RES_SCH_PROCESS_EXPRESS RR WHERE RR.RECORD_STATUS = 'Y' AND RR.PROCESS_FLOW = P.PROCESS_FLOW AND AUDIT_USER_FLOW IS NOT NULL AND HEAD_AUDIT_USER_FLOW IS NULL AND RR.REC_TYPE_ID = 'AfterEvaluation') ) AND EXISTS ( SELECT 1 FROM RES_DOCTOR WHERE DOCTOR_FLOW=P.USER_FLOW AND RECORD_STATUS='Y'
AND SESSION_NUMBER = #{sessionNumber}
) ORDER BY SCH_START_DATE,SCH_END_DATE

SELECT ( case when b.ORG_NAME is not null then A.org_name ||'('||B.ORG_NAME||')' else A.org_name end )AS ORG_NAME_ALL , A.* FROM ( SELECT U.USER_NAME, U.ID_NO,U.CRET_TYPE_NAME,DR.TRAINING_TYPE_NAME, DR.TRAINING_SPE_NAME, DR.SESSION_NUMBER, DR.DOCTOR_FLOW , RDR.GRADUATION_YEAR, DR.ORG_NAME,DR.ORG_FLOW,DR.DOCTOR_TYPE_NAME FROM RES_DOCTOR dr inner join SYS_USER u on dr.DOCTOR_FLOW = u.USER_FLOW left join ( select A.RECRUIT_FLOW,A.DOCTOR_FLOW,A.ORG_FLOW,A.GRADUATION_YEAR,row_number() over(partition by doctor_flow order by create_time desc) as rn from RES_DOCTOR_RECRUIT a where RECORD_STATUS= 'Y' AND AUDIT_STATUS_ID='Passed'
and a.ORG_FLOW in #{item}
) rdr on dr.DOCTOR_FLOW = rdr.DOCTOR_FLOW
where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y' and rn=1
and u.USER_NAME like CONCAT(CONCAT('%', #{userName}),'%')
and dr.TRAINING_TYPE_ID = #{trainingTypeId}
and dr.TRAINING_SPE_ID = #{trainingSpeId}
and dr.DOCTOR_TYPE_ID in #{item}
and dr.SESSION_NUMBER = #{sessionNumber}
and rdr.graduation_year = #{graduationYear}
and dr.ORG_FLOW in #{item}
and exists (select null from res_doctor_sch_process p where p.RECORD_STATUS = 'Y' and p.user_flow = dr.doctor_flow AND (EXISTS (SELECT NULL FROM SYS_USER_DEPT sud WHERE sud.RECORD_STATUS = 'Y' AND sud.USER_FLOW = #{kzrFlow} AND p.DEPT_FLOW = sud.DEPT_FLOW) OR p.DEPT_FLOW = #{deptFlow}))
and exists (select null from res_doctor_sch_process p where p.RECORD_STATUS = 'Y' and p.user_flow = dr.doctor_flow AND (EXISTS (SELECT NULL FROM SYS_USER_DEPT sud WHERE sud.RECORD_STATUS = 'Y' AND sud.USER_FLOW = #{kzrFlow} AND p.DEPT_FLOW = sud.DEPT_FLOW) OR p.DEPT_FLOW in #{item.deptFlow}))
and exists ( select 1 from SCH_ARRANGE_RESULT sar where sar.doctor_flow = dr.doctor_flow and sar.record_status='Y' AND sar.SCH_END_DATE >= #{schStartDate} AND sar.SCH_END_DATE <= #{schEndDate} ) order by dr.SESSION_NUMBER desc,dr.TRAINING_Type_ID,dr.TRAINING_SPE_ID,u.user_flow ) A LEFT JOIN RES_JOINT_ORG B on B.JOINT_ORG_FLOW = a.ORG_FLOW and B.RECORD_STATUS = 'Y'

SELECT *
FROM SCH_ARRANGE_RESULT
WHERE RECORD_STATUS = 'Y'
AND DOCTOR_FLOW = #{doctorFlow}

    AND DEPT_FLOW = #{deptFlow}

AND SCH_END_DATE >= #{schStartDate}
AND SCH_END_DATE <= #{schEndDate}
ORDER BY SCH_START_DATE

select sar.*
from sch_arrange_result sar
where sar.RECORD_STATUS='Y'


    and sar.DOCTOR_NAME like #{doctorName}



    and sar.DEPT_FLOW = #{deptFlow}


    and sar.ORG_FLOW = #{orgFlow}


    and sar.DOCTOR_FLOW = #{doctorFlow}



    and sar.SCH_START_DATE >= #{schStartDate}


    and sar.SCH_END_DATE <= #{SchEndDate}


    and EXISTS (SELECT 1 FROM SYS_USER SU WHERE SU.USER_FLOW=DOCTOR_FLOW AND
    instr(#{medicineTypeId},su.medicine_Type_Id)>0)

ORDER BY sar.SCH_START_DATE DESC

SELECT * FROM SYS_DEPT WHERE RECORD_STATUS='Y' AND DEPT_FLOW IN ( select dept_flow from sch_arrange_result sar where sar.RECORD_STATUS='Y'
and sar.DOCTOR_FLOW = #{doctorFlow}
) ORDER BY ORG_FLOW,DEPT_FLOW DESC

select * from (
select rd.DOCTOR_TYPE_ID,sar.*,rd.DOCTOR_STATUS_ID
from sch_arrange_result sar
left join res_doctor rd
on sar.doctor_flow=rd.doctor_flow
where sar.RECORD_STATUS='Y'


    and sar.DOCTOR_NAME like #{doctorName}


    and sar.DEPT_FLOW = #{deptFlow}


    and sar.ORG_FLOW = #{orgFlow}


    and sar.DOCTOR_FLOW = #{doctorFlow}



    and sar.SCH_START_DATE >= #{schStartDate}


    and sar.SCH_END_DATE <= #{SchEndDate}


    or (sar.SCH_START_DATE <= #{schStartDate} and sar.SCH_END_DATE >= #{schStartDate})
    or (sar.SCH_START_DATE <= #{SchEndDate} and sar.SCH_END_DATE >= #{SchEndDate})


    and EXISTS (SELECT 1 FROM SYS_USER SU WHERE SU.USER_FLOW=DOCTOR_FLOW AND
    instr(#{medicineTypeId},su.medicine_Type_Id)>0)


    and rd.work_org_id = #{workOrgId}


    and rd.TRAINING_SPE_ID = #{trainingSpeId}

ORDER BY sar.SCH_START_DATE DESC
)where RECORD_STATUS='Y'

    and DOCTOR_TYPE_ID in
    #{item}



    AND DOCTOR_STATUS_ID = TO_CHAR(#{doctorStatusId})

SELECT S.SCH_DEPT_FLOW,S.SCH_DEPT_NAME,COUNT(*) AS NUM FROM SCH_ARRANGE_RESULT S LEFT JOIN RES_DOCTOR_SCH_PROCESS P ON S.RESULT_FLOW=P.SCH_RESULT_FLOW LEFT JOIN RES_DOCTOR D ON S.DOCTOR_FLOW=D.DOCTOR_FLOW WHERE S.RECORD_STATUS = 'Y' AND P.RECORD_STATUS = 'Y' AND D.RECORD_STATUS = 'Y' AND D.TRAINING_TYPE_ID = 'DoctorTrainingSpe' AND D.ORG_FLOW= #{orgFlow}
AND S.DEPT_FLOW= #{deptFlow}
and S.SCH_START_DATE <= #{lastDayOfMonth} and S.SCH_END_DATE >= #{firstDayOfMonth} GROUP BY S.SCH_DEPT_FLOW, S.SCH_DEPT_NAME ORDER BY S.SCH_DEPT_FLOW

SELECT S.SCH_DEPT_FLOW,S.SCH_DEPT_NAME,COUNT(*) AS NUM FROM SCH_ARRANGE_RESULT S LEFT JOIN RES_DOCTOR_SCH_PROCESS P ON S.RESULT_FLOW=P.SCH_RESULT_FLOW LEFT JOIN RES_DOCTOR D ON S.DOCTOR_FLOW=D.DOCTOR_FLOW WHERE S.RECORD_STATUS = 'Y' AND P.RECORD_STATUS = 'Y' AND D.RECORD_STATUS = 'Y' AND D.TRAINING_TYPE_ID = 'AssiGeneral' AND D.ORG_FLOW= #{orgFlow}
AND S.DEPT_FLOW= #{deptFlow}
and S.SCH_START_DATE <= #{lastDayOfMonth} and S.SCH_END_DATE >= #{firstDayOfMonth} GROUP BY S.SCH_DEPT_FLOW, S.SCH_DEPT_NAME ORDER BY S.SCH_DEPT_FLOW

SELECT D.DOCTOR_NAME,D.TRAINING_SPE_NAME,P.TEACHER_USER_NAME,SCH_MONTH,S.SCH_START_DATE,S.SCH_END_DATE
FROM SCH_ARRANGE_RESULT S
LEFT JOIN RES_DOCTOR_SCH_PROCESS P ON S.RESULT_FLOW=P.SCH_RESULT_FLOW
LEFT JOIN RES_DOCTOR D ON S.DOCTOR_FLOW=D.DOCTOR_FLOW
WHERE S.RECORD_STATUS = 'Y'
AND P.RECORD_STATUS = 'Y'
AND D.RECORD_STATUS = 'Y'
AND D.ORG_FLOW= #{orgFlow}
AND D.TRAINING_TYPE_ID= #{trainingTypeId}

    AND S.DEPT_FLOW= #{deptFlow}

and S.SCH_START_DATE  <=  #{lastDayOfMonth}
and S.SCH_END_DATE  >=   #{firstDayOfMonth}
ORDER BY D.TRAINING_SPE_ID

SELECT DISTINCT D.DOCTOR_NAME,D.SESSION_NUMBER, D.TRAINING_SPE_NAME,U.SEX_NAME,D.DOCTOR_TYPE_NAME,S.SCH_DEPT_NAME,S.SCH_START_DATE,S.SCH_END_DATE,D.TRAINING_SPE_ID
FROM SCH_ARRANGE_RESULT S
LEFT JOIN RES_DOCTOR D ON S.DOCTOR_FLOW = D.DOCTOR_FLOW
LEFT JOIN SYS_USER U ON U.USER_FLOW = D.DOCTOR_FLOW
WHERE S.RECORD_STATUS = 'Y'
AND D.RECORD_STATUS = 'Y'
AND D.ORG_FLOW = #{orgFlow}
and S.SCH_START_DATE  <=  #{lastDayOfMonth}
and S.SCH_END_DATE  >=   #{firstDayOfMonth}

    AND D.DOCTOR_TYPE_ID= #{doctorTypeId}


    AND D.TRAINING_SPE_ID= #{trainingSpeId}


    AND D.SESSION_NUMBER= #{sessionNumber}


    AND D.DOCTOR_NAME= #{userName}


    AND D.TRAINING_TYPE_ID= #{trainingTypeId}

ORDER BY D.TRAINING_SPE_ID

SELECT SAR.RESULT_FLOW,RD.DOCTOR_FLOW,RD.DOCTOR_NAME,RD.DOCTOR_TYPE_NAME,RD.TRAINING_SPE_ID,SAR.SCH_DEPT_NAME,SRG.SCH_STAGE_NAME,
SRG.GROUP_NAME,SRD.STANDARD_DEPT_NAME,SAR.BASE_AUDIT,SAR.SCH_START_DATE,SAR.SCH_END_DATE
FROM RES_DOCTOR RD
LEFT JOIN SCH_ARRANGE_RESULT SAR ON RD.DOCTOR_FLOW=SAR.DOCTOR_FLOW
LEFT JOIN SCH_ROTATION_GROUP SRG ON SAR.STANDARD_GROUP_FLOW=SRG.GROUP_FLOW
LEFT JOIN SCH_ROTATION_DEPT SRD ON SAR.STANDARD_GROUP_FLOW=SRD.GROUP_FLOW AND SAR.STANDARD_DEPT_ID=SRD.STANDARD_DEPT_ID
WHERE RD.RECORD_STATUS = 'Y' AND SAR.RECORD_STATUS = 'Y' AND SRG.RECORD_STATUS = 'Y' AND SRD.RECORD_STATUS = 'Y'

    AND SAR.RESULT_FLOW = #{resultFlow}


    AND RD.ORG_FLOW= #{orgFlow}


    AND RD.DOCTOR_NAME= #{userName}


    AND RD.SESSION_NUMBER= #{sessionNumber}


    AND RD.TRAINING_SPE_ID= #{speId}


    AND RD.TRAINING_TYPE_ID= #{trainingTypeId}


    AND SAR.SCH_DEPT_FLOW= #{schDeptFlow}


    AND SAR.SCH_START_DATE >= #{schStartDate}


    AND SAR.SCH_END_DATE <= #{schEndDate}


    AND BASE_AUDIT= #{baseAudit}

ORDER BY SAR.SCH_START_DATE

select *
from sch_arrange_result
where record_status = 'Y'
and doctor_flow = #{doctorFlow}
and (
(
sch_start_date <= #{startDate}
and  sch_end_date >= #{startDate}
)
or
(
sch_start_date <= #{endDate}
and  sch_end_date >= #{endDate}
)
or
(
sch_start_date >= #{startDate}
and  sch_end_date <= #{endDate}
)
)

    and result_flow != #{subDeptFlow}


    and rotation_flow = #{rotationFlow}

select sar.* from SCH_ARRANGE_RESULT sar
where sar.record_status = 'Y'
and sar.doctor_flow in

    #{doctorFlow}



        and sar.sch_start_date <= #{schEndDate} and #{schStartDate} <= sar.sch_end_date


        and sar.sch_start_date >= #{schStartDate}


        and sar.sch_end_date <= #{schEndDate}
