mapper.select:
		  update PDSCI.EDC_PATIENT_VISIT_DATA SET ATTR_VALUE=ATTR_VALUE1 , ATTR_VALUE_TIP=ATTR_VALUE1_TIP,
		   MODIFY_TIME = #{modifyTime},
      MODIFY_USER_FLOW = #{modifyUserFlow}
    where VISIT_RECORD_FLOW = #{visitRecordFlow}

		  update PDSCI.EDC_PATIENT_VISIT_DATA SET ATTR_VALUE=ATTR_VALUE2 , ATTR_VALUE_TIP=ATTR_VALUE2_TIP,
		   MODIFY_TIME = #{modifyTime},
      MODIFY_USER_FLOW = #{modifyUserFlow}
    where VISIT_RECORD_FLOW = #{visitRecordFlow}

SELECT V.VISIT_FLOW,V.VISIT_NAME, P.ORG_FLOW,P.PATIENT_FLOW,P.PATIENT_NAME_PY,P.PATIENT_SEQ,P.PATIENT_CODE, DATA.ELEMENT_SERIAL_SEQ,DATA.ATTR_VALUE,DATA.ATTR_VALUE_TIP, ATTR.ELEMENT_CODE,ATTR.ELEMENT_NAME,ATTR.ATTR_CODE,ATTR.ATTR_NAME FROM (SELECT VISIT_FLOW,VISIT_NAME FROM PDSCI.EDC_VISIT WHERE PROJ_FLOW = #{projFlow} AND RECORD_STATUS = 'Y') V, (SELECT ORG_FLOW,PATIENT_FLOW,PATIENT_NAME_PY,PATIENT_SEQ,PATIENT_CODE FROM PDSCI.PUB_PATIENT WHERE PROJ_FLOW = #{projFlow} AND RECORD_STATUS = 'Y'
AND ORG_FLOW = #{orgFlow}
AND PATIENT_CODE = #{patientCode}
) P, (SELECT VISIT_FLOW,PATIENT_FLOW,ELEMENT_SERIAL_SEQ,ATTR_VALUE,ATTR_VALUE_TIP,ELEMENT_CODE,ATTR_CODE,CREATE_TIME FROM PDSCI.EDC_PATIENT_VISIT_DATA WHERE PROJ_FLOW = #{projFlow} AND RECORD_STATUS = 'Y') DATA, (SELECT ELEMENT_CODE,ELEMENT_NAME,ATTR_CODE,ATTR_NAME FROM PDSCI.EDC_ATTRIBUTE WHERE PROJ_FLOW = #{projFlow} AND RECORD_STATUS = 'Y') ATTR WHERE V.VISIT_FLOW = DATA.VISIT_FLOW AND P.PATIENT_FLOW = DATA.PATIENT_FLOW AND DATA.ATTR_VALUE_TIP LIKE #{attrValueTip} AND DATA.ATTR_CODE = ATTR.ATTR_CODE ORDER BY P.PATIENT_SEQ,DATA.CREATE_TIME
