select.if:
    	select 
    	from pdsci.ERP_DOC 
    	where RECORD_STATUS = 'Y'


   			and CREATE_USER_FLOW in (
				select USER_FLOW
    			from pdsci.SYS_USER
    			where RECORD_STATUS ='Y'
	   			and USER_NAME like #{userName}
			)




	   			and (
	   				DOC_NAME like #{docName}
	   				or FILE_NAME like #{docName}
	   			)


	   			and DOC_TYPE_ID = #{erpDoc.docTypeId}


	   			and FILE_TYPE = #{erpDoc.fileType}


	   			and CREATE_TIME >= #{erpDoc.createTime}


   				and CREATE_USER_FLOW = #{erpDoc.createUserFlow}


	   			and IS_PUBLIC = #{erpDoc.isPublic}



   			and DOC_FLOW in (
   				select DOC_FLOW 
   				from pdsci.ERP_DOC_SHARE
   				where RECORD_STATUS = 'Y'
   				and (SHARE_RECORD_FLOW = #{userFlow}

   					or SHARE_RECORD_FLOW = #{deptFlow}
   				)
   			)


	   		and CREATE_TIME  <=  #{afterDate}


   			order by ${sortType}

    		select 
	    	from pdsci.ERP_DOC 
	    	where RECORD_STATUS = 'Y'


	   			and CREATE_USER_FLOW in (
					select USER_FLOW
	    			from pdsci.SYS_USER
	    			where RECORD_STATUS ='Y'
		   			and USER_NAME like #{userName}
				)




		   			and (
		   				DOC_NAME like #{docName}
		   				or FILE_NAME like #{docName}
		   			)


		   			and DOC_TYPE_ID = #{erpDoc.docTypeId}


		   			and FILE_TYPE = #{erpDoc.fileType}


		   			and CREATE_TIME >= #{erpDoc.createTime}


	   				and CREATE_USER_FLOW = #{erpDoc.createUserFlow}



		   		and CREATE_TIME  <=  #{afterDate}


	   		or DOC_FLOW in(
	   			SELECT DOC_FLOW
				FROM pdsci.ERP_DOC
				WHERE RECORD_STATUS = 'Y'


		   			and CREATE_USER_FLOW in (
						select USER_FLOW
		    			from pdsci.SYS_USER
		    			where RECORD_STATUS ='Y'
			   			and USER_NAME like #{userName}
					)




			   			and (
			   				DOC_NAME like #{docName}
			   				or FILE_NAME like #{docName}
			   			)


			   			and DOC_TYPE_ID = #{erpDoc.docTypeId}


			   			and FILE_TYPE = #{erpDoc.fileType}


			   			and CREATE_TIME >= #{erpDoc.createTime}



		   			and DOC_FLOW in (
		   				select DOC_FLOW 
		   				from pdsci.ERP_DOC_SHARE
		   				where RECORD_STATUS = 'Y'
		   				and (SHARE_RECORD_FLOW = #{userFlow}

		   					or SHARE_RECORD_FLOW = #{deptFlow}
		   				)
		   			)


			   		and CREATE_TIME  <=  #{afterDate}

	   		)
	   		or DOC_FLOW in(
	   			SELECT DOC_FLOW
				FROM pdsci.ERP_DOC
				WHERE RECORD_STATUS = 'Y'


		   			and CREATE_USER_FLOW in (
						select USER_FLOW
		    			from pdsci.SYS_USER
		    			where RECORD_STATUS ='Y'
			   			and USER_NAME like #{userName}
					)




			   			and (
			   				DOC_NAME like #{docName}
			   				or FILE_NAME like #{docName}
			   			)


			   			and DOC_TYPE_ID = #{erpDoc.docTypeId}


			   			and FILE_TYPE = #{erpDoc.fileType}


			   			and CREATE_TIME >= #{erpDoc.createTime}


			   			and IS_PUBLIC = #{erpDoc.isPublic}



			   		and CREATE_TIME  <=  #{afterDate}

	   		)

	   			order by ${sortType}
