mapper.select:
select ecm.*,ec.course_name,ec.course_credit,ec.course_period,ec.course_code
from pdsci.edu_course ec,pdsci.edu_course_major ecm
where ec.record_status='Y' and ecm.record_status='Y'
and ec.course_flow=ecm.course_flow

    and ecm.major_id=#{majorId}


    and ecm.plan_year=#{year}

select ecm.major_id,ecm.major_name,ecm.course_type_id,ecm.course_type_name,ecm.plan_year,
eu.course_code,eu.course_flow,eu.gradation_id,eu.gradation_name,
eu.course_name,eu.course_period,eu.course_credit,eu.create_time
from pdsci.edu_course eu,pdsci.edu_course_major ecm
where eu.record_status='Y' and ecm.record_status='Y'
and eu.course_flow=ecm.course_flow


        and ecm.major_id=#{courseMajor.majorId}


        and ecm.plan_year=#{courseMajor.planYear}





        and eu.course_name like #{courseName}


        and eu.gradation_id=#{course.gradationId}


        and eu.course_Flow=#{course.courseFlow}


order by ecm.major_id asc,ecm.course_type_id asc,eu.course_code asc,eu.create_time asc

select

from pdsci.edu_course
where record_status='Y'

    and ASSUME_ORG_FLOW=#{assumeOrgFlow}


    and course_flow not in (select course_flow from pdsci.edu_course_major
    where record_status='Y'

        and major_id=#{majorId}


        and plan_year=#{year}

    )


    and course_session=#{year}


    and course_session=#{planYear}

select cm.RECORD_FLOW, cm.COURSE_FLOW, cm.COURSE_TYPE_ID, cm.MAJOR_ID, cm.MAJOR_NAME, c.COURSE_FLOW, c.COURSE_NAME, c.COURSE_CREDIT,c.COURSE_SPEAKER,c.COURSE_SPEAKER_PHONE,c.COURSE_INTRO, c.COURSE_PERIOD, c.COURSE_CODE, c.COURSE_NAME_EN, c.COURSE_USER_COUNT,c.GRADATION_ID,c.COURSE_SESSION from pdsci.EDU_COURSE_MAJOR cm left join pdsci.EDU_COURSE c on cm.COURSE_FLOW = c.COURSE_FLOW where cm.RECORD_STATUS='Y' and c.RECORD_STATUS='Y'
and cm.MAJOR_ID = #{majorId}
and cm.PLAN_YEAR = #{planYear}
and c.GRADATION_ID = #{course.gradationId}
and (c.GRADATION_ID = #{course.gradationId} or c.GRADATION_ID = '3')
and (c.COURSE_CODE like #{courseFlow} or cm.COURSE_NAME like #{courseFlow})
order by cm.CREATE_TIME DESC

select cm.RECORD_FLOW, cm.COURSE_FLOW, cm.COURSE_TYPE_ID, cm.MAJOR_ID, cm.MAJOR_NAME, c.COURSE_FLOW, c.COURSE_NAME, c.COURSE_CREDIT,c.COURSE_SPEAKER,c.COURSE_SPEAKER_PHONE,c.COURSE_INTRO, c.COURSE_PERIOD, c.COURSE_CODE, c.COURSE_NAME_EN, c.COURSE_USER_COUNT,c.GRADATION_ID from pdsci.EDU_COURSE_MAJOR cm left join pdsci.EDU_COURSE c on cm.COURSE_FLOW = c.COURSE_FLOW where cm.RECORD_STATUS='Y' and c.RECORD_STATUS='Y' and c.course_flow in(select a.course_flow from edu_course a,edu_student_course b where a.course_flow=b.course_flow and b.record_status ='Y' and b.replenish_flag='Y'
and b.USER_FLOW = #{studentCourse.userFlow}
)
and cm.PLAN_YEAR = #{planYear}
order by cm.COURSE_FLOW DESC

select
cm.RECORD_FLOW, cm.COURSE_FLOW, cm.COURSE_TYPE_ID,
c.COURSE_FLOW, c.COURSE_NAME, c.COURSE_CREDIT, c.COURSE_PERIOD, c.COURSE_CODE, c.COURSE_USER_COUNT,c.GRADATION_ID
from pdsci.EDU_COURSE_MAJOR cm
left join pdsci.EDU_COURSE c on cm.COURSE_FLOW = c.COURSE_FLOW
where cm.RECORD_STATUS='Y' and c.RECORD_STATUS='Y'

    and cm.MAJOR_ID = #{majorId}


    and cm.PLAN_YEAR = #{planYear}


    and c.GRADATION_ID = #{gradationId}


    and cm.COURSE_FLOW not in (
    select COURSE_FLOW from pdsci.EDU_STUDENT_COURSE
    where RECORD_STATUS='Y'
    and USER_FLOW = #{userFlow}

        and STUDENT_PERIOD = #{planYear}

    )

order by cm.CREATE_TIME DESC

select distinct(plan_year) from pdsci.edu_course_major
where record_status='Y'

    and plan_year!=#{year}


    and recommend_Flag=#{recommendFlag}

select

from pdsci.edu_course
where record_status='Y'

    and ASSUME_ORG_FLOW=#{assumeOrgFlow}


    and course_session=#{year}


    and course_session=#{planYear}
