select eu.MAJOR_ID,eu.MAJOR_NAME,esc.course_code,esc.COURSE_FLOW,esc.COURSE_NAME,esc.COURSE_TYPE_ID,esc.COURSE_TYPE_Name,eu.USER_FLOW,eu.TRAIN_TYPE_NAME,eu.TRAIN_CATEGORY_NAME,esc.COURSE_PERIOD,esc.COURSE_CREDIT,esc.COURSE_GRADE,esc.RECORD_FLOW,esc.GRADE_TERM_ID,esc.GRADE_TERM_NAME, esc.PACIFIC_GRADE,esc.TEAM_END_GRADE,esc.STUDY_WAY_ID,esc.STUDY_WAY_NAME,esc.ASSESS_TYPE_ID,esc.ASSESS_TYPE_NAME,esc.course_grade,ec.COURSE_NAME_EN,NVL(ec.EFFECTIVE_GRADE,'0') EFFECTIVE_GRADE,esc.DEGREE_COURSE_FLAG from edu_student_course esc inner join EDU_USER eu on eu.USER_FLOW = esc.USER_FLOW left join edu_course ec on esc.course_code = ec.course_code and eu.period = ec.course_session where esc.RECORD_STATUS='Y' and eu.RECORD_STATUS='Y'
and esc.user_flow in #{item}
and esc.course_Name like #{courseName}
and esc.GRADE_TERM_ID = #{studentCourse.gradeTermId}
and esc.IMP_FLOW = #{studentCourse.impFlow}
and esc.AUDIT_STATUS_ID = 'Passed'
ORDER BY esc.COURSE_TYPE_ID,esc.COURSE_CODE

select DISTINCT ecm.RECORD_FLOW, ecm.MAJOR_ID, ecm.MAJOR_NAME, ecm.course_Flow, ecm.course_Name, ecm.COURSE_TYPE_ID, ecm.COURSE_TYPE_NAME, ecm.PLAN_YEAR, ecm.RECORD_STATUS, ec.course_Flow, ec.course_Name, ec.COURSE_CREDIT, ec.COURSE_PERIOD, ec.COURSE_CATEGORY_ID, ec.COURSE_CATEGORY_NAME, ec.COURSE_STATUS_ID, ec.COURSE_STATUS_NAME, ec.COURSE_SPEAKER, ec.DEPT_FLOW, ec.DEPT_NAME, ec.COURSE_IMG_NAME, ec.CREATE_USER_NAME, ec.GRADATION_ID, ec.GRADATION_NAME, ec.COURSE_PERIOD_TEACH, ec.COURSE_PERIOD_EXPER, ec.COURSE_PERIOD_MACHINE, ec.COURSE_PERIOD_OTHER, ec.COURSE_CODE, ec.COURSE_NAME_EN, ec.COURSE_USER_COUNT, esc.COURSE_GRADE, esc.RECORD_FLOW from edu_course_major ecm,edu_course ec LEFT JOIN EDU_STUDENT_COURSE esc ON ec.COURSE_FLOW=esc.COURSE_FLOW where ec.RECORD_STATUS='Y' and ecm.RECORD_STATUS='Y' and ecm.COURSE_FLOW=ec.COURSE_FLOW
and ecm.MAJOR_ID=#{majorId}
and ecm.PLAN_YEAR=#{period}
ORDER BY ecm.COURSE_TYPE_ID

select
eu.USER_FLOW, eu.SID, eu.MAJOR_ID, eu.MAJOR_NAME, eu.PERIOD,
su.USER_FLOW sysUserFlow, su.USER_NAME
from pdsci.EDU_USER eu
left join pdsci.SYS_USER su on su.USER_FLOW = eu.USER_FLOW
left join pdsci.SYS_USER_ROLE sur on sur.USER_FLOW = eu.USER_FLOW
where eu.RECORD_STATUS='Y' and su.RECORD_STATUS='Y' and sur.RECORD_STATUS='Y'

    and sur.ROLE_FLOW = #{roleFlow}




        and su.USER_NAME like #{userName}



        and su.ID_NO like #{idNo}





        and eu.SID like #{sid}


        and eu.MAJOR_ID = #{eduUser.majorId}


        and eu.PERIOD = #{eduUser.period}


        and eu.TRAIN_TYPE_ID = #{eduUser.trainTypeId}


        and eu.TRAIN_ORG_ID = #{eduUser.trainOrgId}




        and exists (select null from res_doctor where doctor_flow=eu.user_flow and org_flow=
        #{resDoctor.orgFlow} and record_status='Y')


order BY eu.sid desc

select eu.*,su.* from pdsci.edu_user eu,pdsci.sys_user su,pdsci.sys_user_role sur where eu.RECORD_STATUS = 'Y'
and su.RECORD_STATUS = 'Y' and eu.user_flow = su.user_flow
and su.user_flow = sur.user_flow


        and su.status_id = #{sysUser.statusId}


        and su.org_flow = #{sysUser.orgFlow}


        and su.user_name like #{sysUser.userName}




        and sur.role_flow = #{sysRole.roleFlow}


order by eu.create_time desc

select eu.user_flow,eu.major_id,eu.major_name,eu.period, eu.sid,su.user_head_img,eu.credit,eu.intro, su.user_name,su.user_code,su.sex_id,su.sex_name,su.status_id, su.status_desc,su.user_phone,su.user_email, su.id_no,su.user_birthday,su.title_id,su.title_name, su.degree_id,su.degree_name,su.education_id,su.education_name, su.post_id,su.post_name,su.CREATE_TIME, so.org_flow,so.org_name from pdsci.edu_user eu,pdsci.sys_user su,pdsci.sys_org so,pdsci.sys_user_role sur where eu.RECORD_STATUS = 'Y' and su.RECORD_STATUS = 'Y' and so.RECORD_STATUS = 'Y' and sur.RECORD_STATUS = 'Y' and eu.user_flow = su.user_flow and so.org_flow = su.org_flow and su.user_flow =sur.user_flow
and so.org_flow=#{sysUser.orgFlow}
and su.status_id=#{sysUser.statusId}
and sur.role_flow=#{roleFlow}
and (su.user_name like #{condition} or so.org_name like #{condition} or eu.major_name like #{condition} )
and eu.user_flow=#{userFlow}
group by eu.user_flow,eu.major_id,eu.major_name,eu.period, eu.sid,su.user_head_img,eu.credit,eu.intro, su.user_name,su.user_code,su.sex_id,su.sex_name,su.status_id, su.status_desc,su.user_phone,su.user_email, su.id_no,su.user_birthday,su.title_id,su.title_name, su.degree_id,su.degree_name,su.education_id,su.education_name, su.post_id,su.post_name,su.CREATE_TIME, so.org_flow,so.org_name
order by #{sort}

select eu.user_flow,eu.major_id,eu.major_name,eu.period, eu.sid,su.user_head_img,eu.credit,eu.intro, su.user_name,su.user_code,su.sex_id,su.sex_name,su.status_id, su.status_desc,su.user_phone,su.user_email, su.id_no,su.user_birthday,su.title_id,su.title_name, su.degree_id,su.degree_name,su.education_id,su.education_name, su.post_id,su.post_name,su.CREATE_TIME, so.org_flow,so.org_name,ec.course_flow,ec.course_name from pdsci.edu_user eu,pdsci.sys_user su,pdsci.sys_org so,pdsci.sys_user_role sur,pdsci.edu_course ec where eu.RECORD_STATUS = 'Y' and su.RECORD_STATUS = 'Y' and so.RECORD_STATUS = 'Y' and sur.RECORD_STATUS = 'Y' and ec.RECORD_STATUS = 'Y' and eu.user_flow = su.user_flow and so.org_flow = su.org_flow and su.user_flow =sur.user_flow and ec.course_flow in (
select distinct(course_flow) from pdsci.edu_course_chapter where RECORD_STATUS = 'Y' and teacher_id=eu.user_flow
select distinct(course_flow) from pdsci.edu_student_course where RECORD_STATUS = 'Y' and user_flow=eu.user_flow
)
and so.org_flow=#{sysUser.orgFlow}
and su.status_id=#{sysUser.statusId}
and sur.role_flow=#{roleFlow}
and (su.user_name like #{condition} or so.org_name like #{condition} or eu.major_name like #{condition} or ec.course_name like #{condition} )
and eu.user_flow=#{userFlow}
group by eu.user_flow,eu.major_id,eu.major_name,eu.period, eu.sid,su.user_head_img,eu.credit,eu.intro, su.user_name,su.user_code,su.sex_id,su.sex_name,su.status_id, su.status_desc,su.user_phone,su.user_email, su.id_no,su.user_birthday,su.title_id,su.title_name, su.degree_id,su.degree_name,su.education_id,su.education_name, su.post_id,su.post_name,su.CREATE_TIME, so.org_flow,so.org_name,ec.course_flow,ec.course_name

SELECT PEU.*
FROM PDSCI.EDU_USER PEU,PDSCI.SYS_USER PSU,PDSCI.RES_DOCTOR PDU
WHERE PEU.RECORD_STATUS = 'Y'
AND PSU.RECORD_STATUS = 'Y'
AND PEU.USER_FLOW = PSU.USER_FLOW
AND PEU.USER_FLOW = PDU.DOCTOR_FLOW


    and peu.period >= #{startDate}


    and peu.period <=  #{endDate}



    and peu.at_school_status_id = #{eduUser.atSchoolStatusId}



    and peu.award_degree_flag = #{eduUser.awardDegreeFlag}



    and pdu.org_name = #{resDoctor.orgName}



    and peu.period = #{eduUser.period}



    and peu.train_org_id = #{eduUser.trainOrgId}



    and pdu.org_Flow = #{resDoctor.orgFlow}



    and peu.class_id = #{eduUser.classId}



    and peu.STUDY_FORM_ID = #{eduUser.studyFormId}




    and psu.user_name like #{userName}



    and psu.sex_id = #{sysUser.sexId}



    and psu.nation_id = #{sysUser.nationId}



    and peu.train_category_id = #{eduUser.trainCategoryId}



    and psu.politics_status_id = #{sysUser.politicsStatusId}



    and psu.user_birthday >= #{startBirthday}


    and psu.user_birthday <= #{endBirthday}



    and psu.cret_type_id like #{sysUser.cretTypeId}



    and peu.major_id = #{eduUser.majorId}



    and peu.sid like '%${eduUser.sid}%'


    and peu.sid >= #{startSid}


    and peu.sid <= #{endSid}



    and peu.first_teacher like #{eduUser.firstTeacher}



    and peu.admit_type_id = #{eduUser.admitTypeId}



    and peu.student_source_id = #{eduUser.studentSourceId}



    and peu.is_recommend_id like #{eduUser.isRecommendId}



    and psu.marital_id = #{sysUser.maritalId}



    and peu.is_exceptional_id like #{eduUser.isExceptionalId}



    and peu.train_type_id like #{eduUser.trainTypeId}



    and peu.is_out_of_school = #{eduUser.isOutOfSchool}



    and peu.is_back_to_school = #{eduUser.isBackToSchool}



    and peu.is5plus3 = #{eduUser.is5plus3}



    and peu.is_mba_dba = #{eduUser.isMbaDba}



    and peu.is_reported = #{eduUser.isReported}




    and (peu.first_teacher like #{teacherName} OR peu.second_teacher like #{teacherName})



        and peu.user_flow in (select user_flow from EDU_USER_INFO_STATUS where record_status='Y' and part_id = #{partId} and part_status=#{partStatus} group by user_Flow)






    and not exists (select null from pub_user_generic_content pugc where pugc.record_status='Y' and pugc.user_flow=peu.user_flow)

order by peu.period desc nulls last, peu.sid asc, peu.create_time asc

