mapper.select:
select * from PDSCI.irb_meeting where record_status = 'Y' 


			and meeting_status = 'Y'


			and meeting_status is null


			and meeting_date >= #{meeting.meetingDate}


			and meeting_date <= #{meetingEndDate}



	order by MEETING_DATE desc

select from PDSCI.irb_meeting where record_status = 'Y' and meeting_flow in (select distinct meeting_flow from PDSCI.irb_meeting_user where record_status = 'Y' and user_flow = #{userFlow} )
and meeting_status = 'Y'
and meeting_status is null
and meeting_date >= #{meeting.meetingDate}
and meeting_date <= #{meetingEndDate}
order by MEETING_DATE desc,MEETING_START_TIME desc
