mapper.select:
 	  select 	dr.*,		
				u.USER_FLOW,
 				u.USER_NAME,
				u.USER_CODE ,
				u.USER_PHONE,
				u.SEX_ID,
				u.SEX_NAME,
				u.ID_NO,
				u.NATION_ID,
				u.NATION_NAME,
				u.USER_BIRTHDAY,
				u.USER_EMAIL
	    from pdsci.RES_DOCTOR_RECRUIT dr
	    inner join pdsci.SYS_USER u on dr.DOCTOR_FLOW = u.USER_FLOW 
	    where dr.RECORD_STATUS = 'Y' AND u.RECORD_STATUS = 'Y'  AND dr.RECRUIT_FLAG = 'Y'


					    and dr.SPE_ID = #{doctorRecruit.speId} 


					    and dr.SESSION_NUMBER = #{doctorRecruit.sessionNumber} 





	    				and u.USER_NAME like #{userName} 


	    				and u.ID_NO = #{users.idNo} 


	    AND (exists(
	      select so.org_flow
	      from  sys_org so
	      where so.record_status = 'Y'
	      and so.org_flow = dr.org_flow

	    	and so.org_flow = #{orgFlow} 


			 and so.ORG_CITY_ID = #{doctorRecruit.placeId} 


			 and so.ORG_TYPE_ID = #{orgTypeId} 

	    )

	    or 
	    exists(
	    select rjo.joint_org_flow
	      from  res_joint_org rjo
	      inner join sys_org so
	      on so.org_flow = rjo.org_flow
	      where so.record_status = 'Y'
	      and rjo.record_status = 'Y'
	      and rjo.joint_org_flow = dr.org_flow

	    	and so.org_flow = #{orgFlow} 


			 and so.ORG_CITY_ID = #{doctorRecruit.placeId} 


			 and so.ORG_TYPE_ID = #{orgTypeId} 

	    )

	    )

    		select *
    		from pdsci.RES_DOCTOR_RECRUIT dr
			left join  RES_DOCTOR ed
			on dr.DOCTOR_FLOW =  ed.DOCTOR_FLOW
    		left join  pdsci.SYS_USER u
    		on dr.DOCTOR_FLOW =  u.USER_FLOW
    		left join pdsci.PUB_USER_RESUME pur
    		on pur.USER_FLOW=dr.DOCTOR_FLOW
		 	left join pdsci.sys_org so on so.org_flow = dr.org_flow
    		where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y'
    		and dr.org_flow in(
    			SELECT ORG_FLOW
			FROM PDSCI.SYS_ORG
			WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'


			    	 AND ORG_PROV_ID = #{sysOrg.orgProvId} 


			    	 AND ORG_CITY_ID = #{sysOrg.orgCityId} 


			    	 AND ORG_FLOW = #{sysOrg.orgFlow} 


			    	 AND ORG_LEVEL_ID = #{sysOrg.orgLevelId} 


			    	 AND ORG_TYPE_ID = #{sysOrg.orgTypeId} 



				and dr.ORG_FLOW in 
    			#{item}


				and ed.DOCTOR_TYPE_ID in
    			#{item}


				 and dr.TRAIN_YEAR in
				 #{item}

    		)


    				and dr.SESSION_NUMBER = #{resDoctorRecruit.sessionNumber} 


    				and dr.CAT_SPE_ID = #{resDoctorRecruit.catSpeId} 


    				and dr.SPE_ID = #{resDoctorRecruit.speId} 


    				and dr.ORG_FLOW = #{resDoctorRecruit.orgFlow} 


    				and dr.AUDIT_STATUS_ID=#{resDoctorRecruit.auditStatusId}


    				and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}



    			and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})




    				and u.USER_NAME like #{userName} 



    				and u.ID_NO like #{idNo} 



				and dr.SESSION_NUMBER in
				#{item}



    				and dr.CAT_SPE_ID = #{doctor.trainingTypeId} 


    				and dr.SPE_ID = #{doctor.trainingSpeId} 


    				and dr.SESSION_NUMBER = #{doctor.sessionNumber} 


    				and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId} 


					and ed.IS_YEAR_GRADUATE = #{doctor.isYearGraduate}


					and dr.doctor_Status_Id = #{doctor.doctorStatusId}


					and (

						OR ed.work_org_id = #{doctor.workOrgId}
						OR ed.work_org_name like CONCAT(CONCAT('%', #{doctor.workOrgName}),'%')

					)


    		order by dr.SESSION_NUMBER desc,so.org_level_id,dr.org_flow,NLSSORT(u.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M'),dr.doctor_flow,dr.CREATE_TIME desc

select dr.*,ed.*,u.*,pur.*,so.*
	, EXTRACTVALUE(XMLTYPE(pur.user_resume), '/userResumeExtInfoForm/armyType') as ARMY_TYPE
from pdsci.RES_DOCTOR_RECRUIT dr
left join  RES_DOCTOR ed
on dr.DOCTOR_FLOW =  ed.DOCTOR_FLOW
left join  pdsci.SYS_USER u
on dr.DOCTOR_FLOW =  u.USER_FLOW
left join pdsci.PUB_USER_RESUME pur
on pur.USER_FLOW=dr.DOCTOR_FLOW
left join pdsci.sys_org so on so.org_flow = dr.org_flow
where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y'
and dr.org_flow in(
SELECT ORG_FLOW
FROM PDSCI.SYS_ORG
WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'
AND NOT EXISTS (
SELECT
1
FROM
RES_DOCOTR_DELAY_TETURN T
WHERE
T.RECORD_STATUS = 'Y'
AND T.TYPE_ID = 'ReturnTraining'
AND T.AUDIT_STATUS_ID = '1'
AND T.RECRUIT_FLOW = DR.RECRUIT_FLOW
AND T.SESSION_NUMBER = DR.SESSION_NUMBER
AND T.DOCTOR_FLOW = DR.DOCTOR_FLOW
)


		AND ORG_PROV_ID = #{sysOrg.orgProvId}


		AND ORG_CITY_ID = #{sysOrg.orgCityId}


		AND ORG_FLOW = #{sysOrg.orgFlow}


		AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}


		AND ORG_TYPE_ID = #{sysOrg.orgTypeId}



	and dr.JOINT_ORG_FLOW in
	#{item}


	and (dr.ORG_FLOW in
	#{item}
	or dr.joint_org_flow in
	#{item}
	)


	and ed.DOCTOR_TYPE_ID in
	#{item}


	and dr.TRAIN_YEAR in
	#{item}

)

	and ed.WORK_ORG_ID = #{workOrgId}


	and ed.WORK_ORG_NAME = #{workOrgName}


	AND pur.user_resume IS NOT NULL
	AND EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') IS NOT NULL
	AND EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') != 'NO'


	AND pur.user_resume IS NOT NULL
	AND
	(
	EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') = 'NO'
	OR
	EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') IS NULL
	)



		and dr.SESSION_NUMBER = #{resDoctorRecruit.sessionNumber}


		and dr.CAT_SPE_ID = #{resDoctorRecruit.catSpeId}


		and dr.SPE_ID = #{resDoctorRecruit.speId}


		and dr.ORG_FLOW = #{resDoctorRecruit.orgFlow}


		and dr.AUDIT_STATUS_ID=#{resDoctorRecruit.auditStatusId}


		and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}


		and dr.DOCTOR_STATUS_ID in ('20','21','22','24')



	and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})




		and u.USER_NAME like #{userName}



		and u.ID_NO like #{idNo}



	and dr.SESSION_NUMBER in
	#{item}



		and dr.CAT_SPE_ID = #{doctor.trainingTypeId}


		and dr.SPE_ID = #{doctor.trainingSpeId}


		and dr.SESSION_NUMBER = #{doctor.sessionNumber}


		and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId}


		and ed.IS_YEAR_GRADUATE = #{doctor.isYearGraduate}


		and dr.doctor_Status_Id = #{doctor.doctorStatusId}


		and (

			OR ed.work_org_id = #{doctor.workOrgId}
			OR ed.work_org_name like CONCAT(CONCAT('%', #{doctor.workOrgName}),'%')

		)




		AND EXISTS (SELECT 1
		FROM RES_DOCOTR_DELAY_TETURN T
		WHERE T.RECORD_STATUS = 'Y'
		AND T.TYPE_ID = 'Delay'
		AND T.DOCTOR_FLOW = DR.DOCTOR_FLOW)


		AND NOT EXISTS (SELECT 1
		FROM RES_DOCOTR_DELAY_TETURN T
		WHERE T.RECORD_STATUS = 'Y'
		AND T.TYPE_ID = 'Delay'
		AND T.DOCTOR_FLOW = DR.DOCTOR_FLOW)


order by dr.SESSION_NUMBER desc,so.org_level_id,dr.org_flow,NLSSORT(u.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M'),dr.doctor_flow,dr.CREATE_TIME desc

    	select dr.*,ed.*,u.*,pur.*,so.*
				 , EXTRACTVALUE(XMLTYPE(pur.user_resume), '/userResumeExtInfoForm/armyType') as ARMY_TYPE
    		from pdsci.RES_DOCTOR_RECRUIT dr
			left join  RES_DOCTOR ed
			on dr.DOCTOR_FLOW =  ed.DOCTOR_FLOW
    		left join  pdsci.SYS_USER u
    		on dr.DOCTOR_FLOW =  u.USER_FLOW
    		left join pdsci.PUB_USER_RESUME pur
    		on pur.USER_FLOW=dr.DOCTOR_FLOW
		 	left join pdsci.sys_org so on so.org_flow = dr.org_flow
    		where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y'
    		and ed.org_flow in(
    			SELECT ORG_FLOW
			FROM PDSCI.SYS_ORG
			WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'


			    	 AND ORG_PROV_ID = #{sysOrg.orgProvId}


			    	 AND ORG_CITY_ID = #{sysOrg.orgCityId}


			    	 AND ORG_FLOW = #{sysOrg.orgFlow}


			    	 AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}


			    	 AND ORG_TYPE_ID = #{sysOrg.orgTypeId}



				and dr.ORG_FLOW in
    			#{item}


				and ed.DOCTOR_TYPE_ID in
    			#{item}


				 and dr.TRAIN_YEAR in
				 #{item}

    		)

			 and ed.WORK_ORG_ID = #{workOrgId}


			 and ed.WORK_ORG_NAME = #{workOrgName}


			 AND pur.user_resume IS NOT NULL
			 AND EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') IS NOT NULL
			 AND EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') != 'NO'


			 AND pur.user_resume IS NOT NULL
			 AND
			 (
			 EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') = 'NO'
			 OR
			 EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') IS NULL
			 )



    				and dr.SESSION_NUMBER = #{resDoctorRecruit.sessionNumber}


    				and dr.CAT_SPE_ID = #{resDoctorRecruit.catSpeId}


    				and dr.SPE_ID = #{resDoctorRecruit.speId}


    				and dr.ORG_FLOW = #{resDoctorRecruit.orgFlow}


    				and dr.AUDIT_STATUS_ID=#{resDoctorRecruit.auditStatusId}


    				and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}



    			and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})




    				and u.USER_NAME like #{userName}



    				and u.ID_NO like #{idNo}



				and dr.SESSION_NUMBER in
				#{item}



    				and dr.CAT_SPE_ID = #{doctor.trainingTypeId}


    				and dr.SPE_ID = #{doctor.trainingSpeId}


    				and dr.SESSION_NUMBER = #{doctor.sessionNumber}


    				and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId}


					and ed.IS_YEAR_GRADUATE = #{doctor.isYearGraduate}


					and dr.doctor_Status_Id = #{doctor.doctorStatusId}


					and (

						OR ed.work_org_id = #{doctor.workOrgId}
						OR ed.work_org_name like CONCAT(CONCAT('%', #{doctor.workOrgName}),'%')

					)


    		order by dr.SESSION_NUMBER desc,so.org_level_id,dr.org_flow,NLSSORT(u.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M'),dr.doctor_flow,dr.CREATE_TIME desc

    	SELECT *
			from pdsci.RES_DOCTOR_RECRUIT dr, pdsci.SYS_USER u,  pdsci.RES_DOCTOR ed,pdsci.PUB_USER_RESUME pur
			where dr.RECORD_STATUS = 'Y' and u.RECORD_STATUS = 'Y' and ed.RECORD_STATUS = 'Y'  and pur.RECORD_STATUS = 'Y' 
    		and dr.DOCTOR_FLOW=u.USER_FLOW and u.USER_FLOW=ed.DOCTOR_FLOW and ed.DOCTOR_FLOW=pur.USER_FLOW
    		and dr.DOCTOR_FLOW=ed.DOCTOR_FLOW and u.USER_FLOW=pur.USER_FLOW 
    		and dr.DOCTOR_FLOW=pur.USER_FLOW
    		and exists(
    				select  RECRUIT_FLOW from res_doctor_recruit r
						where create_time = 
 						 (select max(create_time) from res_doctor_recruit 
   						  where record_status='Y' 
   						  and doctor_flow=r.doctor_flow


				    				and SESSION_NUMBER = #{resDoctorRecruit.sessionNumber} 


				    				and AUDIT_STATUS_ID = #{resDoctorRecruit.auditStatusId} 


    				)  
    				and dr.RECRUIT_FLOW=r.RECRUIT_FLOW
    		)
    		and exists (
					SELECT ORG_FLOW
					FROM PDSCI.SYS_ORG
					WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'
					and ed.org_flow =org_flow 


			    	 AND ORG_PROV_ID = #{sysOrg.orgProvId} 


			    	 AND ORG_CITY_ID = #{sysOrg.orgCityId} 


			    	 AND ORG_FLOW = #{sysOrg.orgFlow} 


			    	 AND ORG_LEVEL_ID = #{sysOrg.orgLevelId} 


			    	 AND ORG_TYPE_ID = #{sysOrg.orgTypeId} 



				and dr.ORG_FLOW in 
    			#{item}


				and ed.DOCTOR_TYPE_ID in
    			#{item}

    		)


    				and dr.SESSION_NUMBER = #{resDoctorRecruit.sessionNumber} 


    				and dr.CAT_SPE_ID = #{resDoctorRecruit.catSpeId} 


    				and dr.SPE_ID = #{resDoctorRecruit.speId} 


    				and dr.ORG_FLOW = #{resDoctorRecruit.orgFlow} 


    				and dr.AUDIT_STATUS_ID=#{resDoctorRecruit.auditStatusId}



    			and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})




    				and u.USER_NAME like #{userName} 



    				and u.ID_NO like #{idNo} 




    				and dr.CAT_SPE_ID = #{doctor.trainingTypeId} 


    				and dr.SPE_ID = #{doctor.trainingSpeId} 


    				and dr.SESSION_NUMBER = #{doctor.sessionNumber} 


    				and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId}

select score.score_flow,score.theory_score,score.skill_score,score.ext_score,score.test_id AS THEORY_TEST_ID,u.USER_FLOW, u.USER_CODE, u.USER_NAME, u.SEX_ID, u.SEX_NAME, u.USER_PHONE, u.USER_EMAIL
, u.ID_NO, u.USER_BIRTHDAY, u.TITLE_ID, u.TITLE_NAME, u.DEGREE_ID, u.DEGREE_NAME, u.EDUCATION_ID, u.EDUCATION_NAME, u.POST_ID, u.POST_NAME,
ed.ORG_FLOW as edOrgFlow,ed.ORG_Name as edOrgName,dr.*
, CASE WHEN ORG.ORG_LEVEL_ID = 'CountryOrg' THEN (SELECT DISTINCT ORG_NAME FROM RES_JOINT_ORG WHERE ORG_FLOW = ORG.ORG_FLOW AND RECORD_STATUS = 'Y')
ELSE (SELECT ORG_NAME FROM RES_JOINT_ORG WHERE JOINT_ORG_FLOW = ORG.ORG_FLOW AND  RECORD_STATUS = 'Y') END COUNTRY_ORG_NAME
from pdsci.res_score score
left join  pdsci.SYS_USER u
on score.DOCTOR_FLOW =  u.USER_FLOW
left join  RES_DOCTOR ed
on score.DOCTOR_FLOW =  ed.DOCTOR_FLOW
left join (
	select a.* from RES_DOCTOR_RECRUIT a,
	(    select t.doctor_flow,max(t.create_time) as create_time from  RES_DOCTOR_RECRUIT t where t.record_status='Y'


			and t.AUDIT_STATUS_ID=#{resDoctorRecruit.auditStatusId}

	 group by t.doctor_flow) b
	where a.doctor_flow=b.doctor_flow and  a.create_time=b.create_time
) dr on score.DOCTOR_FLOW =  dr.DOCTOR_FLOW
left join sys_org org on org.org_flow=ed.org_flow
where score.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y' and  dr.RECORD_STATUS= 'Y'
and  EXISTS (
SELECT null
FROM PDSCI.SYS_ORG
WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'


		AND ORG_PROV_ID = #{sysOrg.orgProvId}


		AND ORG_CITY_ID = #{sysOrg.orgCityId}


		AND ORG_FLOW = #{sysOrg.orgFlow}


		AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}


		AND ORG_TYPE_ID = #{sysOrg.orgTypeId}




		AND ORG_FLOW = #{doctor.orgFlow}



	and ORG_FLOW in
	#{item}

and ed.org_flow=org_flow
)

	and ed.DOCTOR_TYPE_ID in
	#{item}



		and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}



	and score.SCORE_PHASE_ID = #{scoreYear}


	and score.SCORE_TYPE_ID = #{typeId}



		and score.theory_score = '1'


		and score.theory_score != '1'



	and  score.test_id = #{testId}




		and u.USER_NAME like #{userName}



		and u.ID_NO like #{idNo}



	and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})



		and dr.CAT_SPE_ID = #{doctor.trainingTypeId}


		and dr.SPE_ID = #{doctor.trainingSpeId}


		and dr.TRAIN_YEAR = #{doctor.trainingYears}


		and ed.SESSION_NUMBER = #{doctor.sessionNumber}


		and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId}


order by  org.org_code,score.theory_score desc

select score.SKILL_SCORE_FLOW score_flow,score.theory_score,score.skill_score,score.ext_score,u.USER_FLOW, u.USER_CODE, u.USER_NAME, u.SEX_ID, u.SEX_NAME, u.USER_PHONE, u.USER_EMAIL
, u.ID_NO, u.USER_BIRTHDAY, u.TITLE_ID, u.TITLE_NAME, u.DEGREE_ID, u.DEGREE_NAME, u.EDUCATION_ID, u.EDUCATION_NAME, u.POST_ID, u.POST_NAME,
ed.ORG_FLOW as edOrgFlow,ed.ORG_Name as edOrgName,dr.*
from (
SELECT  T.DOCTOR_FLOW,T.SCORE_PHASE_ID, A.THEROY_SCORE_FLOW, B.SKILL_SCORE_FLOW,A.THEORY_SCORE,B.SKILL_SCORE,B.EXT_SCORE
FROM (SELECT DISTINCT S.DOCTOR_FLOW,S.SCORE_PHASE_ID
FROM RES_SCORE S
WHERE S.RECORD_STATUS='Y'
GROUP BY S.DOCTOR_FLOW,S.SCORE_PHASE_ID) T
LEFT JOIN (SELECT T.SCORE_FLOW THEROY_SCORE_FLOW,
T.DOCTOR_FLOW,
T.THEORY_SCORE,
T.SCORE_PHASE_ID
FROM RES_SCORE T
WHERE T.RECORD_STATUS = 'Y'
AND T.SCORE_TYPE_ID IN ('TheoryScore')) A
ON T.DOCTOR_FLOW = A.DOCTOR_FLOW
AND T.SCORE_PHASE_ID = A.SCORE_PHASE_ID
LEFT JOIN (SELECT T.SCORE_FLOW SKILL_SCORE_FLOW,
T.DOCTOR_FLOW,
T.SKILL_SCORE,
T.EXT_SCORE,
T.SCORE_PHASE_ID
FROM RES_SCORE T
WHERE T.RECORD_STATUS = 'Y'
AND T.SCORE_TYPE_ID IN ('SkillScore')) B
ON T.DOCTOR_FLOW = B.DOCTOR_FLOW
AND T.SCORE_PHASE_ID = B.SCORE_PHASE_ID ) score
left join  pdsci.SYS_USER u
on score.DOCTOR_FLOW =  u.USER_FLOW
left join  RES_DOCTOR ed
on score.DOCTOR_FLOW =  ed.DOCTOR_FLOW
left join (
select a.* from RES_DOCTOR_RECRUIT a,
(    select t.doctor_flow,max(t.create_time) as create_time from  RES_DOCTOR_RECRUIT t where t.record_status='Y'


		and t.AUDIT_STATUS_ID=#{resDoctorRecruit.auditStatusId}

 group by t.doctor_flow) b
where a.doctor_flow=b.doctor_flow and  a.create_time=b.create_time
) dr on score.DOCTOR_FLOW =  dr.DOCTOR_FLOW
left join sys_org org on org.org_flow=ed.org_flow
where  u.RECORD_STATUS= 'Y' and  dr.RECORD_STATUS= 'Y'
and  EXISTS (
SELECT null
FROM PDSCI.SYS_ORG
WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'


		AND ORG_PROV_ID = #{sysOrg.orgProvId}


		AND ORG_CITY_ID = #{sysOrg.orgCityId}


		AND ORG_FLOW = #{sysOrg.orgFlow}


		AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}


		AND ORG_TYPE_ID = #{sysOrg.orgTypeId}



	and ORG_FLOW in
	#{item}

and ed.org_flow=org_flow
)

	and ed.DOCTOR_TYPE_ID in
	#{item}



		and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}



	and score.SCORE_PHASE_ID = #{scoreYear}


	and score.SCORE_TYPE_ID = #{typeId}


	and score.theory_score = #{isHege}


	and score.skill_score = #{skillIsHege}




		and u.USER_NAME like #{userName}



		and u.ID_NO like #{idNo}



	and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})



		and dr.CAT_SPE_ID = #{doctor.trainingTypeId}


		and dr.SPE_ID = #{doctor.trainingSpeId}


		and dr.TRAIN_YEAR = #{doctor.trainingYears}


		and ed.SESSION_NUMBER = #{doctor.sessionNumber}


		and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId}


order by  org.org_code desc,u.user_code

select score.SKILL_SCORE_FLOW score_flow,score.THEROY_SCORE_FLOW,score.theory_score,score.THEROY_SCORE_PHASE_ID AS SCORE_PHASE_ID,score.skill_score, score.SKILL_SCORE_PHASE_ID AS SCORE_PHASE_NAME,score.ext_score,u.USER_FLOW, u.USER_CODE, u.USER_NAME, u.SEX_ID, u.SEX_NAME, u.USER_PHONE, u.USER_EMAIL , u.ID_NO, u.USER_BIRTHDAY, u.TITLE_ID, u.TITLE_NAME, u.DEGREE_ID, u.DEGREE_NAME, u.EDUCATION_ID, u.EDUCATION_NAME, u.POST_ID, u.POST_NAME, ed.ORG_FLOW as edOrgFlow,ed.ORG_Name as edOrgName,ed.training_spe_id,ed.training_spe_name, dr.RECRUIT_FLOW, dr.DOCTOR_FLOW, dr.ORG_FLOW, u.ORG_NAME, dr.SPE_ID, dr.SPE_NAME, dr.SWAP_FLAG, dr.RECRUIT_TYPE_ID, dr.RECRUIT_TYPE_NAME, dr.RECRUIT_YEAR, dr.RECRUIT_DATE, dr.RETEST_FLAG, dr.EXAM_RESULT, dr.AUDITION_RESULT, dr.OPER_RESULT, dr.TOTLE_RESULT, dr.ADMIT_FLAG, dr.RECRUIT_FLAG, dr.CONFIRM_FLAG, dr.SWAP_SPE_ID, dr.SWAP_SPE_NAME, dr.RECORD_STATUS, dr.CREATE_TIME, dr.CREATE_USER_FLOW, dr.MODIFY_TIME, dr.MODIFY_USER_FLOW, dr.RETEST_NOTICE, dr.ADMIT_NOTICE, dr.CAT_SPE_ID, dr.CAT_SPE_NAME, dr.SESSION_NUMBER, dr.TRAIN_YEAR, dr.YET_TRAIN_YEAR, dr.DOCTOR_STATUS_ID, dr.DOCTOR_STATUS_NAME, dr.DOCTOR_STRIKE_ID, dr.DOCTOR_STRIKE_NAME, dr.TRAIN_NO, dr.AUDIT_STATUS_ID, dr.AUDIT_STATUS_NAME, dr.PLACE_ID, dr.PLACE_NAME, dr.PROVE_FILE_URL, dr.COMPLETE_CERT_NO, dr.COMPLETE_FILE_URL, dr.CURR_DEGREE_CATEGORY_ID, dr.CURR_DEGREE_CATEGORY_NAME, dr.PROV_REMARK, dr.GRADUATION_YEAR, dr.ADMIN_SWAP_FLAG, dr.AVG_COMPLETE, dr.AVG_AUDIT, dr.MATERIAL_ID, dr.PRACTICING_SCOPE_ID, dr.PRACTICING_SCOPE_NAME, dr.CERTIFICATE_URI, dr.QUALIFICATION_MATERIAL_URI, dr.SPECIAL_CERTIFICATION_URI, dr.QUALIFICATION_MATERIAL_CODE, dr.TYPE_ID, dr.REGISTE_MANUA, dr.ROTATION_FLOW, dr.ROTATION_NAME, dr.SPECIAL_CERT_NO, dr.SPECIAL_FILE_URL, dr.SECOND_SPE_ID, dr.SECOND_SPE_NAME, dr.GRADUATION_CERTIFICATE_NO, dr.CERTIFICATE_DATE, dr.GRADUATION_CERTIFICATE_TYPE, dr.CHANGE_SPE_ID, dr.CHANGE_SPE_NAME, dr.RETURN_BACK_FLAG, dr.RETURN_BACK_REMARK, dr.IS_SEND, dr.IS_CERT_SEND, dr.QUALIFIED_ID, dr.CERTIFICATE_ISSUING_STATUS, dr.JOINT_ORG_NAME, dr.OPER_STATUS_ID, dr.EXAM_STATUS_NAME, dr.ARMY_HOSPITAL, dr.IN_JOINT_ORG_TRAIN, dr.WORK_UNIT, dr.EXAM_STATUS_ID, dr.OPER_STATUS_NAME, dr.AUDITION_STATUS_ID, dr.JOINT_ORG_FLOW, dr.AUDITION_STATUS_NAME, dr.WORK_UNIT_CODE, JGA.LOCAL_REASON, JGA.CITY_REASON, JGA.GLOBAL_REASON, score.THEORY_TEST_ID,score.SKILL_TEST_ID, score.JES_THEORY_TEST_ID,score.JES_SKILL_TEST_ID, score.THEROY_SESSION_NUMBER, score.skill_SESSION_NUMBER, GREATEST(nvl ( score.THEORY_TEST_ID, 0 ),nvl ( score.SKILL_TEST_ID, 0 )) as QUALIFIED_TEST_ID , CASE WHEN ORG.ORG_LEVEL_ID = 'CountryOrg' THEN (SELECT DISTINCT ORG_NAME FROM RES_JOINT_ORG WHERE ORG_FLOW = ORG.ORG_FLOW AND RECORD_STATUS = 'Y') ELSE (SELECT ORG_NAME FROM RES_JOINT_ORG WHERE JOINT_ORG_FLOW = ORG.ORG_FLOW AND RECORD_STATUS = 'Y') END COUNTRY_ORG_NAME from ( SELECT case when A.DOCTOR_FLOW is not null then a.doctor_flow when b.doctor_flow is not null then b.doctor_flow else null end doctor_flow, A.THEROY_SCORE_FLOW, A.SESSION_NUMBER THEROY_SESSION_NUMBER, B.SESSION_NUMBER skill_SESSION_NUMBER,B.SKILL_SCORE_FLOW,A.THEROY_SCORE_PHASE_ID, A.THEORY_SCORE, B.SKILL_SCORE_PHASE_ID, B.SKILL_SCORE, B.EXT_SCORE,A.THEORY_TEST_ID,B.SKILL_TEST_ID,A.JES_THEORY_TEST_ID,B.JES_SKILL_TEST_ID FROM ( select R.SCORE_FLOW AS THEROY_SCORE_FLOW,a.DOCTOR_FLOW,A.SESSION_NUMBER,R.THEORY_SCORE,a.THEROY_SCORE_PHASE_ID,R.TEST_ID as THEORY_TEST_ID, JES.TEST_ID AS JES_THEORY_TEST_ID from (SELECT T.DOCTOR_FLOW,T.SESSION_NUMBER,T.SCORE_PHASE_ID AS THEROY_SCORE_PHASE_ID FROM RES_SCORE T WHERE T.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID IN ('TheoryScore') AND THEORY_SCORE = '1'
AND T.SCORE_PHASE_ID = #{theroyScoreYear}
) a LEFT JOIN JSRES_EXAM_SIGNUP JES ON a.DOCTOR_FLOW = JES.DOCTOR_FLOW AND JES.SIGNUP_TYPE_ID = 'Theory' AND a.THEROY_SCORE_PHASE_ID = JES.SIGNUP_YEAR AND JES.RECORD_STATUS = 'Y' AND JES.AUDIT_STATUS_ID = 'GlobalPassed' left join RES_SCORE R on a.DOCTOR_FLOW=R.DOCTOR_FLOW where a.THEROY_SCORE_PHASE_ID=R.SCORE_PHASE_ID AND R.SCORE_TYPE_ID IN ('TheoryScore') and R.RECORD_STATUS = 'Y'
AND R.SCORE_PHASE_ID = #{theroyScoreYear}
AND R.IS_AFFIRM_ID = 'Y'
) A FULL JOIN ( select R.SCORE_FLOW AS SKILL_SCORE_FLOW,a.DOCTOR_FLOW,a.SESSION_NUMBER,R.SKILL_SCORE,R.EXT_SCORE,a.SKILL_SCORE_PHASE_ID,R.TEST_ID as SKILL_TEST_ID, JES.TEST_ID AS JES_SKILL_TEST_ID from (SELECT T.DOCTOR_FLOW,T.SESSION_NUMBER,T.SCORE_PHASE_ID AS SKILL_SCORE_PHASE_ID FROM RES_SCORE T WHERE T.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID IN ('SkillScore') AND SKILL_SCORE = '1'
AND T.SCORE_PHASE_ID = #{skillScoreYear}
) a LEFT JOIN JSRES_EXAM_SIGNUP JES ON a.DOCTOR_FLOW = JES.DOCTOR_FLOW AND JES.SIGNUP_TYPE_ID = 'Skill' AND a.SKILL_SCORE_PHASE_ID = JES.SIGNUP_YEAR AND JES.RECORD_STATUS = 'Y' AND JES.AUDIT_STATUS_ID = 'GlobalPassed' left join RES_SCORE R on a.DOCTOR_FLOW=R.DOCTOR_FLOW where a.SKILL_SCORE_PHASE_ID=R.SCORE_PHASE_ID AND R.SCORE_TYPE_ID IN ('SkillScore') and R.RECORD_STATUS = 'Y'
AND R.SCORE_PHASE_ID = #{skillScoreYear}
AND R.IS_AFFIRM_ID = 'Y'
) B ON A.DOCTOR_FLOW = B.DOCTOR_FLOW
) score left join pdsci.SYS_USER u on score.DOCTOR_FLOW = u.USER_FLOW left join RES_DOCTOR ed on score.DOCTOR_FLOW = ed.DOCTOR_FLOW left join ( select a.* from RES_DOCTOR_RECRUIT a, ( select t.doctor_flow,t.create_time as create_time from RES_DOCTOR_RECRUIT t where t.record_status='Y'
and t.AUDIT_STATUS_ID=#{resDoctorRecruit.auditStatusId}
) b where a.doctor_flow=b.doctor_flow and a.create_time=b.create_time ) dr on score.DOCTOR_FLOW = dr.DOCTOR_FLOW and score.THEROY_SESSION_NUMBER = dr.SESSION_NUMBER and score.skill_SESSION_NUMBER = dr.SESSION_NUMBER left join sys_org org on org.org_flow=ed.org_flow LEFT JOIN JSRES_GRADUATION_APPLY JGA ON DR.RECRUIT_FLOW = JGA.RECRUIT_FLOW
and JGA.APPLY_YEAR = #{resDoctorRecruit.graduationYear}
where u.RECORD_STATUS= 'Y' and dr.RECORD_STATUS= 'Y' and EXISTS ( SELECT null FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y' AND score.theory_score = '1' AND score.skill_score = '1'
AND ORG_PROV_ID = #{sysOrg.orgProvId}
AND ORG_CITY_ID = #{sysOrg.orgCityId}
AND ORG_FLOW = #{sysOrg.orgFlow}
AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}
AND ORG_TYPE_ID = #{sysOrg.orgTypeId}
AND ORG_FLOW = #{doctor.orgFlow}
and ORG_FLOW in #{item}
and ed.org_flow=org_flow )
and ed.DOCTOR_TYPE_ID in #{item}
and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}
AND nvl(score.THEORY_TEST_ID, 0) != nvl(score.JES_THEORY_TEST_ID, 0) AND nvl(score.SKILL_TEST_ID, 0) != nvl(score.JES_SKILL_TEST_ID, 0)
AND GREATEST(nvl ( score.THEORY_TEST_ID, 0 ),nvl ( score.SKILL_TEST_ID, 0 )) = #{testId}
AND score.THEORY_TEST_ID = #{scoreTestId}
AND DR.CAT_SPE_ID = #{trainingTypeId}
and score.SCORE_PHASE_ID = #{scoreYear}
and score.SCORE_TYPE_ID = #{typeId}
and score.theory_score = '1'
and score.theory_score != '1'
and score.skill_score = #{skillIsHege}
and u.USER_NAME like #{userName}
and u.ID_NO like #{idNo}
and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})
and dr.CAT_SPE_ID = #{doctor.trainingTypeId}
and dr.SPE_ID = #{doctor.trainingSpeId}
and dr.TRAIN_YEAR = #{doctor.trainingYears}
and ed.SESSION_NUMBER = #{doctor.sessionNumber}
and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId}
And ((nvl(score.THEROY_SCORE_PHASE_ID,0)-nvl(score.SKILL_SCORE_PHASE_ID,0)) > -5 and (5 > nvl(score.THEROY_SCORE_PHASE_ID,0)-nvl(score.SKILL_SCORE_PHASE_ID,0))) AND 5 > dr.GRADUATION_YEAR - nvl(score.THEROY_SCORE_PHASE_ID,0) AND 5 > dr.GRADUATION_YEAR - nvl(score.SKILL_SCORE_PHASE_ID,0)
and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}
order by org.org_code desc,u.user_code

