) b where a.doctor_flow=b.doctor_flow and a.create_time=b.create_time ) dr on score.DOCTOR_FLOW = dr.DOCTOR_FLOW and score.SESSION_NUMBER = dr.SESSION_NUMBER left join sys_org org on org.org_flow=ed.org_flow where score.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y' and dr.RECORD_STATUS= 'Y' and EXISTS ( SELECT null FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'
