US_NAME, dr.AUDITION_STATUS_ID, dr.JOINT_ORG_FLOW, dr.AUDITION_STATUS_NAME, dr.WORK_UNIT_CODE from pdsci.res_score score left join pdsci.SYS_USER u on score.DOCTOR_FLOW = u.USER_FLOW left join RES_DOCTOR ed on score.DOCTOR_FLOW = ed.DOCTOR_FLOW left join ( select a.* from RES_DOCTOR_RECRUIT a, ( select t.doctor_flow,max(t.create_time) as create_time from RES_DOCTOR_RECRUIT t where t.record_status='Y'
and t.AUDIT_STATUS_ID=#{resDoctorRecruit.auditStatusId}
group by t.doctor_flow) b where a.doctor_flow=b.doctor_flow and a.create_time=b.create_time ) dr on score.DOCTOR_FLOW = dr.DOCTOR_FLOW left join sys_org org on org.org_flow=ed.org_flow where score.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y' and dr.RECORD_STATUS= 'Y' and EXISTS ( SELECT null FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'
AND ORG_PROV_ID = #{sysOrg.orgProvId}
AND ORG_CITY_ID = #{sysOrg.orgCityId}
AND ORG_FLOW = #{sysOrg.orgFlow}
AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}
AND ORG_TYPE_ID = #{sysOrg.orgTypeId}
and ORG_FLOW in #{item}
and ed.org_flow=org_flow )
and ed.DOCTOR_TYPE_ID in #{item}
and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}
and score.SCORE_PHASE_ID = #{scoreYear}
and score.SCORE_TYPE_ID = #{typeId}
and score.skill_score = #{notAllQualified}
and score.theory_score >= #{hegeScore}
and not (score.theory_score >= #{hegeScore})
and u.USER_NAME like #{userName}
and u.ID_NO like #{idNo}
and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})
and ed.SESSION_NUMBER in #{item}
and dr.CAT_SPE_ID = #{doctor.trainingTypeId}
and dr.SPE_ID = #{doctor.trainingSpeId}
and dr.TRAIN_YEAR = #{doctor.trainingYears}
and ed.SESSION_NUMBER = #{doctor.sessionNumber}
and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId}
order by org.org_code,u.user_code

SELECT DR.AUDITION_STATUS_ID, DR.AUDITION_STATUS_NAME, DR.OPER_STATUS_ID, DR.OPER_STATUS_NAME, DR.IN_JOINT_ORG_TRAIN, DR.JOINT_ORG_FLOW, DR.JOINT_ORG_NAME, DR.ROTATION_FLOW, DR.ROTATION_NAME, DR.SPECIAL_CERT_NO, DR.SPECIAL_FILE_URL, DR.AVG_COMPLETE, DR.AVG_AUDIT, DR.MATERIAL_ID, DR.PRACTICING_SCOPE_ID, DR.PRACTICING_SCOPE_NAME, DR.CERTIFICATE_URI, DR.QUALIFICATION_MATERIAL_URI, DR.SPECIAL_CERTIFICATION_URI, DR.QUALIFICATION_MATERIAL_CODE, DR.TYPE_ID, DR.REGISTE_MANUA, DR.RECRUIT_FLOW, DR.DOCTOR_FLOW, DR.ORG_FLOW, DR.SPE_ID, DR.SPE_NAME, DR.SWAP_FLAG, DR.RECRUIT_TYPE_ID, DR.RECRUIT_TYPE_NAME, DR.RECRUIT_YEAR, DR.RECRUIT_DATE, DR.RETEST_FLAG, DR.EXAM_RESULT, DR.AUDITION_RESULT, DR.OPER_RESULT, DR.TOTLE_RESULT, DR.ADMIT_FLAG, DR.RECRUIT_FLAG, DR.CONFIRM_FLAG, DR.SWAP_SPE_ID, DR.SWAP_SPE_NAME, DR.RECORD_STATUS, DR.CREATE_TIME, DR.CREATE_USER_FLOW, DR.MODIFY_TIME, DR.MODIFY_USER_FLOW, DR.RETEST_NOTICE, DR.ADMIT_NOTICE, DR.GLOBAL_NOTICE, DR.CAT_SPE_ID, DR.CAT_SPE_NAME, DR.SESSION_NUMBER, DR.TRAIN_YEAR, DR.YET_TRAIN_YEAR, DR.DOCTOR_STATUS_ID, DR.DOCTOR_STATUS_NAME, DR.DOCTOR_STRIKE_ID, DR.DOCTOR_STRIKE_NAME, DR.TRAIN_NO, DR.AUDIT_STATUS_ID, DR.AUDIT_STATUS_NAME, DR.PLACE_ID, DR.PLACE_NAME, DR.PROVE_FILE_URL, DR.COMPLETE_CERT_NO, DR.COMPLETE_FILE_URL, DR.CURR_DEGREE_CATEGORY_ID, DR.CURR_DEGREE_CATEGORY_NAME, DR.PROV_REMARK, DR.GRADUATION_YEAR, DR.ADMIN_SWAP_FLAG, DR.SECOND_SPE_ID, DR.SECOND_SPE_NAME, DR.GRADUATION_CERTIFICATE_NO, DR.CERTIFICATE_DATE, DR.GRADUATION_CERTIFICATE_TYPE, DR.CHANGE_SPE_ID, DR.CHANGE_SPE_NAME, DR.RETURN_BACK_FLAG, DR.RETURN_BACK_REMARK, DR.IS_SEND, DR.IS_CERT_SEND, DR.QUALIFIED_ID, DR.CERTIFICATE_ISSUING_STATUS, DR.WORK_UNIT, DR.WORK_UNIT_CODE, DR.ARMY_HOSPITAL, DR.EXAM_STATUS_ID, DR.EXAM_STATUS_NAME, DR.ORG_NAME, DR.IS_RETRAIN, DR.ORG_AUDIT, DR.JOINT_ORG_AUDIT, DR.SIGNUP_WAY, U.USER_FLOW, U.USER_CODE, U.USER_NAME, U.SEX_ID, U.SEX_NAME, U.USER_PHONE, U.USER_EMAIL, U.ID_NO, U.USER_BIRTHDAY, U.TITLE_ID, U.TITLE_NAME, U.DEGREE_ID, U.DEGREE_NAME, U.EDUCATION_ID, U.EDUCATION_NAME, U.POST_ID, U.POST_NAME, U.USER_ADDRESS, ED.ORG_FLOW AS EDORGFLOW, ED.ORG_NAME AS EDORGNAME, ED.DOCTOR_TYPE_NAME, ED.DOCTOR_TYPE_ID, ED.WORK_ORG_ID, ED.WORK_ORG_NAME
, EXTRACTVALUE(XMLTYPE(pur.user_resume), '/userResumeExtInfoForm/armyType') as ARMY_TYPE
FROM RES_DOCTOR_RECRUIT DR LEFT JOIN PDSCI.SYS_USER U ON DR.DOCTOR_FLOW = U .USER_FLOW LEFT JOIN RES_DOCTOR ED ON DR.DOCTOR_FLOW = ED.DOCTOR_FLOW
INNER JOIN PUB_USER_RESUME pur ON DR.DOCTOR_FLOW = pur.USER_FLOW AND pur.RECORD_STATUS = 'Y'
WHERE DR.RECORD_STATUS = 'Y'
and ED.WORK_ORG_ID = #{workOrgId}
and ED.WORK_ORG_NAME = #{workOrgName}
AND pur.user_resume IS NOT NULL AND EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') IS NOT NULL AND EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') != 'NO'
AND pur.user_resume IS NOT NULL AND ( EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') = 'NO' OR EXTRACTVALUE(XMLTYPE(pur.user_resume),'/userResumeExtInfoForm/armyType') IS NULL )
AND U .RECORD_STATUS = 'Y' AND NOT EXISTS ( SELECT 1 FROM RES_DOCOTR_DELAY_TETURN T WHERE T.RECORD_STATUS = 'Y' AND T.TYPE_ID = 'ReturnTraining' AND T.AUDIT_STATUS_ID = '1' AND T.RECRUIT_FLOW = DR.RECRUIT_FLOW AND T.SESSION_NUMBER = DR.SESSION_NUMBER AND T.DOCTOR_FLOW = DR.DOCTOR_FLOW ) AND DR.AUDIT_STATUS_ID != 'NotSubmit' AND EXISTS ( SELECT 1 FROM SYS_ORG O WHERE O.RECORD_STATUS = 'Y' AND O.ORG_FLOW = DR.ORG_FLOW
AND ORG_PROV_ID = #{sysOrg.orgProvId}
AND ORG_CITY_ID = #{sysOrg.orgCityId}
AND ORG_FLOW = #{sysOrg.orgFlow}
AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}
AND ORG_TYPE_ID = #{sysOrg.orgTypeId}
)
and dr.ORG_FLOW in #{item}
and dr.JOINT_ORG_FLOW in #{item}
and (dr.ORG_FLOW in #{item} or dr.joint_org_flow in #{item} )
and dr.ORG_FLOW = #{joinOrgFlow}
and dr.SIGNUP_WAY = #{signupWay}
and ed.DOCTOR_TYPE_ID in #{item}
and dr.TRAIN_YEAR in #{item}
and dr.SESSION_NUMBER in #{item}
and ed.SESSION_NUMBER >= #{sessionNumberBefore}
and dr.SESSION_NUMBER = #{resDoctorRecruit.sessionNumber}
and dr.IS_RETRAIN = #{resDoctorRecruit.isRetrain}
and dr.CAT_SPE_ID = #{resDoctorRecruit.catSpeId}
and dr.SPE_ID = #{resDoctorRecruit.speId}
and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}
and dr.DOCTOR_STATUS_ID != 'Auditing' and dr.DOCTOR_STATUS_ID != 'NotPassed' and dr.DOCTOR_STATUS_ID is not null
and dr.DOCTOR_STATUS_ID=#{resDoctorRecruit.doctorStatusId}
and dr.DOCTOR_STATUS_ID in ('20','21','22','24')
and dr.AUDIT_STATUS_ID=#{resDoctorRecruit.auditStatusId}
and dr.org_audit = #{resDoctorRecruit.orgAudit}
and dr.joint_org_audit = #{resDoctorRecruit.jointOrgAudit}
and dr.recruit_flag =#{resDoctorRecruit.recruitFlag}
and dr.confirm_Flag =#{resDoctorRecruit.confirmFlag}
and dr.signup_way =#{resDoctorRecruit.signupWay}
and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})
and u.USER_NAME like #{userName}
and u.ID_NO like #{idNo}
and u.NATIVE_PLACE_CITY_ID = #{user.nativePlaceCityId}
and dr.CAT_SPE_ID = #{doctor.trainingTypeId}
and dr.SPE_ID = #{doctor.trainingSpeId}
and dr.SESSION_NUMBER = #{doctor.sessionNumber}
and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId}
and ed.WORK_ORG_LEVEL_ID = #{doctor.workOrgLevelId}
and ed.IS_YEAR_GRADUATE = #{doctor.isYearGraduate}
and dr.doctor_Status_Id = #{doctor.doctorStatusId}
and dr.DOCTOR_STATUS_ID in ('20','21','22')
and (
OR ed.work_org_id = #{doctor.workOrgId} OR ed.work_org_name like CONCAT(CONCAT('%', #{doctor.workOrgName}),'%')
)
AND EXISTS (SELECT 1 FROM RES_DOCOTR_DELAY_TETURN T WHERE T.RECORD_STATUS = 'Y' AND T.TYPE_ID = 'Delay' AND T.DOCTOR_FLOW = DR.DOCTOR_FLOW)
AND NOT EXISTS (SELECT 1 FROM RES_DOCOTR_DELAY_TETURN T WHERE T.RECORD_STATUS = 'Y' AND T.TYPE_ID = 'Delay' AND T.DOCTOR_FLOW = DR.DOCTOR_FLOW)
order by to_number(decode(dr.AUDITION_RESULT,null,'0',dr.audition_result)) desc
order by to_number(decode(dr.OPER_RESULT,null,'0',dr.OPER_RESULT)) desc
order by to_number(decode(dr.AUDITION_RESULT,null,'0',dr.AUDITION_RESULT))+ to_number(decode(dr.OPER_RESULT,null,'0',dr.OPER_RESULT)) desc
order by dr.session_number desc,dr.org_flow,dr.spe_id,NLSSORT(u.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M'), dr.doctor_flow,dr.CREATE_TIME desc

