SELECT ORG_CODE,LPAD(SUM(NUM)+1, 3, '0') NUM FROM ( SELECT ORG.ORG_FLOW, ORG.ORG_CODE, (SELECT COUNT(1) FROM RES_DOCTOR_recruit T WHERE T.RECORD_STATUS='Y' and t.audit_status_id='Passed' and t.graduation_Certificate_No is not null AND (T.cat_spe_id='WMFirst'OR T.cat_spe_id='DoctorTrainingSpe') AND T.ORG_FLOW = ORG.ORG_FLOW
AND T.graduation_Certificate_No LIKE #{year}
) NUM FROM (SELECT ORG.ORG_CODE, JIORG.JOINT_ORG_FLOW ORG_FLOW FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW WHERE 1= 1 AND JIORG.RECORD_STATUS='Y'
AND ORG.ORG_FLOW = #{orgFlow}
UNION ALL SELECT ORG.ORG_CODE, ORG.ORG_FLOW FROM SYS_ORG ORG WHERE 1=1
AND ORG.ORG_FLOW = #{orgFlow}
) ORG ) TEMP GROUP BY ORG_CODE

select LPAD(count(1)+1, 4, '0') NUM from res_doctor_recruit rd where rd.record_status='Y' and rd.cat_spe_id='AssiGeneral' and rd.graduation_Certificate_No is not null
AND (#{yearbefore}||rd.graduation_Certificate_No) LIKE #{year}
and exists ( select 1 from sys_org org where 1=1
AND org.org_city_id = #{orgCityId}
and org.record_status='Y' and org.org_flow=rd.org_flow)

select LPAD(count(1)+1, 4, '0') NUM from res_doctor_recruit rd where rd.record_status='Y' and rd.graduation_Certificate_No is not null AND ( (rd.cat_spe_id='WMFirst' and rd.SPE_ID='51') OR (rd.cat_spe_id='WMFirst' and rd.SPE_ID='52') or (rd.cat_spe_id='DoctorTrainingSpe'and rd.SPE_ID='0700'))
AND (#{yearbefore}||rd.graduation_Certificate_No) LIKE #{year}
and exists ( select 1 from sys_org org where 1=1
AND org.org_city_id = #{orgCityId}
and org.record_status='Y' and org.org_flow=rd.org_flow)

select LPAD(count(1)+1, 4, '0') NUM from res_doctor_recruit rd where rd.record_status='Y' AND ( (rd.cat_spe_id='WMFirst' and rd.SPE_ID !='51') OR (rd.cat_spe_id='WMFirst' and rd.SPE_ID !='52') or (rd.cat_spe_id='DoctorTrainingSpe'and rd.SPE_ID !='0700'))
AND rd.graduation_Certificate_No LIKE #{year}
and exists ( select 1 from sys_org org where 1=1
AND org.org_city_id = #{orgCityId}
and org.record_status='Y' and org.org_flow=rd.org_flow)

select u.USER_FLOW, u.USER_CODE, u.USER_NAME, u.SEX_ID, u.SEX_NAME, u.USER_PHONE, u.USER_EMAIL , u.ID_NO, u.USER_BIRTHDAY, u.TITLE_ID, u.TITLE_NAME, u.DEGREE_ID, u.DEGREE_NAME, u.EDUCATION_ID, u.EDUCATION_NAME, u.POST_ID, u.POST_NAME, ed.ORG_FLOW as edOrgFlow,ed.ORG_Name as edOrgName, ED.COMPLETE_START_DATE,ED.COMPLETE_END_DATE,ED.TRAINING_SPE_NAME,ED.ORG_NAME,ED.ORG_FLOW ,RESUME.USER_RESUME,TS.SCORE_TYPE_ID,TS.SCORE_FLOW,TS.THEORY_SCORE,TS.THEORY_SCORE_YEAR,SS.SKILL_SCORE_FLOW,SS.SKILL_SCORE,SS.EXT_SCORE,SS.SKILL_SCORE_YEAR ,PS.PUBLIC_SCORE_FLOW,PS.PUBLIC_ALL,PS.PUBLIC_NOTALL_PASS, PS.PUBLIC_NOTALL_SCORE, PS.PUBLIC_SCORE_YEAR,ed.DISAGREE_REASON,dr.DOCTOR_FLOW,ed.GRADUATION_STATUS_ID ,ed.CERTIFICATE_NO from pdsci.RES_DOCTOR_RECRUIT dr left join pdsci.SYS_USER u on dr.DOCTOR_FLOW = u.USER_FLOW left join RES_DOCTOR ed on dr.DOCTOR_FLOW = ed.DOCTOR_FLOW LEFT JOIN PUB_USER_RESUME RESUME ON ED.DOCTOR_FLOW=RESUME.USER_FLOW LEFT JOIN (SELECT T.DOCTOR_FLOW, T.SCORE_TYPE_ID, S.SCORE_FLOW, S.THEORY_SCORE, S.SCORE_PHASE_ID THEORY_SCORE_YEAR FROM (SELECT S.DOCTOR_FLOW, S.SCORE_TYPE_ID, MAX(S.SCORE_PHASE_ID) SCORE_PHASE_ID FROM RES_SCORE S WHERE S.RECORD_STATUS = 'Y' GROUP BY S.DOCTOR_FLOW, S.SCORE_TYPE_ID) T LEFT JOIN RES_SCORE S ON S.DOCTOR_FLOW = T.DOCTOR_FLOW AND S.SCORE_TYPE_ID = T.SCORE_TYPE_ID AND S.SCORE_PHASE_ID = T.SCORE_PHASE_ID WHERE S.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID = 'TheoryScore') TS ON TS.DOCTOR_FLOW=ED.DOCTOR_FLOW LEFT JOIN (select T.DOCTOR_FLOW, T.SCORE_TYPE_ID SKILL_SCORE_TYPE_ID, S.SCORE_FLOW SKILL_SCORE_FLOW, S.SKILL_SCORE, S.EXT_SCORE, S.SCORE_PHASE_ID SKILL_SCORE_YEAR from (SELECT S.DOCTOR_FLOW, S.SCORE_TYPE_ID, MAX(S.SCORE_PHASE_ID) SCORE_PHASE_ID FROM RES_SCORE S WHERE S.RECORD_STATUS = 'Y' GROUP BY S.DOCTOR_FLOW, S.SCORE_TYPE_ID) T left JOIN RES_SCORE S ON S.DOCTOR_FLOW = T.DOCTOR_FLOW AND S.SCORE_TYPE_ID = T.SCORE_TYPE_ID AND S.SCORE_PHASE_ID = T.SCORE_PHASE_ID WHERE S.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID = 'SkillScore') SS ON SS.DOCTOR_FLOW=ED.DOCTOR_FLOW LEFT JOIN (SELECT T.SCORE_FLOW PUBLIC_SCORE_FLOW, T.DOCTOR_FLOW, T.THEORY_SCORE PUBLIC_ALL, T.SKILL_SCORE PUBLIC_NOTALL_PASS, T.EXT_SCORE PUBLIC_NOTALL_SCORE, T.SCORE_PHASE_ID PUBLIC_SCORE_YEAR, T.SCORE_TYPE_ID FROM RES_SCORE T WHERE T.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID IN ('PublicScore'))PS ON PS.DOCTOR_FLOW=ED.DOCTOR_FLOW left join sys_org org on org.org_flow=ed.org_flow where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y' and dr.org_flow in( SELECT ORG_FLOW FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'
AND ORG_PROV_ID = #{sysOrg.orgProvId}
AND ORG_CITY_ID = #{sysOrg.orgCityId}
AND ORG_FLOW = #{sysOrg.orgFlow}
AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}
AND ORG_TYPE_ID = #{sysOrg.orgTypeId}
)
and dr.ORG_FLOW in #{item}
and ed.DOCTOR_TYPE_ID in #{item}
and ed.GRADUATION_STATUS_ID in #{item}
and dr.SESSION_NUMBER = #{resDoctorRecruit.sessionNumber}
and dr.CAT_SPE_ID = #{resDoctorRecruit.catSpeId}
and dr.SPE_ID = #{resDoctorRecruit.speId}
and dr.ORG_FLOW = #{resDoctorRecruit.orgFlow}
and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}
and dr.AUDIT_STATUS_ID=#{resDoctorRecruit.auditStatusId}
and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})
and u.USER_NAME like #{userName}
and u.ID_NO like #{idNo}
and dr.CAT_SPE_ID = #{doctor.trainingTypeId}
and dr.SPE_ID = #{doctor.trainingSpeId}
and dr.SESSION_NUMBER = #{doctor.sessionNumber}
and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId}
and ed.WORK_ORG_LEVEL_ID = #{doctor.workOrgLevelId}
and ed.IS_YEAR_GRADUATE = #{doctor.isYearGraduate}
and ed.WORK_ORG_ID = #{doctor.workOrgId}
order by org.org_code desc

select u.USER_FLOW, u.USER_CODE, u.USER_NAME, u.SEX_ID, u.SEX_NAME, u.USER_PHONE, u.USER_EMAIL , u.ID_NO, u.USER_BIRTHDAY, u.TITLE_ID, u.TITLE_NAME, u.DEGREE_ID, u.DEGREE_NAME, u.EDUCATION_ID, u.EDUCATION_NAME, u.POST_ID, u.POST_NAME, ed.ORG_FLOW as edOrgFlow,ed.ORG_Name as edOrgName, ED.COMPLETE_START_DATE,ED.COMPLETE_END_DATE,ED.TRAINING_SPE_NAME,ED.ORG_NAME,ED.ORG_FLOW ,RESUME.USER_RESUME,TS.SCORE_TYPE_ID,TS.SCORE_FLOW,TS.THEORY_SCORE,TS.THEORY_SCORE_YEAR,SS.SKILL_SCORE_FLOW,SS.SKILL_SCORE,SS.EXT_SCORE,SS.SKILL_SCORE_YEAR ,PS.PUBLIC_SCORE_FLOW,PS.PUBLIC_ALL,PS.PUBLIC_NOTALL_PASS, PS.PUBLIC_NOTALL_SCORE, PS.PUBLIC_SCORE_YEAR,ed.DISAGREE_REASON,dr.DOCTOR_FLOW,ed.GRADUATION_STATUS_ID ,ed.CERTIFICATE_NO,ed.GRADUATION_STATUS_NAME from pdsci.RES_DOCTOR_RECRUIT dr left join pdsci.SYS_USER u on dr.DOCTOR_FLOW = u.USER_FLOW left join RES_DOCTOR ed on dr.DOCTOR_FLOW = ed.DOCTOR_FLOW LEFT JOIN PUB_USER_RESUME RESUME ON ED.DOCTOR_FLOW=RESUME.USER_FLOW LEFT JOIN (SELECT T.DOCTOR_FLOW, T.SCORE_TYPE_ID, S.SCORE_FLOW, S.THEORY_SCORE, S.SCORE_PHASE_ID THEORY_SCORE_YEAR FROM (SELECT S.DOCTOR_FLOW, S.SCORE_TYPE_ID, MAX(S.SCORE_PHASE_ID) SCORE_PHASE_ID FROM RES_SCORE S WHERE S.RECORD_STATUS = 'Y' GROUP BY S.DOCTOR_FLOW, S.SCORE_TYPE_ID) T LEFT JOIN RES_SCORE S ON S.DOCTOR_FLOW = T.DOCTOR_FLOW AND S.SCORE_TYPE_ID = T.SCORE_TYPE_ID AND S.SCORE_PHASE_ID = T.SCORE_PHASE_ID WHERE S.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID = 'TheoryScore') TS ON TS.DOCTOR_FLOW=ED.DOCTOR_FLOW LEFT JOIN (select T.DOCTOR_FLOW, T.SCORE_TYPE_ID SKILL_SCORE_TYPE_ID, S.SCORE_FLOW SKILL_SCORE_FLOW, S.SKILL_SCORE, S.EXT_SCORE, S.SCORE_PHASE_ID SKILL_SCORE_YEAR from (SELECT S.DOCTOR_FLOW, S.SCORE_TYPE_ID, MAX(S.SCORE_PHASE_ID) SCORE_PHASE_ID FROM RES_SCORE S WHERE S.RECORD_STATUS = 'Y' GROUP BY S.DOCTOR_FLOW, S.SCORE_TYPE_ID) T left JOIN RES_SCORE S ON S.DOCTOR_FLOW = T.DOCTOR_FLOW AND S.SCORE_TYPE_ID = T.SCORE_TYPE_ID AND S.SCORE_PHASE_ID = T.SCORE_PHASE_ID WHERE S.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID = 'SkillScore') SS ON SS.DOCTOR_FLOW=ED.DOCTOR_FLOW LEFT JOIN (SELECT T.SCORE_FLOW PUBLIC_SCORE_FLOW, T.DOCTOR_FLOW, T.THEORY_SCORE PUBLIC_ALL, T.SKILL_SCORE PUBLIC_NOTALL_PASS, T.EXT_SCORE PUBLIC_NOTALL_SCORE, T.SCORE_PHASE_ID PUBLIC_SCORE_YEAR, T.SCORE_TYPE_ID FROM RES_SCORE T WHERE T.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID IN ('PublicScore'))PS ON PS.DOCTOR_FLOW=ED.DOCTOR_FLOW left join sys_org org on org.org_flow=ed.org_flow where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y' and dr.org_flow in( SELECT ORG_FLOW FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'
AND ORG_FLOW = #{orgFlow}
AND ORG_FLOW IN ( SELECT DISTINCT RDR.ORG_FLOW FROM RES_DOCTOR_RECRUIT RDR LEFT JOIN RES_DOCTOR RD ON RDR.DOCTOR_FLOW=RD.DOCTOR_FLOW JOIN SYS_USER SU ON SU.USER_FLOW = RD.DOCTOR_FLOW WHERE RD.RECORD_STATUS = 'Y' AND SU.RECORD_STATUS = 'Y' AND RD.DOCTOR_TYPE_ID = 'Graduate' AND (
OR RD.work_org_id = #{org.sendSchoolId} OR RD.work_org_name = #{org.sendSchoolName}
) AND RDR.ORG_FLOW IS NOT NULL ) ) and ed.DOCTOR_TYPE_ID = 'Graduate' AND (
OR ED.work_org_id = #{org.sendSchoolId} OR ED.work_org_name = #{org.sendSchoolName}
)
and ed.GRADUATION_STATUS_ID in #{item}
and ed.SCHOOL = #{school}
and dr.SESSION_NUMBER = #{sessionNumber}
and dr.ORG_FLOW = #{orgFlow}
and dr.GRADUATION_YEAR=#{graduationYear}
and dr.AUDIT_STATUS_ID='Passed'
and u.USER_NAME like #{userName}
and u.ID_NO like #{idNo}
and dr.CAT_SPE_ID = #{trainingTypeId}
and dr.SPE_ID = #{trainingSpeId}
order by org.org_code desc

    		SELECT count(DOCTOR_FLOW)
			from (select DOCTOR_FLOW, count(DOCTOR_FLOW) dcount
			from RES_DOCTOR_RECRUIT
			where RECORD_STATUS = 'Y'


    				and RES_DOCTOR_RECRUIT.ORG_FLOW = #{recruit.orgFlow,jdbcType=VARCHAR} 


