			and RES_DOCTOR_RECRUIT.SESSION_NUMBER= #{year}
			AND RES_DOCTOR_RECRUIT.recruit_flag = 'Y'
			AND RES_DOCTOR_RECRUIT.confirm_Flag = 'Y'

				and RES_DOCTOR_RECRUIT.ORG_FLOW  in 
    		#{item}

			GROUP by DOCTOR_FLOW) a
			where dcount > 0

