select a.*,b.*,c.*,d.* from ( select nvl(count(distinct rdr.doctor_Flow),0) doctorCount from RES_DOCTOR_RECRUIT rdr left join res_doctor d on rdr.doctor_flow = d.doctor_flow where rdr.RECORD_STATUS = 'Y' AND d.RECORD_STATUS = 'Y' AND rdr.AUDIT_STATUS_ID = 'Passed' and rdr.CAT_SPE_ID in ('DoctorTrainingSpe','AssiGeneral')
AND rdr.SESSION_NUMBER = #{sessionNumber,jdbcType=VARCHAR}
AND rdr.PLACE_ID in #{item}
and rdr.ORG_FLOW = #{orgFlow,jdbcType=VARCHAR}
and rdr.JOINT_ORG_FLOW = #{jointOrgFlow,jdbcType=VARCHAR}
) a, ( select nvl(sum(decode(rdr.CAT_SPE_ID,'DoctorTrainingSpe', COUNT(DISTINCT d.doctor_flow),0)),0) "DoctorTrainingSpe", nvl(sum(decode(rdr.CAT_SPE_ID,'AssiGeneral', COUNT(DISTINCT d.doctor_flow),0)),0) "AssiGeneral" FROM RES_DOCTOR_RECRUIT rdr left join res_doctor d on rdr.doctor_flow = d.doctor_flow WHERE rdr.RECORD_STATUS = 'Y' AND d.record_status = 'Y' AND rdr.AUDIT_STATUS_ID = 'Passed'
and rdr.SESSION_NUMBER = #{sessionNumber,jdbcType=VARCHAR}
and rdr.PLACE_ID in #{item}
and rdr.ORG_FLOW = #{orgFlow,jdbcType=VARCHAR}
and rdr.JOINT_ORG_FLOW = #{jointOrgFlow,jdbcType=VARCHAR}
group by rdr.CAT_SPE_ID ) b, ( select nvl(sum(decode(d.doctor_type_id,'CompanyEntrust', COUNT(DISTINCT d.doctor_flow),0)),0) "CompanyEntrust", nvl(sum(decode(d.doctor_type_id,'Company', COUNT(DISTINCT d.doctor_flow),0)),0) "Company", nvl(sum(decode(d.doctor_type_id,'Graduate', COUNT(DISTINCT d.doctor_flow),0)),0) "Graduate", nvl(sum(decode(d.doctor_type_id,'Social', COUNT(DISTINCT d.doctor_flow),0)),0) "Social" FROM RES_DOCTOR_RECRUIT rdr left join res_doctor d on rdr.doctor_flow = d.doctor_flow WHERE rdr.RECORD_STATUS = 'Y' AND d.record_status = 'Y' AND rdr.AUDIT_STATUS_ID = 'Passed' and rdr.CAT_SPE_ID in ('DoctorTrainingSpe','AssiGeneral')
and rdr.SESSION_NUMBER = #{sessionNumber,jdbcType=VARCHAR}
and rdr.PLACE_ID in #{item}
and rdr.ORG_FLOW = #{orgFlow,jdbcType=VARCHAR}
and rdr.JOINT_ORG_FLOW = #{jointOrgFlow,jdbcType=VARCHAR}
group by d.doctor_type_id ) c, ( select nvl(sum(decode(o.org_level_id,'CountryOrg', COUNT(DISTINCT d.doctor_flow),0)),0) "CountryOrg", nvl(sum(decode(o.org_level_id,'JointOrg', COUNT(DISTINCT d.doctor_flow),0)),0) "JointOrg", nvl(sum(decode(o.org_level_id,'ProvinceOrg', COUNT(DISTINCT d.doctor_flow),0)),0) "ProvinceOrg" FROM RES_DOCTOR_RECRUIT rdr left join res_doctor d on rdr.doctor_flow = d.doctor_flow LEFT JOIN ( select case when org.org_level_id ='CountryOrg' then org.org_level_id when org.org_level_id ='ProvinceOrg' then org.org_level_id when 1=( select 1 from res_joint_org j where j.record_status='Y' and j.joint_org_flow=org.org_flow and rownum=1 ) then 'JointOrg' end org_level_id ,org.org_flow,org.record_status from sys_org org where org.RECORD_STATUS = 'Y' ) o ON d.org_flow = o.org_flow WHERE rdr.RECORD_STATUS = 'Y' AND d.record_status = 'Y' AND o.record_status = 'Y' AND rdr.AUDIT_STATUS_ID = 'Passed' and rdr.CAT_SPE_ID in ('DoctorTrainingSpe','AssiGeneral')
and rdr.SESSION_NUMBER = #{sessionNumber,jdbcType=VARCHAR}
and rdr.PLACE_ID in #{item}
and rdr.ORG_FLOW = #{orgFlow,jdbcType=VARCHAR}
and rdr.JOINT_ORG_FLOW = #{jointOrgFlow,jdbcType=VARCHAR}
group by o.org_level_id ) d

