select org_city_id, org_city_name, session_number, doctor_type_id, count(1) num from (SELECT org.org_city_id, org.org_city_name, t.session_number, rd.doctor_type_id, rd.doctor_type_name, rd.doctor_flow FROM RES_DOCTOR_recruit T left join sys_org org on t.org_flow = org.org_flow left join res_doctor rd on t.doctor_flow = rd.doctor_flow LEFT JOIN SYS_USER u ON t.DOCTOR_FLOW = u.USER_FLOW WHERE T.RECORD_STATUS = 'Y' and t.audit_status_id = 'Passed' AND t.org_flow IN (SELECT ORG_FLOW FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS = 'Y' AND ORG_PROV_ID = '320000') AND u.RECORD_STATUS = 'Y' and rd.doctor_type_id in ('Company', 'Graduate', 'Social','CompanyEntrust') and t.session_number is not null and t.session_number >'2014' and t.cat_spe_id='DoctorTrainingSpe' union select org.org_city_id, org.org_city_name, t.session_number, rd.doctor_type_id, rd.doctor_type_name, rd.doctor_flow from res_docotr_delay_teturn t left join sys_org org on t.org_flow = org.org_flow left join res_doctor rd on t.doctor_flow = rd.doctor_flow LEFT JOIN SYS_USER u ON t.DOCTOR_FLOW = u.USER_FLOW where t.record_status = 'Y' and t.type_id = 'ReturnTraining' AND T.AUDIT_STATUS_ID !='0' AND t.org_flow IN (SELECT ORG_FLOW FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS = 'Y' AND ORG_PROV_ID = '320000') and not exists( select 1 FROM RES_DOCTOR_recruit r where r.record_status='Y' and r.audit_status_id='Passed' and r.doctor_flow=t.doctor_flow and r.session_number=t.session_number ) and rd.doctor_type_id in ('Company', 'Graduate', 'Social','CompanyEntrust') and t.session_number is not null and t.session_number >'2014' and t.training_type_id='DoctorTrainingSpe' ) t
where session_number=#{sessionNumber}
