SELECT ORG.ORG_FLOW, org.org_city_id, T.SPE_ID, COUNT(1) NUM FROM jsres_recruit_info T left join (SELECT JIORG.JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and org.org_level_id = 'CountryOrg' UNION SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where org.org_level_id = 'CountryOrg') ORG on t.org_flow = org.JOINT_ORG_FLOW WHERE T.RECORD_STATUS = 'Y' AND ORG.org_flow IN (SELECT ORG_FLOW FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS = 'Y' AND ORG_PROV_ID = '320000')
and t.DOCTOR_TYPE_ID in #{item}
and t.session_number is not null
and t.session_number=#{sessionNumber}
and t.cat_spe_id=#{trainTypeId}
and t.spe_id=#{trainingSpeId}
and org.org_city_id=#{orgCityId}
group by ORG.ORG_FLOW, org.org_city_id, T.SPE_ID order by org.org_city_id, T.SPE_ID

