    				and RES_DOCTOR_RECRUIT.CAT_SPE_ID=#{recruit.catSpeId}


					and RES_DOCTOR_RECRUIT.JOINT_ORG_FLOW=#{recruit.jointOrgFlow}



				and RES_DOCTOR_RECRUIT.ORG_FLOW  in 
    		#{item}


				 and doc.doctor_type_id  in
				 #{item}

