select dr.*,u.USER_FLOW, u.USER_CODE, u.USER_NAME, u.SEX_ID, u.SEX_NAME, u.USER_PHONE, u.USER_EMAIL
, u.ID_NO, u.USER_BIRTHDAY, u.TITLE_ID, u.TITLE_NAME, u.DEGREE_ID, u.DEGREE_NAME, u.EDUCATION_ID, u.EDUCATION_NAME, u.POST_ID, u.POST_NAME,
ed.ORG_FLOW as edOrgFlow,ed.ORG_Name as edOrgName,ed.school as school
from pdsci.RES_DOCTOR_RECRUIT dr
left join  pdsci.SYS_USER u
on dr.DOCTOR_FLOW =  u.USER_FLOW
left join  RES_DOCTOR ed
on dr.DOCTOR_FLOW =  ed.DOCTOR_FLOW
where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y'AND ed.RECORD_STATUS = 'Y'
and EXISTS ( select 1 from sys_org org where (org.send_school_id=ed.work_org_id or
org.send_school_name=ed.work_org_name) and org.record_status='Y' and org.org_flow =#{currUser.orgFlow})

	and ed.DOCTOR_TYPE_ID in
	#{item}



		and dr.SESSION_NUMBER = #{resDoctorRecruit.sessionNumber}


		and dr.CAT_SPE_ID = #{resDoctorRecruit.catSpeId}


		and dr.SPE_ID = #{resDoctorRecruit.speId}


		and dr.ORG_FLOW = #{resDoctorRecruit.orgFlow}


		and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}


		and dr.AUDIT_STATUS_ID=#{resDoctorRecruit.auditStatusId}



	and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})




		and u.USER_NAME like #{userName}



		and u.ID_NO like #{idNo}




		and dr.org_Flow = #{doctor.orgFlow}


		and dr.CAT_SPE_ID = #{doctor.trainingTypeId}


		and dr.SPE_ID = #{doctor.trainingSpeId}


		and dr.SESSION_NUMBER = #{doctor.sessionNumber}


		and ed.IS_YEAR_GRADUATE = #{doctor.isYearGraduate}


		and dr.doctor_Status_Id = #{doctor.doctorStatusId}


		and ed.school = #{doctor.school}

	and dr.org_flow in(
	SELECT ORG_FLOW
	FROM PDSCI.SYS_ORG
	WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'


			AND ORG_PROV_ID = #{sysOrg.orgProvId}


			AND ORG_CITY_ID = #{sysOrg.orgCityId}


			AND ORG_FLOW = #{sysOrg.orgFlow}


			AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}


			AND ORG_TYPE_ID = #{sysOrg.orgTypeId}


	)
	AND ed.doctor_type_id = 'Graduate'

order by  NLSSORT(u.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M'),dr.org_flow, dr.doctor_flow,dr.CREATE_TIME desc

select score.SKILL_SCORE_FLOW score_flow,score.theory_score,score.skill_score,score.ext_score,u.USER_FLOW, u.USER_CODE, u.USER_NAME, u.SEX_ID, u.SEX_NAME, u.USER_PHONE, u.USER_EMAIL , u.ID_NO, u.USER_BIRTHDAY, u.TITLE_ID, u.TITLE_NAME, u.DEGREE_ID, u.DEGREE_NAME, u.EDUCATION_ID, u.EDUCATION_NAME, u.POST_ID, u.POST_NAME, ed.ORG_FLOW as edOrgFlow,ed.ORG_Name as edOrgName,dr.* from ( SELECT T.DOCTOR_FLOW,T.SCORE_PHASE_ID, A.THEROY_SCORE_FLOW, B.SKILL_SCORE_FLOW,A.THEORY_SCORE,B.SKILL_SCORE,B.EXT_SCORE FROM (SELECT DISTINCT S.DOCTOR_FLOW,S.SCORE_PHASE_ID FROM RES_SCORE S WHERE S.RECORD_STATUS='Y' GROUP BY S.DOCTOR_FLOW,S.SCORE_PHASE_ID) T LEFT JOIN (SELECT T.SCORE_FLOW THEROY_SCORE_FLOW, T.DOCTOR_FLOW, T.THEORY_SCORE, T.SCORE_PHASE_ID FROM RES_SCORE T WHERE T.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID IN ('TheoryScore')) A ON T.DOCTOR_FLOW = A.DOCTOR_FLOW AND T.SCORE_PHASE_ID = A.SCORE_PHASE_ID LEFT JOIN (SELECT T.SCORE_FLOW SKILL_SCORE_FLOW, T.DOCTOR_FLOW, T.SKILL_SCORE, T.EXT_SCORE, T.SCORE_PHASE_ID FROM RES_SCORE T WHERE T.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID IN ('SkillScore')) B ON T.DOCTOR_FLOW = B.DOCTOR_FLOW AND T.SCORE_PHASE_ID = B.SCORE_PHASE_ID ) score left join pdsci.SYS_USER u on score.DOCTOR_FLOW = u.USER_FLOW left join RES_DOCTOR ed on score.DOCTOR_FLOW = ed.DOCTOR_FLOW left join ( select a.* from RES_DOCTOR_RECRUIT a, ( select t.doctor_flow,max(t.create_time) as create_time from RES_DOCTOR_RECRUIT t where t.record_status='Y' and t.AUDIT_STATUS_ID='Passed' group by t.doctor_flow) b where a.doctor_flow=b.doctor_flow and a.create_time=b.create_time ) dr on score.DOCTOR_FLOW = dr.DOCTOR_FLOW left join sys_org org on org.org_flow=ed.org_flow where u.RECORD_STATUS= 'Y' and dr.RECORD_STATUS= 'Y' and EXISTS ( SELECT null FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'
AND ORG_FLOW = #{orgFlow}
AND ORG_FLOW IN ( SELECT DISTINCT RDR.ORG_FLOW FROM RES_DOCTOR_RECRUIT RDR LEFT JOIN RES_DOCTOR RD ON RDR.DOCTOR_FLOW=RD.DOCTOR_FLOW JOIN SYS_USER SU ON SU.USER_FLOW = RD.DOCTOR_FLOW WHERE RD.RECORD_STATUS = 'Y' AND SU.RECORD_STATUS = 'Y' AND RD.DOCTOR_TYPE_ID = 'Graduate' AND (
OR ed.work_org_id = #{org.sendSchoolId} OR ed.work_org_name = #{org.sendSchoolName}
) AND RDR.ORG_FLOW IS NOT NULL ) and ed.org_flow=org_flow )
and dr.GRADUATION_YEAR=#{graduationYear}
and score.SCORE_PHASE_ID = #{scoreYear}
and score.theory_score = #{isHege}
and score.skill_score = '1'
and score.skill_score = '0'
and u.USER_NAME like #{userName}
and u.ID_NO like #{idNo}
and dr.TRAIN_YEAR not in (#{trainYear})
and dr.CAT_SPE_ID = #{trainingTypeId}
and dr.SPE_ID = #{trainingSpeId}
and dr.TRAIN_YEAR = #{trainingYears}
and ed.SESSION_NUMBER = #{sessionNumber}
and ed.SCHOOL = #{school}
and ed.DOCTOR_TYPE_ID = 'Graduate' order by org.org_code desc

