from pdsci.sch_arrange_result
where record_status = 'Y'
and doctor_flow = #{doctorFlow}
and rotation_flow = #{rotationFlow}

select rdr.session_number, rdr.org_name, rdr.spe_Name, rdr.train_Year, rdr.recruit_date, so.org_level_id, rjo.joint_flow, su.user_name, su.sex_name, su.cret_type_name, su.id_no, su.nation_name, su.user_birthday, su.user_phone, su.user_email, su.education_id, rd.doctor_type_id, rd.work_org_name, rd.doctor_type_name, rd.is_year_graduate, rd.certificate_no, rd.degree_no, pur.user_resume, nvl(rpsc.cfg_pass_score,'60') cfg_pass_score, a.score_phase_id theory_year, a.theory_score, b.score_phase_id skill_year, b.skill_score, b.ext_score from res_doctor_recruit rdr left join sys_org so on rdr.org_flow = so.org_flow left join res_joint_org rjo on so.org_flow = rjo.joint_org_flow and rjo.record_status='Y' left join sys_user su on rdr.doctor_flow = su.user_flow left join res_doctor rd on su.user_flow = rd.doctor_flow left join pub_user_resume pur on su.user_flow = pur.user_flow left join res_pass_score_cfg rpsc on rpsc.cfg_year = rdr.graduation_year and rpsc.record_status = 'Y' left join (select doctor_flow, theory_score, score_phase_id from (select doctor_flow,theory_score,score_phase_id,row_number() over(partition by doctor_flow order by score_phase_id desc) rn from res_score where record_status = 'Y' and score_type_id = 'TheoryScore') where rn = 1) a on rdr.doctor_flow = a.doctor_flow left join (select doctor_flow, skill_score, ext_score, score_phase_id from (select doctor_flow,skill_score,ext_score,score_phase_id,row_number() over(partition by doctor_flow order by score_phase_id desc) rn from res_score where record_status = 'Y' and score_type_id = 'SkillScore') where rn = 1) b on rdr.doctor_flow = b.doctor_flow where rdr.record_status = 'Y' and su.record_status = 'Y' and rdr.audit_status_id = 'Passed' and ( rdr.graduation_year = '2017' or ( rdr.session_number != '2017' and rdr.graduation_year != '2017' and rdr.doctor_flow in (select doctor_flow from res_score where record_status = 'Y' and score_type_id in ('TheoryScore','SkillScore') ) ) ) and exists (select null from sys_org so where so.record_status = 'Y'
and so.org_prov_id = #{orgProvId}
and so.org_flow = rdr.org_flow)

select *
from pdsci.RES_DOCTOR_RECRUIT dr
left join  pdsci.SYS_USER u
on dr.DOCTOR_FLOW =  u.USER_FLOW
left join  RES_DOCTOR ed
on dr.DOCTOR_FLOW =  ed.DOCTOR_FLOW
left join pdsci.PUB_USER_RESUME pur
on pur.USER_FLOW=dr.DOCTOR_FLOW
where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y'
and dr.org_flow in(
SELECT ORG_FLOW
FROM PDSCI.SYS_ORG
WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'


		AND ORG_PROV_ID = #{sysOrg.orgProvId}


		AND ORG_CITY_ID = #{sysOrg.orgCityId}


		AND ORG_FLOW = #{sysOrg.orgFlow}


		AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}


		AND ORG_TYPE_ID = #{sysOrg.orgTypeId}



	and dr.ORG_FLOW in
	#{item}


	and ed.DOCTOR_TYPE_ID in
	#{item}


	and dr.TRAIN_YEAR in
	#{item}

)

	and dr.SESSION_NUMBER in
	#{item}



		and dr.IS_RETRAIN = #{resDoctorRecruit.isRetrain}


		and dr.SESSION_NUMBER = #{resDoctorRecruit.sessionNumber}


		and dr.CAT_SPE_ID = #{resDoctorRecruit.catSpeId}


		and dr.SPE_ID = #{resDoctorRecruit.speId}


		and dr.ORG_FLOW = #{resDoctorRecruit.orgFlow}


		and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}


		and dr.doctor_STATUS_ID=#{resDoctorRecruit.doctorStatusId}



	and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})




		and u.USER_NAME like #{userName}



		and u.ID_NO like #{idNo}


		and u.NATIVE_PLACE_CITY_ID = #{user.nativePlaceCityId}




		and dr.CAT_SPE_ID = #{doctor.trainingTypeId}


		and dr.SPE_ID = #{doctor.trainingSpeId}


		and dr.SESSION_NUMBER = #{doctor.sessionNumber}


		and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId}


		and ed.WORK_ORG_LEVEL_ID = #{doctor.workOrgLevelId}


		and ed.IS_YEAR_GRADUATE = #{doctor.isYearGraduate}


		and dr.doctor_Status_Id = #{doctor.doctorStatusId}


		and (

			OR ed.work_org_id = #{doctor.workOrgId}
			OR ed.work_org_name like CONCAT(CONCAT('%', #{doctor.workOrgName}),'%')

		)


order by  dr.session_number desc,dr.org_flow,dr.spe_id,NLSSORT(u.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M'), dr.doctor_flow,dr.CREATE_TIME desc

select *
from pdsci.RES_DOCTOR_RECRUIT dr
left join  pdsci.SYS_USER u
on dr.DOCTOR_FLOW =  u.USER_FLOW
left join  RES_DOCTOR ed
on dr.DOCTOR_FLOW =  ed.DOCTOR_FLOW
left join pdsci.PUB_USER_RESUME pur
on pur.USER_FLOW=dr.DOCTOR_FLOW
where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y'
AND dr.signup_way = 'DoctorSignup'
AND dr.AUDIT_STATUS_ID != 'NotSubmit'
and dr.org_flow in(
SELECT ORG_FLOW
FROM PDSCI.SYS_ORG
WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'


		AND ORG_PROV_ID = #{sysOrg.orgProvId}


		AND ORG_CITY_ID = #{sysOrg.orgCityId}


		AND ORG_FLOW = #{sysOrg.orgFlow}


		AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}


		AND ORG_TYPE_ID = #{sysOrg.orgTypeId}



	and dr.ORG_FLOW in
	#{item}


	and dr.JOINT_ORG_FLOW in
	#{item}


	and ed.DOCTOR_TYPE_ID in
	#{item}


	and dr.TRAIN_YEAR in
	#{item}

)

	and dr.SESSION_NUMBER in
	#{item}



		and dr.SESSION_NUMBER = #{resDoctorRecruit.sessionNumber}


		and dr.CAT_SPE_ID = #{resDoctorRecruit.catSpeId}


		and dr.SPE_ID = #{resDoctorRecruit.speId}





		and dr.GRADUATION_YEAR=#{resDoctorRecruit.graduationYear}






			and dr.DOCTOR_STATUS_ID != 'Auditing' and dr.DOCTOR_STATUS_ID != 'NotPassed'
			and dr.DOCTOR_STATUS_ID != 'OrgAuditing' AND DR.AUDIT_STATUS_ID = 'Passed' and dr.DOCTOR_STATUS_ID is not null



			and dr.AUDIT_STATUS_ID ='Auditing'


			and dr.AUDIT_STATUS_ID ='Auditing' AND DR.JOINT_ORG_AUDIT = 'Auditing'



			and dr.AUDIT_STATUS_ID = 'NotPassed'




			and dr.DOCTOR_STATUS_ID != 'Auditing' and dr.DOCTOR_STATUS_ID != 'NotPassed'
			and dr.DOCTOR_STATUS_ID != 'OrgAuditing' AND DR.AUDIT_STATUS_ID = 'Passed' and dr.DOCTOR_STATUS_ID is not null



			and dr.AUDIT_STATUS_ID ='Auditing'


			and dr.AUDIT_STATUS_ID ='Auditing' AND DR.JOINT_ORG_AUDIT = 'Auditing'



			and dr.AUDIT_STATUS_ID = 'NotPassed'




	and dr.TRAIN_YEAR not in (#{resDoctorRecruit.trainYear})




		and u.USER_NAME like #{userName}



		and u.ID_NO like #{idNo}


		and u.NATIVE_PLACE_CITY_ID = #{user.nativePlaceCityId}




		and dr.CAT_SPE_ID = #{doctor.trainingTypeId}


		and dr.SPE_ID = #{doctor.trainingSpeId}


		and dr.SESSION_NUMBER = #{doctor.sessionNumber}


		and ed.DOCTOR_TYPE_ID = #{doctor.doctorTypeId}


		and ed.WORK_ORG_LEVEL_ID = #{doctor.workOrgLevelId}


		and ed.IS_YEAR_GRADUATE = #{doctor.isYearGraduate}


		and dr.doctor_Status_Id = #{doctor.doctorStatusId}


		and (

			OR ed.work_org_id = #{doctor.workOrgId}
			OR ed.work_org_name like CONCAT(CONCAT('%', #{doctor.workOrgName}),'%')

		)


order by  dr.session_number desc,dr.org_flow,dr.spe_id,NLSSORT(u.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M'), dr.doctor_flow,dr.CREATE_TIME desc

select score.SKILL_SCORE_FLOW score_flow,score.theory_score,score.THEROY_SCORE_PHASE_ID AS SCORE_PHASE_ID,score.skill_score,score.SKILL_SCORE_PHASE_ID AS SCORE_PHASE_NAME,score.ext_score,u.USER_FLOW, u.USER_CODE, u.USER_NAME, u.SEX_ID, u.SEX_NAME, u.USER_PHONE, u.USER_EMAIL , u.ID_NO, u.USER_BIRTHDAY, u.TITLE_ID, u.TITLE_NAME, u.DEGREE_ID, u.DEGREE_NAME, u.EDUCATION_ID, u.EDUCATION_NAME, u.POST_ID, u.POST_NAME, ed.ORG_FLOW as edOrgFlow,ed.ORG_Name as edOrgName,dr.* from ( SELECT case when A.DOCTOR_FLOW is not null then a.doctor_flow when b.doctor_flow is not null then b.doctor_flow else null end doctor_flow, A.THEROY_SCORE_FLOW, B.SKILL_SCORE_FLOW,A.THEROY_SCORE_PHASE_ID, A.THEORY_SCORE, B.SKILL_SCORE_PHASE_ID, B.SKILL_SCORE, B.EXT_SCORE FROM ( select R.SCORE_FLOW AS THEROY_SCORE_FLOW,a.DOCTOR_FLOW,R.THEORY_SCORE,a.THEROY_SCORE_PHASE_ID from (SELECT T.DOCTOR_FLOW,max(T.SCORE_PHASE_ID) AS THEROY_SCORE_PHASE_ID FROM RES_SCORE T WHERE T.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID IN ('TheoryScore')
AND T.SCORE_PHASE_ID = #{theroyScoreYear}
group by T.DOCTOR_FLOW) a left join RES_SCORE R on a.DOCTOR_FLOW=R.DOCTOR_FLOW where a.THEROY_SCORE_PHASE_ID=R.SCORE_PHASE_ID AND R.SCORE_TYPE_ID IN ('TheoryScore') and R.RECORD_STATUS = 'Y'
AND R.SCORE_PHASE_ID = #{theroyScoreYear}
) A FULL JOIN ( select R.SCORE_FLOW AS SKILL_SCORE_FLOW,a.DOCTOR_FLOW,R.SKILL_SCORE,R.EXT_SCORE,a.SKILL_SCORE_PHASE_ID from (SELECT T.DOCTOR_FLOW,max(T.SCORE_PHASE_ID) AS SKILL_SCORE_PHASE_ID FROM RES_SCORE T WHERE T.RECORD_STATUS = 'Y' AND T.SCORE_TYPE_ID IN ('SkillScore')
AND T.SCORE_PHASE_ID = #{skillScoreYear}
group by T.DOCTOR_FLOW) a left join RES_SCORE R on a.DOCTOR_FLOW=R.DOCTOR_FLOW where a.SKILL_SCORE_PHASE_ID=R.SCORE_PHASE_ID AND R.SCORE_TYPE_ID IN ('SkillScore') and R.RECORD_STATUS = 'Y'
AND R.SCORE_PHASE_ID = #{skillScoreYear}
) B ON A.DOCTOR_FLOW = B.DOCTOR_FLOW ) score left join pdsci.SYS_USER u on score.DOCTOR_FLOW = u.USER_FLOW left join RES_DOCTOR ed on score.DOCTOR_FLOW = ed.DOCTOR_FLOW left join ( select a.* from RES_DOCTOR_RECRUIT a, ( select t.doctor_flow,max(t.create_time) as create_time from RES_DOCTOR_RECRUIT t where t.record_status='Y' and t.AUDIT_STATUS_ID='Passed' group by t.doctor_flow) b where a.doctor_flow=b.doctor_flow and a.create_time=b.create_time ) dr on score.DOCTOR_FLOW = dr.DOCTOR_FLOW left join sys_org org on org.org_flow=ed.org_flow where u.RECORD_STATUS= 'Y' and dr.RECORD_STATUS= 'Y' and EXISTS ( SELECT null FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'
AND ORG_FLOW = #{orgFlow}
AND ORG_FLOW IN ( SELECT DISTINCT RDR.ORG_FLOW FROM RES_DOCTOR_RECRUIT RDR LEFT JOIN RES_DOCTOR RD ON RDR.DOCTOR_FLOW=RD.DOCTOR_FLOW JOIN SYS_USER SU ON SU.USER_FLOW = RD.DOCTOR_FLOW WHERE RD.RECORD_STATUS = 'Y' AND SU.RECORD_STATUS = 'Y' AND RD.DOCTOR_TYPE_ID = 'Graduate'
and RD.SCHOOL =#{school}
AND (
OR ed.work_org_id = #{org.sendSchoolId} OR ed.work_org_name = #{org.sendSchoolName}
) AND RDR.ORG_FLOW IS NOT NULL ) and ed.org_flow=org_flow )
and dr.GRADUATION_YEAR=#{graduationYear}
and score.SCORE_PHASE_ID = #{scoreYear}
and score.theory_score = #{isHege}
and score.skill_score = '1'
and score.skill_score = '0'
and u.USER_NAME like #{userName}
and u.ID_NO like #{idNo}
and dr.TRAIN_YEAR not in (#{trainYear})
and dr.CAT_SPE_ID = #{trainingTypeId}
and dr.SPE_ID = #{trainingSpeId}
and dr.TRAIN_YEAR = #{trainingYears}
and ed.SESSION_NUMBER = #{sessionNumber}
And ((nvl(score.THEROY_SCORE_PHASE_ID,0)-nvl(score.SKILL_SCORE_PHASE_ID,0)) > -5 and (5 > nvl(score.THEROY_SCORE_PHASE_ID,0)-nvl(score.SKILL_SCORE_PHASE_ID,0)))
and ed.DOCTOR_TYPE_ID = 'Graduate' order by org.org_code desc

SELECT DR.AUDITION_STATUS_ID, DR.AUDITION_STATUS_NAME, DR.OPER_STATUS_ID, DR.OPER_STATUS_NAME, DR.IN_JOINT_ORG_TRAIN, DR.JOINT_ORG_FLOW, DR.JOINT_ORG_NAME, DR.ROTATION_FLOW, DR.ROTATION_NAME, DR.SPECIAL_CERT_NO, DR.SPECIAL_FILE_URL, DR.AVG_COMPLETE, DR.AVG_AUDIT, DR.MATERIAL_ID, DR.PRACTICING_SCOPE_ID, DR.PRACTICING_SCOPE_NAME, DR.CERTIFICATE_URI, DR.QUALIFICATION_MATERIAL_URI, DR.SPECIAL_CERTIFICATION_URI, DR.QUALIFICATION_MATERIAL_CODE, DR.TYPE_ID, DR.REGISTE_MANUA, DR.RECRUIT_FLOW, DR.DOCTOR_FLOW, DR.ORG_FLOW, DR.SPE_ID, DR.SPE_NAME, DR.SWAP_FLAG, DR.RECRUIT_TYPE_ID, DR.RECRUIT_TYPE_NAME, DR.RECRUIT_YEAR, DR.RECRUIT_DATE, DR.RETEST_FLAG, DR.EXAM_RESULT, DR.AUDITION_RESULT, DR.OPER_RESULT, DR.TOTLE_RESULT, DR.ADMIT_FLAG, DR.RECRUIT_FLAG, DR.CONFIRM_FLAG, DR.SWAP_SPE_ID, DR.SWAP_SPE_NAME, DR.RECORD_STATUS, DR.CREATE_TIME, DR.CREATE_USER_FLOW, DR.MODIFY_TIME, DR.MODIFY_USER_FLOW, DR.RETEST_NOTICE, DR.ADMIT_NOTICE, DR.GLOBAL_NOTICE, DR.CAT_SPE_ID, DR.CAT_SPE_NAME, DR.SESSION_NUMBER, DR.TRAIN_YEAR, DR.YET_TRAIN_YEAR, DR.DOCTOR_STATUS_ID, DR.DOCTOR_STATUS_NAME, DR.DOCTOR_STRIKE_ID, DR.DOCTOR_STRIKE_NAME, DR.TRAIN_NO, DR.AUDIT_STATUS_ID, DR.AUDIT_STATUS_NAME, DR.PLACE_ID, DR.PLACE_NAME, DR.PROVE_FILE_URL, DR.COMPLETE_CERT_NO, DR.COMPLETE_FILE_URL, DR.CURR_DEGREE_CATEGORY_ID, DR.CURR_DEGREE_CATEGORY_NAME, DR.PROV_REMARK, DR.GRADUATION_YEAR, DR.ADMIN_SWAP_FLAG, DR.SECOND_SPE_ID, DR.SECOND_SPE_NAME, DR.GRADUATION_CERTIFICATE_NO, DR.CERTIFICATE_DATE, DR.GRADUATION_CERTIFICATE_TYPE, DR.CHANGE_SPE_ID, DR.CHANGE_SPE_NAME, DR.RETURN_BACK_FLAG, DR.RETURN_BACK_REMARK, DR.IS_SEND, DR.IS_CERT_SEND, DR.QUALIFIED_ID, DR.CERTIFICATE_ISSUING_STATUS, DR.WORK_UNIT, DR.WORK_UNIT_CODE, DR.ARMY_HOSPITAL, DR.EXAM_STATUS_ID, DR.EXAM_STATUS_NAME, DR.ORG_NAME, DR.IS_RETRAIN, DR.ORG_AUDIT, DR.JOINT_ORG_AUDIT, DR.SIGNUP_WAY, U.USER_FLOW, U.USER_CODE, U.USER_NAME, U.SEX_ID, U.SEX_NAME, U.USER_PHONE, U.USER_EMAIL, U.ID_NO, U.USER_BIRTHDAY, U.TITLE_ID, U.TITLE_NAME, U.DEGREE_ID, U.DEGREE_NAME, U.EDUCATION_ID, U.EDUCATION_NAME, U.POST_ID, U.POST_NAME, ED.ORG_FLOW AS EDORGFLOW, ED.ORG_NAME AS EDORGNAME, ED.DOCTOR_TYPE_NAME, ED.DOCTOR_TYPE_ID FROM RES_DOCTOR_RECRUIT DR LEFT JOIN PDSCI.SYS_USER U ON DR.DOCTOR_FLOW = U .USER_FLOW LEFT JOIN RES_DOCTOR ED ON DR.DOCTOR_FLOW = ED.DOCTOR_FLOW WHERE DR.RECORD_STATUS = 'Y' AND U .RECORD_STATUS = 'Y' AND NOT EXISTS ( SELECT 1 FROM RES_DOCOTR_DELAY_TETURN T WHERE T.RECORD_STATUS = 'Y' AND T.TYPE_ID = 'ReturnTraining' AND T.AUDIT_STATUS_ID = '1' AND T.RECRUIT_FLOW = DR.RECRUIT_FLOW AND T.SESSION_NUMBER = DR.SESSION_NUMBER AND T.DOCTOR_FLOW = DR.DOCTOR_FLOW ) AND DR.AUDIT_STATUS_ID != 'NotSubmit' AND DR.ORG_FLOW IN ( SELECT ORG_FLOW FROM SYS_ORG WHERE RECORD_STATUS = 'Y'
AND ORG_PROV_ID = #{sysOrg.orgProvId}
AND ORG_CITY_ID = #{sysOrg.orgCityId}
AND ORG_LEVEL_ID = #{sysOrg.orgLevelId}
AND ORG_TYPE_ID = #{sysOrg.orgTypeId}
)
and dr.ORG_FLOW = #{orgFlow}
and ed.DOCTOR_TYPE_ID in #{item}
and dr.SESSION_NUMBER in #{item}
and dr.signup_way = #{doctorSignupFlag}
and dr.audit_status_id = 'WaitGlobalPass'
and dr.CAT_SPE_ID = #{trainingTypeId}
and dr.SPE_ID = #{trainingSpeId}
and u.USER_NAME like #{userName}
and u.ID_NO like #{idNo}
order by to_number(decode(dr.AUDITION_RESULT,null,'0',dr.audition_result)) desc
order by to_number(decode(dr.OPER_RESULT,null,'0',dr.OPER_RESULT)) desc
order by to_number(decode(dr.AUDITION_RESULT,null,'0',dr.AUDITION_RESULT))+ to_number(decode(dr.OPER_RESULT,null,'0',dr.OPER_RESULT)) desc
order by dr.session_number desc,dr.org_flow,dr.spe_id,NLSSORT(u.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M'), dr.doctor_flow,dr.CREATE_TIME desc

SELECT su.USER_NAME AS user_name, rdr.doctor_flow,su.user_phone,su.id_no,rdr.session_number,rdr.spe_id ,rdr.spe_name,dr.doctor_type_id,dr.doctor_type_name,NVL(COMP.COMPLETE_NUM,0) AS COMPLETE_NUM ,NVL((CASE WHEN rdr.cat_spe_id = 'DoctorTrainingSpe' AND rdr.session_number >= '2015' AND (rdr.train_year = 'OneYear' OR rdr.train_year = 'TwoYear') AND (SELECT COUNT(1) FROM sch_doctor_dept sdd WHERE sdd.record_status = 'Y' AND sdd.doctor_flow = rdr.doctor_flow AND sdd.rotation_flow = rdr.rotation_flow AND sdd.org_flow = rdr.org_flow) > 0 THEN SUM(NVL(ROUND((CASE WHEN NVL(SRD.SCH_MONTH, 0) = 0 THEN 0 ELSE NVL(SDD.SCH_MONTH, 0) / SRD.SCH_MONTH * REQ.REQ_NUM END), 0), 0)) ELSE SUM(REQ.REQ_NUM) END), 0) AS REQ_NUM FROM RES_DOCTOR_RECRUIT rdr INNER JOIN SYS_USER su ON rdr.DOCTOR_FLOW = su.USER_FLOW LEFT JOIN RES_DOCTOR dr ON dr.DOCTOR_FLOW = rdr.DOCTOR_FLOW LEFT JOIN SCH_ROTATION_DEPT SRD ON RDR.ROTATION_FLOW = SRD.ROTATION_FLOW AND SRD.ORG_FLOW IS NULL AND SRD.RECORD_STATUS = 'Y' LEFT JOIN SCH_ROTATION_DEPT_REQ REQ ON REQ.REL_RECORD_FLOW = SRD.RECORD_FLOW AND REQ.RECORD_STATUS = 'Y' AND REQ.REC_TYPE_ID IN ('CaseRegistry', 'DiseaseRegistry', 'SkillRegistry', 'OperationRegistry', 'CampaignRegistry') LEFT JOIN SCH_DOCTOR_DEPT SDD ON SDD.GROUP_FLOW = SRD.GROUP_FLOW AND SDD.STANDARD_DEPT_ID = SRD.STANDARD_DEPT_ID AND SDD.ORG_FLOW = RDR.ORG_FLOW AND SDD.DOCTOR_FLOW = RDR.DOCTOR_FLOW AND SDD.RECORD_STATUS = 'Y' LEFT JOIN (SELECT COUNT(1) COMPLETE_NUM, RR.OPER_USER_FLOW FROM RES_REC rr LEFT JOIN RES_DOCTOR_RECRUIT RDR ON RR.OPER_USER_FLOW = RDR.DOCTOR_FLOW INNER JOIN SCH_ROTATION_DEPT srd ON RDR.ROTATION_FLOW = srd.ROTATION_FLOW AND SRD.ORG_FLOW IS NULL and SRD.RECORD_STATUS = 'Y' AND rr.SCH_ROTATION_DEPT_FLOW = srd.RECORD_FLOW WHERE rr.RECORD_STATUS = 'Y' AND srd.RECORD_STATUS = 'Y' AND RDR.RECORD_STATUS = 'Y' and RR.REC_TYPE_ID in('CaseRegistry','DiseaseRegistry','SkillRegistry','OperationRegistry','CampaignRegistry') GROUP BY RR.OPER_USER_FLOW) COMP ON COMP.OPER_USER_FLOW = RDR.DOCTOR_FLOW WHERE dr.RECORD_STATUS = 'Y' AND su.RECORD_STATUS = 'Y' AND rdr.record_status = 'Y' AND rdr.AUDIT_STATUS_ID = 'Passed' and RDR.DOCTOR_STATUS_ID = '20' and rdr.org_flow in ( SELECT ORG_FLOW FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'
AND ORG_PROV_ID = #{searchOrg.orgProvId}
AND ORG_CITY_ID = #{searchOrg.orgCityId}
AND ORG_TYPE_ID = #{searchOrg.orgTypeId}
)
AND dr.DOCTOR_TYPE_ID in #{item}
AND rdr.org_flow = #{orgFlow}
AND su.id_no = #{idNo}
and su.USER_NAME like #{userName}
AND rdr.spe_id = #{speId}
AND rdr.session_number = #{sessionNumber}
group by su.USER_NAME ,rdr.doctor_flow,su.user_phone,su.id_no,rdr.session_number,rdr.spe_id ,rdr.spe_name,dr.doctor_type_id,dr.doctor_type_name ,rdr.cat_spe_id,rdr.train_year ,RDR.ROTATION_FLOW,RDR.ORG_FLOW,COMP.COMPLETE_NUM ORDER BY RDR.SESSION_NUMBER DESC, RDR.SPE_ID

SELECT * from DOCTOR_DATA
where org_flow in (
SELECT ORG_FLOW
FROM PDSCI.SYS_ORG
WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'


		AND ORG_PROV_ID = #{searchOrg.orgProvId}


		AND ORG_CITY_ID = #{searchOrg.orgCityId}


		AND ORG_TYPE_ID = #{searchOrg.orgTypeId}


)

	AND DOCTOR_TYPE_ID in
	#{item}


	AND org_flow = #{orgFlow}


	AND id_no = #{idNo}



	and USER_NAME like #{userName}


	AND spe_id = #{speId}


	AND session_number = #{sessionNumber}

ORDER BY SESSION_NUMBER DESC

SELECT d.*, RD.GRADUATION_YEAR AS GRADUATION_YEAR from DOCTOR_DATA D
LEFT JOIN RES_DOCTOR R ON D.DOCTOR_FLOW=R.DOCTOR_FLOW
LEFT JOIN RES_DOCTOR_RECRUIT RD ON D.DOCTOR_FLOW=RD.DOCTOR_FLOW
where D.org_flow in (
SELECT ORG_FLOW
FROM PDSCI.SYS_ORG
WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'


		AND ORG_PROV_ID = #{searchOrg.orgProvId}


		AND ORG_CITY_ID = #{searchOrg.orgCityId}


		AND ORG_TYPE_ID = #{searchOrg.orgTypeId}


)

	AND D. DOCTOR_TYPE_ID in
	#{item}


	AND D. org_flow = #{orgFlow}


	AND D. id_no = #{idNo}



	and D. USER_NAME like #{userName}


	AND D. spe_id = #{speId}


	AND D. session_number = #{sessionNumber}


	AND R.TRAINING_TYPE_ID = #{trainingTypeId}


	AND RD.GRADUATION_YEAR = #{graduationYear}


	AND (D.COMPLETE_NUM/(D.REQ_NUM + 0.01)) >= #{fillInPercentLow}


	AND (CASE WHEN ((D.COMPLETE_NUM/(D.REQ_NUM + 0.01)) > 1) THEN 1 ELSE (D.COMPLETE_NUM/(D.REQ_NUM + 0.01)) END)  <=  #{fillInPercentHigh}

ORDER BY D. SESSION_NUMBER DESC

SELECT su.user_flow,su.user_name,su.org_flow,su.org_name,su.dept_flow,su.dept_name FROM PDSCI.SYS_USER su WHERE RECORD_STATUS = 'Y' AND NOT EXISTS (SELECT NULL FROM res_doctor rd WHERE su.user_flow = rd.doctor_flow AND rd.RECORD_STATUS = 'Y') AND EXISTS (SELECT NULL FROM SYS_USER_ROLE sur WHERE sur.RECORD_STATUS = 'Y' AND su.USER_FLOW = sur.USER_FLOW AND sur.ROLE_FLOW = #{roleFlow}) AND su.org_flow in ( SELECT ORG_FLOW FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'
AND ORG_PROV_ID = #{searchOrg.orgProvId}
AND ORG_CITY_ID = #{searchOrg.orgCityId}
AND ORG_TYPE_ID = #{searchOrg.orgTypeId}
)
AND su.org_flow = #{orgFlow}
AND su.dept_flow = #{deptFlow}
and su.USER_NAME like #{teacherName}
ORDER BY SU.DEPT_FLOW

SELECT
dr.org_flow,
dr.spe_id speId, count(1) trainingCount FROM RES_DOCTOR_RECRUIT DR LEFT JOIN PDSCI.SYS_USER U ON DR.DOCTOR_FLOW = U.USER_FLOW LEFT JOIN RES_DOCTOR ED ON DR.DOCTOR_FLOW = ED.DOCTOR_FLOW WHERE DR.RECORD_STATUS = 'Y' AND U.RECORD_STATUS = 'Y' AND NOT EXISTS (SELECT 1 FROM RES_DOCOTR_DELAY_TETURN T WHERE T.RECORD_STATUS = 'Y' AND T.TYPE_ID = 'ReturnTraining' AND T.AUDIT_STATUS_ID = '1' AND T.RECRUIT_FLOW = DR.RECRUIT_FLOW AND T.SESSION_NUMBER = DR.SESSION_NUMBER AND T.DOCTOR_FLOW = DR.DOCTOR_FLOW) AND DR.AUDIT_STATUS_ID != 'NotSubmit' AND EXISTS (SELECT 1 FROM SYS_ORG O WHERE O.RECORD_STATUS = 'Y' AND O.ORG_FLOW = DR.ORG_FLOW AND ORG_PROV_ID = '320000' AND ORG_TYPE_ID = 'Hospital') AND dr.AUDIT_STATUS_ID = 'Passed'
AND dr.doctor_Status_Id = #{doctorStatusId}
AND dr.TRAIN_YEAR IN
#{trainYear}
AND ed.DOCTOR_TYPE_ID in
#{docType}
and dr.ORG_FLOW in
#{orgFlow}
and dr.spe_id in
#{speId}
group by
dr.org_flow,
dr.spe_id
