select ORG_FLOW, org_city_id, SPE_ID,SESSION_NUMBER, CAT_SPE_ID,COUNT(1) NUM from (SELECT ORG.ORG_FLOW, org.org_city_id, T.SPE_ID, T.SESSION_NUMBER,T.CAT_SPE_ID, rd.doctor_flow FROM jsres_recruit_doc_info T left join (
SELECT JIORG.JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and org.org_level_id = 'CountryOrg' UNION
SELECT ORG.ORG_FLOW JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where org.org_level_id = 'CountryOrg') ORG on t.org_flow = org.JOINT_ORG_FLOW left join res_doctor rd on t.doctor_flow = rd.doctor_flow WHERE T.RECORD_STATUS = 'Y' AND ORG.org_flow IN (SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y' AND ORG_PROV_ID = '320000')
and rd.DOCTOR_TYPE_ID in #{item}
and t.session_number is not null
and t.session_number IN #{item}
and t.train_Year in #{item}
) t
where org_city_id=#{orgCityId}
