select JOINT_ORG_FLOW,ORG_FLOW, org_city_id, SPE_ID,SESSION_NUMBER, CAT_SPE_ID, COUNT(1) NUM from (SELECT ORG.JOINT_ORG_FLOW,ORG.ORG_FLOW, org.org_city_id, T.SPE_ID, T.SESSION_NUMBER,T.CAT_SPE_ID,rd.doctor_flow FROM jsres_recruit_doc_info T left join (SELECT JIORG.JOINT_ORG_FLOW, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and org.org_level_id = 'CountryOrg'
and org.org_flow=#{orgFlow}
) ORG on t.org_flow = org.JOINT_ORG_FLOW left join res_doctor rd on t.doctor_flow = rd.doctor_flow WHERE T.RECORD_STATUS = 'Y' AND ORG.JOINT_ORG_FLOW IN (SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y' AND ORG_PROV_ID = '320000')
