where org_city_id=#{orgCityId}
group by JOINT_ORG_FLOW,ORG_FLOW, org_city_id, SPE_ID,SESSION_NUMBER, CAT_SPE_ID order by org_city_id, SPE_ID

select org_city_id, JOINT_ORG_FLOW,JOINT_ORG_NAME, ORG_FLOW, ORG_NAME, cat_spe_id, SPE_ID, second_spe_id, COUNT(1) AS NUM from ( SELECT ORG.JOINT_ORG_FLOW,ORG.JOINT_ORG_NAME, ORG.ORG_FLOW,ORG.ORG_NAME, org.org_city_id, t.cat_spe_id, T.SPE_ID, CASE WHEN t.second_spe_id IS NULL THEN 'wu' ELSE t.second_spe_id END second_spe_id, rd.doctor_flow FROM RES_DOCTOR_recruit T left join ( SELECT JIORG.JOINT_ORG_FLOW,JIORG.JOINT_ORG_NAME, ORG.ORG_FLOW,ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and org.org_level_id = 'CountryOrg' UNION SELECT ORG.ORG_FLOW JOINT_ORG_FLOW,ORG.ORG_NAME JOINT_ORG_NAME, ORG.ORG_FLOW,ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where org.org_level_id = 'CountryOrg') ORG on t.org_flow = org.JOINT_ORG_FLOW left join res_doctor rd on t.doctor_flow = rd.doctor_flow LEFT JOIN SYS_USER u ON t.DOCTOR_FLOW = u.USER_FLOW WHERE T.RECORD_STATUS = 'Y' and t.audit_status_id = 'Passed' AND ORG.org_flow IN (SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y' AND ORG_PROV_ID = '320000'
and org_Flow=#{orgFlow}
) AND u.RECORD_STATUS = 'Y'
and rd.DOCTOR_TYPE_ID in #{item}
and t.session_number is not null
and t.session_number=#{sessionNumber}
and t.cat_spe_id in #{item}
union select ORG.JOINT_ORG_FLOW,ORG.JOINT_ORG_NAME, ORG.ORG_FLOW,ORG.ORG_NAME, org.org_city_id, T.TRAINING_TYPE_ID cat_spe_id, T.TRAINING_SPE_ID SPE_ID, CASE WHEN t.second_spe_id IS NULL THEN 'wu' ELSE t.second_spe_id END second_spe_id, rd.doctor_flow from res_docotr_delay_teturn t left join (SELECT JIORG.JOINT_ORG_FLOW,JIORG.JOINT_ORG_NAME, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG LEFT JOIN RES_JOINT_ORG JIORG ON ORG.ORG_FLOW = JIORG.ORG_FLOW WHERE 1 = 1 AND JIORG.RECORD_STATUS = 'Y' and org.org_level_id = 'CountryOrg' UNION SELECT ORG.ORG_FLOW JOINT_ORG_FLOW,ORG.ORG_NAME JOINT_ORG_NAME, ORG.ORG_FLOW, ORG.ORG_NAME, ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME FROM SYS_ORG ORG where org.org_level_id = 'CountryOrg') ORG on t.org_flow = org.JOINT_ORG_FLOW left join res_doctor rd on t.doctor_flow = rd.doctor_flow LEFT JOIN SYS_USER u ON t.DOCTOR_FLOW = u.USER_FLOW where t.record_status = 'Y' and t.type_id = 'ReturnTraining' AND ORG.org_flow IN (SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y' AND ORG_PROV_ID = '320000') and not exists( select 1 FROM RES_DOCTOR_recruit r where r.record_status='Y' and r.audit_status_id='Passed' and r.doctor_flow=t.doctor_flow and r.session_number=t.session_number )
and t.DOCTOR_TYPE_ID in #{item}
and t.session_number is not null
and t.session_number=#{sessionNumber}
and t.TRAINING_TYPE_ID in #{item}
) t
where org_city_id=#{orgCityId}
group by org_city_id,JOINT_ORG_FLOW,JOINT_ORG_Name,ORG_FLOW,org_name,cat_spe_id,SPE_ID,second_spe_id order by org_city_id,ORG_FLOW,cat_spe_id, SPE_ID,second_spe_id

select org_city_id, org_city_name, ORG_FLOW, ORG_NAME, cat_spe_id, COUNT(1) AS NUM from ( SELECT ORG.ORG_FLOW,ORG.ORG_NAME, org.org_city_id, org.org_city_name, t.cat_spe_id, rd.doctor_flow FROM JSRES_RECRUIT_doc_INFO T left join SYS_ORG ORG on t.org_flow = org.ORG_FLOW left join res_doctor rd on t.doctor_flow = rd.doctor_flow WHERE T.RECORD_STATUS = 'Y' AND ORG.org_flow IN (SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y' AND ORG_PROV_ID = '320000'
and org_Flow=#{orgFlow}
) and t.session_number is not null
and rd.DOCTOR_TYPE_ID in #{item}
and t.session_number=#{sessionNumber}
and t.train_Year in #{item}
and t.cat_spe_id in #{item}
) t WHERE 1=1
AND T.ORG_CITY_ID = #{cityId}
group by org_city_id,org_city_name,ORG_FLOW,org_name,cat_spe_id order by org_city_id,ORG_FLOW,cat_spe_id

		select dr.*,u.USER_FLOW, u.USER_CODE, u.USER_NAME, u.SEX_ID, u.SEX_NAME, u.USER_PHONE, u.USER_EMAIL
		, u.ID_NO, u.USER_BIRTHDAY, u.TITLE_ID, u.TITLE_NAME, u.DEGREE_ID, u.DEGREE_NAME, u.EDUCATION_ID, u.EDUCATION_NAME, u.POST_ID, u.POST_NAME,
		ed.ORG_FLOW as edOrgFlow,ed.ORG_Name as edOrgName
        from RES_DOCTOR_RECRUIT dr
        left join SYS_USER u
		on dr.DOCTOR_FLOW =  u.USER_FLOW
		left join  RES_DOCTOR ed
		on dr.DOCTOR_FLOW =  ed.DOCTOR_FLOW
		where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y'
		and dr.org_flow in(
		SELECT ORG_FLOW
        FROM SYS_ORG
        WHERE SYS_ORG.RECORD_STATUS='Y'

			AND ORG_PROV_ID = #{orgProvId}


			AND ORG_CITY_ID = #{orgCityId}


			AND ORG_FLOW = #{orgFlow}


			AND ORG_LEVEL_ID = #{orgLevel}


			AND ORG_TYPE_ID = #{orgTypeId}

		)

			and dr.ORG_FLOW in
			#{item}


			and ed.DOCTOR_TYPE_ID in
			#{item}


			and dr.graduation_Certificate_No like CONCAT(CONCAT('%', #{graduationCertificateNo}),'%')

		and dr.graduation_Certificate_No is not null

			and dr.CAT_SPE_ID = #{catSpeId}


			and dr.SPE_ID = #{speId}


			and dr.ORG_FLOW = #{orgFlow}


			and dr.GRADUATION_YEAR=#{graduationYear}


			and dr.doctor_flow=#{doctorFlow}

		and dr.AUDIT_STATUS_ID='Passed'


			and u.USER_NAME like #{userName}



			and u.ID_NO like #{idNo}


			and u.ID_NO =#{notLikeIdNo}


			and u.user_name =#{notLikeUserName}


			and dr.CAT_SPE_ID in
			#{item}


			and dr.SPE_ID = #{trainingSpeId}


			and dr.SESSION_NUMBER = #{sessionNumber}


			and ed.DOCTOR_TYPE_ID = #{doctorTypeId}


			and ed.WORK_ORG_LEVEL_ID = #{workOrgLevelId}


			and ed.doctor_Status_Id = #{doctorStatusId}


			and (

				OR ed.work_org_id = #{workOrgId}
				OR ed.work_org_name = #{workOrgName}

			)

		order by  dr.GRADUATION_YEAR desc, dr.org_flow,NLSSORT(u.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M'),dr.CREATE_TIME desc

SELECT ORG_CITY_ID, ORG_CITY_NAME, ORG_FLOW, ORG_NAME, CAT_SPE_ID, CAT_SPE_NAME, COUNT(1) AS NUM FROM (SELECT ORG.ORG_CITY_ID, ORG.ORG_CITY_NAME, ORG.ORG_FLOW, ORG.ORG_NAME, T.CAT_SPE_ID, T.CAT_SPE_NAME, RD.DOCTOR_FLOW FROM RES_DOCTOR_RECRUIT T LEFT JOIN SYS_ORG ORG ON T.ORG_FLOW = ORG.ORG_FLOW LEFT JOIN RES_DOCTOR RD ON T.DOCTOR_FLOW = RD.DOCTOR_FLOW LEFT JOIN SYS_USER U ON T.DOCTOR_FLOW = U.USER_FLOW WHERE T.RECORD_STATUS = 'Y' AND T.AUDIT_STATUS_ID = 'Passed' AND T.ORG_FLOW IN (SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y' AND ORG_PROV_ID = '320000') AND U.RECORD_STATUS = 'Y'
AND RD.DOCTOR_TYPE_ID IN #{item}
AND T.TRAIN_YEAR IN #{item}
AND org.ORG_CITY_ID = #{cityId}
and T.ORG_FLOW = #{orgFlow}
AND T.SESSION_NUMBER = #{sessionNumber}
AND rd.doctor_Status_Id = '20' AND T.SESSION_NUMBER IS NOT NULL AND T.SESSION_NUMBER > '2014') T GROUP BY ORG_CITY_ID, ORG_CITY_NAME, ORG_FLOW, ORG_NAME, CAT_SPE_ID, CAT_SPE_NAME ORDER BY ORG_CITY_ID

WITH change_detail AS ( SELECT h.* FROM ( SELECT H.DOCTOR_FLOW,dr.session_number, H.ORG_FLOW, H.HISTORY_ORG_FLOW, CASE H.TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.TRAINING_SPE_ID END AS CAT_SPE_ID, CASE H.HISTORY_TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.HISTORY_TRAINING_SPE_ID END AS HISTORY_CAT_SPE_ID , '' AS CHANGE_TYPE, H.DOCTOR_NAME, TO_CHAR(TO_DATE(H.CREATE_TIME, 'yyyymmddhh24miss'), 'yyyy-mm-dd hh24:mi') || ' ' || H.HISTORY_TRAINING_TYPE_NAME || '-' || H.HISTORY_TRAINING_SPE_NAME || NVL2(H.HISTORY_SECOND_SPE_NAME, CONCAT('-', H.HISTORY_SECOND_SPE_NAME), NULL) || '' || H.TRAINING_TYPE_NAME || '-' || H.TRAINING_SPE_NAME || NVL2(H.SECOND_SPE_NAME, CONCAT('-', H.SECOND_SPE_NAME), NULL) || '' AS DESCRIPTION FROM RES_DOCTOR_ORG_HISTORY H LEFT JOIN RES_DOCTOR_RECRUIT DR ON H.DOCTOR_FLOW = DR.DOCTOR_FLOW WHERE H.CHANGE_STATUS_ID IN ('GlobalApplyPass', 'GlobalAuditPass', 'BaseAuditPass') AND H.CHANGE_TYPE_ID = 'SpeChange' AND H.RECORD_STATUS = 'Y' UNION SELECT H.DOCTOR_FLOW,dr.session_number, H.ORG_FLOW, H.HISTORY_ORG_FLOW, CASE H.TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.TRAINING_SPE_ID END AS CAT_SPE_ID, CASE H.HISTORY_TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.HISTORY_TRAINING_SPE_ID END AS HISTORY_CAT_SPE_ID , '' AS CHANGE_TYPE, H.DOCTOR_NAME, TO_CHAR(TO_DATE(H.CREATE_TIME, 'yyyymmddhh24miss'), 'yyyy-mm-dd hh24:mi') || ' ' || H.HISTORY_ORG_NAME || '' || H.ORG_NAME || '' AS DESCRIPTION FROM RES_DOCTOR_ORG_HISTORY H LEFT JOIN RES_DOCTOR_RECRUIT DR ON H.DOCTOR_FLOW = DR.DOCTOR_FLOW WHERE H.CHANGE_STATUS_ID IN ('GlobalApplyPass', 'GlobalAuditPass', 'BaseAuditPass') AND H.CHANGE_TYPE_ID = 'BaseChange' AND H.RECORD_STATUS = 'Y' UNION SELECT T.DOCTOR_FLOW, t.session_number,T.ORG_FLOW, T.ORG_FLOW AS HISTORY_ORG_FLOW, DR.CAT_SPE_ID AS HISTORY_CAT_SPE_ID, DR.CAT_SPE_ID AS CAT_SPE_ID , '' AS CHANGE_TYPE, T.DOCTOR_NAME, TO_CHAR(TO_DATE(T.CREATE_TIME, 'yyyymmddhh24miss'), 'yyyy-mm-dd hh24:mi') || '' || T.TYPE_NAME || '' AS DESCRIPTION FROM RES_DOCOTR_DELAY_TETURN T LEFT JOIN RES_DOCTOR_RECRUIT DR ON T.DOCTOR_FLOW = DR.DOCTOR_FLOW WHERE T.TYPE_ID = 'Delay' AND T.RECORD_STATUS = 'Y' UNION SELECT T.DOCTOR_FLOW, t.session_number,T.ORG_FLOW, T.ORG_FLOW AS HISTORY_ORG_FLOW, DR.CAT_SPE_ID AS HISTORY_CAT_SPE_ID, DR.CAT_SPE_ID AS CAT_SPE_ID , '' AS CHANGE_TYPE, T.DOCTOR_NAME, TO_CHAR(TO_DATE(T.CREATE_TIME, 'yyyymmddhh24miss'), 'yyyy-mm-dd hh24:mi') || ' ' || T.POLICY_NAME || '' AS DESCRIPTION FROM RES_DOCOTR_DELAY_TETURN T LEFT JOIN RES_DOCTOR_RECRUIT DR ON T.DOCTOR_FLOW = DR.DOCTOR_FLOW WHERE T.TYPE_ID = 'ReturnTraining' AND T.RECORD_STATUS = 'Y' AND T.AUDIT_STATUS_ID = 'GlobalPassed' UNION SELECT H.user_flow AS doctor_flow,H.session_number ,H.ORG_FLOW ,H.ORG_FLOW AS HISTORY_ORG_FLOW, CASE H.TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.TRAINING_SPE_ID END AS CAT_SPE_ID, CASE H.TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.TRAINING_SPE_ID END AS HISTORY_CAT_SPE_ID, '' AS CHANGE_TYPE,H.user_NAME,TO_CHAR(TO_DATE(H.CREATE_TIME, 'yyyymmddhh24miss'),'yyyy-mm-dd hh24:mi') || ' ' AS DESCRIPTION FROM JSRES_USER_BALCKLIST H WHERE H.OPER_TYPE_ID = '2' AND H.RECORD_STATUS = 'Y' AND H.USER_FLOW IS NOT NULL ) H LEFT JOIN SYS_ORG ORG ON H.HISTORY_ORG_FLOW = ORG.ORG_FLOW OR H.ORG_FLOW = ORG.ORG_FLOW LEFT JOIN RES_DOCTOR RD ON H.DOCTOR_FLOW = RD.DOCTOR_FLOW LEFT JOIN SYS_USER U ON H.DOCTOR_FLOW = U.USER_FLOW WHERE H.ORG_FLOW IN (SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y' AND ORG_PROV_ID = '320000')
AND RD.DOCTOR_TYPE_ID IN #{item}
AND org.ORG_CITY_ID = #{cityId}
AND (H.ORG_FLOW =#{orgFlow} OR H.HISTORY_ORG_FLOW = #{orgFlow})
AND (H.CAT_SPE_ID = #{catSpeId} OR H.HISTORY_CAT_SPE_ID = #{catSpeId})
AND h.SESSION_NUMBER = #{sessionNumber}
AND h.SESSION_NUMBER IS NOT NULL AND h.SESSION_NUMBER > '2014'), count1 AS ( SELECT doctor_flow,org_flow,cat_spe_id FROM change_detail ), count2 AS ( SELECT doctor_flow,history_org_flow AS org_flow,history_cat_spe_id AS cat_spe_id FROM change_detail ), count3 AS ( SELECT * FROM count1 UNION SELECT * FROM count2 )
SELECT org_flow,cat_spe_id ,COUNT(1) num FROM count3 GROUP BY org_flow,cat_spe_id

SELECT DISTINCT H.DOCTOR_FLOW, H.HISTORY_ORG_FLOW, H.DOCTOR_FLOW, H.HISTORY_CAT_SPE_ID, H.CAT_SPE_ID, H.CHANGE_TYPE, H.DOCTOR_NAME, H.DESCRIPTION FROM ( SELECT H.DOCTOR_FLOW,dr.session_number, H.ORG_FLOW, H.HISTORY_ORG_FLOW, CASE H.TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.TRAINING_SPE_ID END AS CAT_SPE_ID, CASE H.HISTORY_TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.HISTORY_TRAINING_SPE_ID END AS HISTORY_CAT_SPE_ID , '' AS CHANGE_TYPE, H.DOCTOR_NAME, TO_CHAR(TO_DATE(H.CREATE_TIME, 'yyyymmddhh24miss'), 'yyyy-mm-dd hh24:mi') || ' ' || H.HISTORY_TRAINING_TYPE_NAME || '-' || H.HISTORY_TRAINING_SPE_NAME || NVL2(H.HISTORY_SECOND_SPE_NAME, CONCAT('-', H.HISTORY_SECOND_SPE_NAME), NULL) || '' || H.TRAINING_TYPE_NAME || '-' || H.TRAINING_SPE_NAME || NVL2(H.SECOND_SPE_NAME, CONCAT('-', H.SECOND_SPE_NAME), NULL) || '' AS DESCRIPTION FROM RES_DOCTOR_ORG_HISTORY H LEFT JOIN RES_DOCTOR_RECRUIT DR ON H.DOCTOR_FLOW = DR.DOCTOR_FLOW WHERE H.CHANGE_STATUS_ID IN ('GlobalApplyPass', 'GlobalAuditPass', 'BaseAuditPass') AND H.CHANGE_TYPE_ID = 'SpeChange' AND H.RECORD_STATUS = 'Y' UNION SELECT H.DOCTOR_FLOW,dr.session_number, H.ORG_FLOW, H.HISTORY_ORG_FLOW, CASE H.TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.TRAINING_SPE_ID END AS CAT_SPE_ID, CASE H.HISTORY_TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.HISTORY_TRAINING_SPE_ID END AS HISTORY_CAT_SPE_ID , '' AS CHANGE_TYPE, H.DOCTOR_NAME, TO_CHAR(TO_DATE(H.CREATE_TIME, 'yyyymmddhh24miss'), 'yyyy-mm-dd hh24:mi') || ' ' || H.HISTORY_ORG_NAME || '' || H.ORG_NAME || '' AS DESCRIPTION FROM RES_DOCTOR_ORG_HISTORY H LEFT JOIN RES_DOCTOR_RECRUIT DR ON H.DOCTOR_FLOW = DR.DOCTOR_FLOW WHERE H.CHANGE_STATUS_ID IN ('GlobalApplyPass', 'GlobalAuditPass', 'BaseAuditPass') AND H.CHANGE_TYPE_ID = 'BaseChange' AND H.RECORD_STATUS = 'Y' UNION SELECT T.DOCTOR_FLOW, t.session_number,T.ORG_FLOW, T.ORG_FLOW AS HISTORY_ORG_FLOW, DR.CAT_SPE_ID AS HISTORY_CAT_SPE_ID, DR.CAT_SPE_ID AS CAT_SPE_ID , '' AS CHANGE_TYPE, T.DOCTOR_NAME, TO_CHAR(TO_DATE(T.CREATE_TIME, 'yyyymmddhh24miss'), 'yyyy-mm-dd hh24:mi') || '' || T.TYPE_NAME || '' AS DESCRIPTION FROM RES_DOCOTR_DELAY_TETURN T LEFT JOIN RES_DOCTOR_RECRUIT DR ON T.DOCTOR_FLOW = DR.DOCTOR_FLOW WHERE T.TYPE_ID = 'Delay' AND T.RECORD_STATUS = 'Y' UNION SELECT T.DOCTOR_FLOW, t.session_number,T.ORG_FLOW, T.ORG_FLOW AS HISTORY_ORG_FLOW, DR.CAT_SPE_ID AS HISTORY_CAT_SPE_ID, DR.CAT_SPE_ID AS CAT_SPE_ID , '' AS CHANGE_TYPE, T.DOCTOR_NAME, TO_CHAR(TO_DATE(T.CREATE_TIME, 'yyyymmddhh24miss'), 'yyyy-mm-dd hh24:mi') || ' ' || T.POLICY_NAME || '' AS DESCRIPTION FROM RES_DOCOTR_DELAY_TETURN T LEFT JOIN RES_DOCTOR_RECRUIT DR ON T.DOCTOR_FLOW = DR.DOCTOR_FLOW WHERE T.TYPE_ID = 'ReturnTraining' AND T.RECORD_STATUS = 'Y' AND T.AUDIT_STATUS_ID = 'GlobalPassed' UNION SELECT H.user_flow AS doctor_flow,H.session_number ,H.ORG_FLOW ,H.ORG_FLOW AS HISTORY_ORG_FLOW, CASE H.TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.TRAINING_SPE_ID END AS CAT_SPE_ID, CASE H.TRAINING_SPE_ID WHEN '3501' THEN 'ChineseMedicine' WHEN '3502' THEN 'ChineseMedicine' WHEN '3601' THEN 'TCMGeneral' WHEN '3602' THEN 'TCMGeneral' WHEN '6000' THEN 'TCMAssiGeneral' ELSE H.TRAINING_SPE_ID END AS HISTORY_CAT_SPE_ID, '' AS CHANGE_TYPE,H.user_NAME,TO_CHAR(TO_DATE(H.CREATE_TIME, 'yyyymmddhh24miss'),'yyyy-mm-dd hh24:mi') || ' ' AS DESCRIPTION FROM JSRES_USER_BALCKLIST H WHERE H.OPER_TYPE_ID = '2' AND H.RECORD_STATUS = 'Y' AND H.USER_FLOW IS NOT NULL ) H LEFT JOIN SYS_ORG ORG ON H.HISTORY_ORG_FLOW = ORG.ORG_FLOW OR H.ORG_FLOW = ORG.ORG_FLOW LEFT JOIN RES_DOCTOR RD ON H.DOCTOR_FLOW = RD.DOCTOR_FLOW LEFT JOIN SYS_USER U ON H.DOCTOR_FLOW = U.USER_FLOW WHERE H.ORG_FLOW IN (SELECT ORG_FLOW FROM SYS_ORG WHERE SYS_ORG.RECORD_STATUS = 'Y' AND ORG_PROV_ID = '320000')
AND RD.DOCTOR_TYPE_ID IN #{item}
AND org.ORG_CITY_ID = #{cityId}
AND (H.ORG_FLOW =#{orgFlow} OR H.HISTORY_ORG_FLOW = #{orgFlow})
AND (H.CAT_SPE_ID = #{catSpeId} OR H.HISTORY_CAT_SPE_ID = #{catSpeId})
AND h.SESSION_NUMBER = #{sessionNumber}
AND h.SESSION_NUMBER IS NOT NULL AND h.SESSION_NUMBER > '2014' ORDER BY H.CHANGE_TYPE DESC
