mapper.select:
SELECT LFA.FIXED_FLOW,LFA.DICT_ID,
        LFA.DICT_NAME,LFA.FIXED_PRICE,
        LFA.FIXED_CODE,LFA.STATUS_ID,LFA.STATUS_NAME
FROM LCJN_FIXED_ASSETS LFA
WHERE LFA.RECORD_STATUS='Y'
AND LFA.ORG_FLOW=#{orgFlow}


    AND LFA.DICT_NAME LIKE #{name}


    AND LFA.STATUS_ID =#{statusId}

SELECT LS.SUPPLIES_FLOW,LS.DICT_NAME,LS.DICT_ID,LS.SUPPLIES_PRICE,TEMP.STOCK_NUM FROM LCJN_SUPPLIES LS LEFT JOIN ( SELECT LSB.SUPPLIES_FLOW SUPPLIES_FLOW,SUM(LSB.STOCK_NUM) STOCK_NUM FROM LCJN_SUPPLIES_BATCH LSB WHERE LSB.RECORD_STATUS='Y' GROUP BY LSB.SUPPLIES_FLOW) TEMP ON LS.SUPPLIES_FLOW=TEMP.SUPPLIES_FLOW WHERE LS.RECORD_STATUS='Y' AND LS.ORG_FLOW=#{orgFlow}
AND LS.DICT_NAME LIKE #{name}
AND LS.SUPPLIES_FLOW = #{suppliesFlow}
