mapper.select:
		 select t1.*,t2.* from (select QUESTION_FLOW, COURSE_FLOW, CHAPTER_FLOW, QUESTION_STATUS_ID, QUESTION_STATUS_NAME, 
				QUESTION_CONTENT, QUESTION_TIME, SUBMIT_TEACHER,quintessence,su.USER_FLOW,USER_NAME from  pdsci.edu_question eq,pdsci.sys_user su
				where eq.user_flow = su.user_flow and eq.RECORD_STATUS = 'Y' and su.RECORD_STATUS = 'Y' and eq.QUES_TYPE_ID = 'QA'

 			  	and CHAPTER_FLOW = #{chapterFlow}

       		)t1 left join 
       			(select ANSWER_FLOW, QUESTION_FLOW,  ANSWER_USER_FLOW, ANSWER_CONTENT, ANSWER_TIME,asu.user_name as ANSWER_USER_NAME from 
       		pdsci.edu_answer ea,pdsci.sys_user asu where ea.ANSWER_USER_FLOW = asu.user_flow and ea.RECORD_STATUS = 'Y' and asu.RECORD_STATUS = 'Y' and ea.ANS_TYPE_ID = 'QA'
       		) t2 on t1.QUESTION_FLOW = t2.QUESTION_FLOW 			  
			order  by t1.QUESTION_TIME desc,t2.ANSWER_TIME desc

select so.ORG_FLOW,so.ORG_NAME,eu.MAJOR_ID,eu.MAJOR_NAME from pdsci.edu_question qs left join pdsci.edu_user eu on qs.user_flow=eu.user_flow left join pdsci.sys_user su on su.user_flow= eu.user_flow left join pdsci.sys_org so on so.org_flow=su.org_flow where eu.record_status='Y' and eu.record_status='Y' and eu.record_status='Y' and eu.record_status='Y'
and qs.course_flow=#{courseFlow}
and (so.org_name like #{condition} or eu.major_name like #{condition})
and qs.QUES_TYPE_ID =#{eduQuestion.quesTypeId}
GROUP BY eu.MAJOR_ID,so.ORG_FLOW,so.ORG_NAME,eu.MAJOR_NAME

		  select count(qs.question_flow) questionCount 
        from pdsci.edu_question qs,pdsci.edu_user eu, pdsci.sys_user su
        where qs.user_flow=eu.user_flow
        and su.user_flow= eu.user_flow
        and qs.record_status='Y'
        and su.record_status='Y'
        and eu.record_status='Y'

           and qs.course_flow = #{courseFlow}


          and qs.question_Status_Id = #{questionStatusId}


          and su.org_flow = #{orgFlow}


          and eu.major_id = #{majorId}

		select eq.QUESTION_FLOW, eq.CREATE_TIME, eq.QUES_DIR, eq.QUESTION_CONTENT, eq.RECORD_STATUS,
			 ec.COURSE_FLOW courseFlow, ec.COURSE_NAME,
			 ecc.CHAPTER_FLOW chapterFlow, ecc.CHAPTER_NAME,
			 su.USER_FLOW , su.USER_NAME
		from pdsci.EDU_QUESTION eq, pdsci.EDU_COURSE ec, pdsci.EDU_COURSE_CHAPTER ecc, pdsci.SYS_USER su
		where eq.COURSE_FLOW = ec.COURSE_FLOW and eq.CHAPTER_FLOW = ecc.CHAPTER_FLOW
		and eq.USER_FLOW = su.USER_FLOW
		and eq.RECORD_STATUS != 'N' and ec.RECORD_STATUS = 'Y' and ecc.RECORD_STATUS = 'Y' and su.RECORD_STATUS = 'Y'
		and eq.QUES_TYPE_ID = #{question.quesTypeId}


        	and ecc.CHAPTER_NAME like #{chapterName}


        	and ecc.COURSE_FLOW = #{chapter.courseFlow}


        	and eq.USER_FLOW = #{question.userFlow}

		order by eq.CREATE_TIME DESC

select eq.QUESTION_FLOW, eq.CREATE_TIME, eq.QUES_DIR, eq.QUESTION_CONTENT, ec.COURSE_FLOW ec_course_flow, ec.COURSE_NAME, ecc.CHAPTER_FLOW ecc_chapter_flow, ecc.CHAPTER_NAME, su.USER_FLOW su_user_flow, su.USER_NAME from pdsci.EDU_QUESTION eq left join pdsci.EDU_COURSE ec on eq.COURSE_FLOW = ec.COURSE_FLOW left join pdsci.EDU_COURSE_CHAPTER ecc on eq.CHAPTER_FLOW = ecc.CHAPTER_FLOW left join pdsci.SYS_USER su on ecc.TEACHER_ID = su.USER_FLOW where eq.RECORD_STATUS = 'Y' and ec.RECORD_STATUS = 'Y' and ecc.RECORD_STATUS='Y' and eq.QUES_TYPE_ID = #{question.quesTypeId}
and ecc.CHAPTER_NAME like #{chapterName}
and ecc.COURSE_FLOW = #{chapter.courseFlow}
and ec.COURSE_FLOW in ( select COURSE_FLOW from EDU_STUDENT_COURSE where RECORD_STATUS = 'Y' and USER_FLOW = #{studentCourse.userFlow} )
order by eq.CREATE_TIME DESC
