mapper.select:
select esc.record_flow,esc.user_flow,esc.course_flow, esc.study_status_Id,esc.study_status_Name,esc.current_chapter_flow, esc.course_grade,esc.choose_time,esc.achieve_credit,
ec.course_name,ec.course_major_id, ec.course_major_name,ec.course_credit,ec.course_type_id, ec.course_type_name,ec.course_period from pdsci.edu_student_course esc,pdsci.edu_course ec where esc.record_status='Y' and ec.record_status='Y' and esc.course_flow=ec.course_flow
and esc.user_flow=#{userFlow}
and ec.course_Type_Id=#{courseTypeId}

select count(esc.user_flow) from pdsci.edu_user eu left join pdsci.sys_user su on eu.user_flow=su.user_flow left join pdsci.sys_org so on su.org_flow=so.org_flow left join pdsci.edu_student_course esc on esc.user_flow=eu.user_flow left join pdsci.edu_course ec on esc.course_flow=ec.course_flow where eu.RECORD_STATUS='Y' and su.RECORD_STATUS='Y' and so.RECORD_STATUS='Y' and esc.RECORD_STATUS='Y' and ec.RECORD_STATUS='Y'
and so.org_flow=#{orgFlow}
and ec.course_Flow=#{courseFlow}
