mapper.select:
select eu.*,su.* from pdsci.edu_user eu,pdsci.sys_user su,pdsci.sys_user_role sur where eu.RECORD_STATUS = 'Y' and su.RECORD_STATUS = 'Y' and eu.user_flow = su.user_flow
       and su.user_flow = sur.user_flow


		and su.status_id = #{sysUser.statusId}


		and su.org_flow = #{sysUser.orgFlow}


		and su.user_name like #{sysUser.userName}




		and sur.role_flow = #{sysRole.roleFlow}


order by eu.create_time desc

select eu.user_flow,eu.major_id,eu.major_name,eu.period, eu.sid,eu.credit,eu.intro, su.user_name,su.user_code,su.sex_id,su.sex_name,su.status_id, su.status_desc,su.user_phone,su.user_email,su.dept_flow,su.dept_name, su.id_no,su.user_birthday,su.title_id,su.title_name, su.degree_id,su.degree_name,su.education_id,su.education_name, su.post_id,su.post_name,su.CREATE_TIME,su.user_head_img, so.org_flow,so.org_name from pdsci.edu_user eu,pdsci.sys_user su,pdsci.sys_org so,pdsci.sys_user_role sur where eu.RECORD_STATUS = 'Y' and su.RECORD_STATUS = 'Y' and so.RECORD_STATUS = 'Y' and sur.RECORD_STATUS = 'Y' and eu.user_flow = su.user_flow and so.org_flow = su.org_flow and su.user_flow =sur.user_flow
and su.user_flow in ( SELECT distinct doctor_flow FROM study_subject_detail where audit_status_id ='PassedTwo' )
and so.org_flow=#{sysUser.orgFlow}
and su.status_id=#{sysUser.statusId}
and su.user_name like '%${sysUser.userName}%'
and su.id_No like '%${sysUser.idNo}%'
and eu.major_Id = #{majorId}
and sur.role_flow=#{roleFlow}
and (su.user_name like #{condition} or so.org_name like #{condition} or eu.major_name like #{condition} )
and eu.user_flow=#{userFlow}
and (select count(1) FROM edu_exam_score ees left join edu_course dc on ees.course_flow=dc.course_flow where is_qualified='Y' and ees.record_status='Y' and Dc.Record_Status='Y' and ees.user_flow=su.user_flow) =(select count(1) from edu_course ec where ec.record_status='Y' )
and (select count(1) FROM edu_exam_score ees left join edu_course dc on ees.course_flow=dc.course_flow where is_qualified='Y' and ees.record_status='Y' and Dc.Record_Status='Y' and ees.user_flow=su.user_flow) !=(select count(1) from edu_course ec where ec.record_status='Y' )
and (select count(1) FROM edu_student_course ees left join edu_course dc on ees.course_flow=dc.course_flow where study_status_Id='Finish' and ees.record_status='Y' and Dc.Record_Status='Y' and ees.user_flow=su.user_flow) =(select count(1) from edu_course ec where ec.record_status='Y' )
and (select count(1) FROM edu_student_course ees left join edu_course dc on ees.course_flow=dc.course_flow where study_status_Id='Finish' and ees.record_status='Y' and Dc.Record_Status='Y' and ees.user_flow=su.user_flow) !=(select count(1) from edu_course ec where ec.record_status='Y' )
group by eu.user_flow,eu.major_id,eu.major_name,eu.period, eu.sid,su.user_head_img,eu.credit,eu.intro, su.user_name,su.user_code,su.sex_id,su.sex_name,su.status_id,su.org_flow, su.status_desc,su.user_phone,su.user_email,su.dept_flow,su.dept_name, su.id_no,su.user_birthday,su.title_id,su.title_name, su.degree_id,su.degree_name,su.education_id,su.education_name, su.post_id,su.post_name,su.CREATE_TIME,su.user_head_img, so.org_flow,so.org_name order by su.org_flow desc,eu.period desc,eu.major_id desc,su.id_no desc
#{sort}

select eu.user_flow,eu.major_id,eu.major_name,eu.period, eu.sid,su.user_head_img,eu.credit,eu.intro, su.user_name,su.user_code,su.sex_id,su.sex_name,su.status_id, su.status_desc,su.user_phone,su.user_email, su.id_no,su.user_birthday,su.title_id,su.title_name, su.degree_id,su.degree_name,su.education_id,su.education_name, su.post_id,su.post_name,su.CREATE_TIME, so.org_flow,so.org_name,ec.course_flow,ec.course_name from pdsci.edu_user eu,pdsci.sys_user su,pdsci.sys_org so,pdsci.sys_user_role sur,pdsci.edu_course ec where eu.RECORD_STATUS = 'Y' and su.RECORD_STATUS = 'Y' and so.RECORD_STATUS = 'Y' and sur.RECORD_STATUS = 'Y' and ec.RECORD_STATUS = 'Y' and eu.user_flow = su.user_flow and so.org_flow = su.org_flow and su.user_flow =sur.user_flow
and su.user_flow in ( SELECT distinct doctor_flow FROM study_subject_detail where audit_status_id ='PassedTwo' )
and ec.course_flow in (
select distinct(course_flow) from pdsci.edu_course_chapter where RECORD_STATUS = 'Y' and teacher_id=eu.user_flow
select distinct(course_flow) from pdsci.edu_student_course where RECORD_STATUS = 'Y' and user_flow=eu.user_flow
)
and so.org_flow=#{sysUser.orgFlow}
and su.status_id=#{sysUser.statusId}
and sur.role_flow=#{roleFlow}
and (su.user_name like #{condition} or so.org_name like #{condition} or eu.major_name like #{condition} or ec.course_name like #{condition} or eu.SID like #{condition} )
and eu.user_flow=#{userFlow}
group by eu.user_flow,eu.major_id,eu.major_name,eu.period, eu.sid,su.user_head_img,eu.credit,eu.intro, su.user_name,su.user_code,su.sex_id,su.sex_name,su.status_id, su.status_desc,su.user_phone,su.user_email, su.id_no,su.user_birthday,su.title_id,su.title_name, su.degree_id,su.degree_name,su.education_id,su.education_name, su.post_id,su.post_name,su.CREATE_TIME, so.org_flow,so.org_name,ec.course_flow,ec.course_name
