mapper.select:
select * from (
select proj_flow ,PROJ_NO,account_No, proj_year , proj_type_id , proj_type_name , proj_start_time , proj_end_time , proj_name ,
apply_user_flow , apply_user_name , apply_org_flow , apply_org_name , create_time ct
,PROJ_DECLARER,PROJ_DECLARER_FLOW,PROJ_SECOND_SOURCE_ID,PROJ_SECOND_SOURCE_NAME
from PDSCI.pub_proj pp
where RECORD_STATUS='Y' and PROJ_CATEGORY_ID = #{projCateScope}

    and pp.PROJ_FLOW=#{projFlow}


    and pp.account_No like '%${accountNo}%'


    and pp.PROJ_NO like '%${projNo}%'


    and pp.proj_year  >=  '${startYear}'


    and pp.proj_year  <=  '${endYear}'


    and pp.APPLY_DEPT_FLOW = '${applyDeptFlow}'


    and pp.PROJ_STAGE_ID=#{projStageId}


    and pp.PROJ_STATUS_ID=#{projStatusId}


    and pp.PROJ_YEAR=#{projYear}


    and pp.PROJ_TYPE_ID=#{projTypeId}


    and pp.PROJ_DECLARER_FLOW = #{projDeclarerFlow}


    and pp.PROJ_SECOND_SOURCE_ID = #{projSecondSourceId}


    and pp.PROJ_NAME LIKE '%${projName}%'


    and pp.APPLY_ORG_FLOW = '${applyOrgFlow}'


    and pp.CHARGE_ORG_FLOW = '${chargeOrgFlow}'


    and pp.SUBJ_NAME like '%${subjName}%'


    and pp.APPLY_USER_NAME like '%${applyUserName}%'



    and (EXISTS (SELECT null FROM PDSCI.PUB_PROJ_REC WHERE rec_type_id=#{recTypeId}
    AND pp.PROJ_FLOW = PROJ_FLOW))

) t left join PDSCI.SRM_EXPERT_PROJ_EVAL segp on t.proj_flow=segp.PROJ_FLOW AND
SEGP.EVALUATION_ID=#{srmExpertGroupProj.evaluationId} ORDER BY segp.proj_flow desc , ct DESC

select count(*)
from

    PDSCI.pub_proj_rec pr,

PDSCI.pub_proj pp
where pp.RECORD_STATUS='Y'

    and pp.proj_Category_Id=#{projCategoryId}


    and pr.PROJ_FLOW=pp.PROJ_FLOW



        and pr.REC_TYPE_ID=#{recTypeId}




        and pr.PROJ_STAGE_ID=#{projStageId}

    and pp.PROJ_STAGE_ID=#{projStageId}



        and pr.PROJ_STATUS_ID in

            #{status}



        and pp.PROJ_STATUS_ID in

            #{status}




    and pp.APPLY_ORG_FLOW in (select org_flow from PDSCI.sys_org start WITH org_flow=#{chargeOrgFlow} connect by
    prior org_flow=charge_org_flow)


    and pp.PROJ_FLOW not in (select PROJ_FLOW from PDSCI.pub_proj_process where
    PROJ_STAGE_ID=#{processApproveStageId} and PROJ_STATUS_ID=#{processApproveStatusId})


    and pp.PROJ_FLOW not in (select PROJ_FLOW from PDSCI.pub_proj_process where
    PROJ_STAGE_ID=#{processCompleteStageId} and PROJ_STATUS_ID=#{processCompleteStatusId})

select count(*) from ${tableName} where RECORD_STATUS='Y'

    and OPER_STATUS_ID in

        #{status}



    and APPLY_ORG_FLOW=#{applyOrgFlow}

select count(*) from PDSCI.pub_proj pp where pp.RECORD_STATUS='Y'
and pp.PROJ_STAGE_ID=#{projStageId}
and pp.PROJ_STATUS_ID in
#{status}
and pp.APPLY_ORG_FLOW in (select org_flow from PDSCI.sys_org start WITH org_flow=#{chargeOrgFlow} connect by prior org_flow=charge_org_flow)
and pp.PROJ_FLOW in (select proj_flow from PDSCI.srm_proj_fund_info where budget_status_id=#{budgetStatusId})

select count(*) projCount,so.org_name orgName,so.org_flow orgFlow
from PDSCI.sys_org so,PDSCI.pub_proj pp

    ,PDSCI.pub_proj_process ppp

where pp.apply_org_flow=so.org_Flow

    and ppp.proj_stage_id=#{projStageId}


    and pp.proj_category_id=#{projCategoryId}


    and ppp.proj_status_id=#{projStatusId}


    and ppp.proj_flow=pp.proj_Flow


    and so.charge_org_flow=#{orgFlow}

group by so.org_name,so.org_flow

select count(*)
from pdsci.PUB_PROJ p
where p.RECORD_STATUS='Y'


        and p.PROJ_YEAR = #{pubProj.projYear}


        and p.APPLY_ORG_FLOW = #{pubProj.applyOrgFlow}


        and p.PROJ_CATEGORY_ID = #{pubProj.projCategoryId}


        and p.PROJ_TYPE_ID = #{pubProj.projTypeId}

    and proj_flow not in ( select proj_flow from pub_proj where PROJ_STAGE_ID=#{projStageId} and (PROJ_STATUS_ID
    = #{projApplyStatusId1} or PROJ_STATUS_ID = #{projApplyStatusId2}))

select

    distinct


from PDSCI.PUB_PROJ
where RECORD_STATUS='Y'

    and PROJ_STAGE_ID=#{proj.projStageId}


    and proj_category_id=#{proj.projCategoryId}


    and PROJ_STATUS_ID=#{proj.projStatusId}


    and PROJ_YEAR=#{proj.projYear}


    and PROJ_TYPE_ID=#{proj.projTypeId}


    and PROJ_NAME LIKE '%${proj.projName}%'


    and APPLY_ORG_FLOW = '${proj.applyOrgFlow}'


    and CHARGE_ORG_FLOW = '${proj.chargeOrgFlow}'


    and PROJ_NO = '${proj.projNo}'



    ${sql}

order by CREATE_TIME DESC

select t.projTypeId projTypeId, t.projTypeName projTypeName, (t.a+t.b) applyNum,t.b applyPassNum,decode(t.a+t.b,0,0,t.b/(t.a+t.b)) applyPassScale, t.c reviewNum,t.d approvePassNum,decode(t.b,0,0,t.d/t.b) approvePassScale from( select pp.proj_type_id projTypeId,max(pp.proj_type_name) projTypeName, count(case when pp.proj_stage_id = 'Apply'and pp.proj_status_id not in ('Submit','Apply') then 1 else null end) a, count(case when pp.proj_stage_id <> 'Apply' then 1 else null end) b, count(sepe.proj_flow) c, count(case when pp.proj_stage_id <> 'Apply' and pp.proj_stage_id <> 'Approve' then 1 else null end) d from pub_proj pp left join SRM_EXPERT_PROJ_EVAL sepe on pp.proj_flow = sepe.proj_flow and sepe.EVALUATION_WAY_ID = 'NetWorkWay' where pp.record_status='Y'
and pp.proj_category_id=#{projCategoryId}
and pp.apply_org_flow=#{applyOrgFlow}
and pp.CHARGE_ORG_FLOW=#{chargeOrgFlow}
and pp.PROJ_YEAR=#{projYear}
group by pp.proj_type_id)t
