and (exists( select NULL from (select p.PROCESS_FLOW PROCESS_FLOW, P.USER_FLOW USER_FLOW , P.DEPT_FLOW DEPT_FLOW, RD.DOCTOR_TYPE_ID, nvl((select CFG_VALUE from jsres_power_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jsres_doctor_app_menu_'||rd.doctor_flow),'N')APP_MENU_FLAG from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y') A WHERE A.APP_MENU_FLAG='Y' AND dtgi.PROCESS_FLOW = a.PROCESS_FLOW AND dtgi.OPER_USER_FLOW = a.USER_FLOW AND dtgi.rec_type_id = 'DeptGrade'
and a.DEPT_FLOW=#{deptFlow}
))

