from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y'
AND RD.WORK_ORG_ID = #{workOrgId}
