and sur.ROLE_FLOW = #{roleFlow}
) AND NOT EXISTS ( SELECT 1 FROM res_doctor rd WHERE su.user_flow = rd.doctor_flow AND rd.RECORD_STATUS = 'Y' )
and su.DEPT_FLOW = #{sysUser.deptFlow}
and su.USER_NAME like #{userName}
