mapper.update:
UPDATE res_doctor_sch_process set record_status='N'
where record_status='Y'
and sch_result_flow=#{resultFlow}

mapper.select:
		select rdsp.PROCESS_FLOW, rdsp.USER_FLOW,su.USER_NAME,rr.REC_CONTENT from RES_DOCTOR_SCH_PROCESS rdsp,RES_REC rr,SYS_USER su
		where rdsp.RECORD_STATUS='Y' and rr.RECORD_STATUS='Y' and su.RECORD_STATUS='Y'
		 and rr.PROCESS_FLOW =rdsp.PROCESS_FLOW
      	 and rr.OPER_USER_FLOW=rdsp.USER_FLOW
      	 and su.USER_FLOW=rdsp.USER_FLOW
		 and rr.rec_type_id ='DeptGrade'

			  and rdsp.DEPT_FLOW=#{deptFlow}


			 and rr.OPER_TIME>=#{startDate}


		 	 and rr.OPER_TIME<=#{endDate}

		select rdsp.PROCESS_FLOW, rdsp.USER_FLOW,su.USER_NAME,rr.REC_CONTENT from RES_DOCTOR_SCH_PROCESS rdsp,RES_REC rr,SYS_USER su
		where rdsp.RECORD_STATUS='Y' and rr.RECORD_STATUS='Y' and su.RECORD_STATUS='Y'
		 and rr.PROCESS_FLOW =rdsp.PROCESS_FLOW
      	 and rr.OPER_USER_FLOW=rdsp.USER_FLOW
      	 and su.USER_FLOW=rdsp.USER_FLOW
		 and rr.rec_type_id ='TeacherGrade'

			  and rdsp.TEACHER_USER_FLOW=#{teacherFlow}


			 and rr.OPER_TIME>=#{startDate}


		 	 and rr.OPER_TIME<=#{endDate}

select count(distinct(a.USER_FLOW)) from ( SELECT SAR.SESSION_NUMBER, SAR.RESULT_FLOW, RDR.SPE_ID, RDR.SPE_NAME, RDR.CAT_SPE_ID, RDR.CAT_SPE_NAME, RDSP.PROCESS_FLOW, RDSP.USER_FLOW, RDSP.ORG_FLOW, RDSP.ORG_NAME, RDSP.DEPT_FLOW, RDSP.DEPT_NAME, RDSP.SCH_DEPT_FLOW, RDSP.SCH_DEPT_NAME, RDSP.SCH_RESULT_FLOW, RDSP.SCH_START_DATE, RDSP.SCH_END_DATE, RDSP.TEACHER_USER_FLOW, tu.user_name TEACHER_USER_NAME, RDSP.HEAD_USER_FLOW, RDSP.HEAD_USER_NAME, RDSP.SCH_FLAG, RDSP.SCH_SCORE, RDSP.IS_CURRENT_FLAG, SU.USER_NAME, SU.USER_HEAD_IMG, RD.DOCTOR_TYPE_ID, nvl((select CFG_VALUE from jsres_power_cfg cfg where cfg.record_status = 'Y' and cfg.cfg_code = 'jsres_doctor_app_menu_' || rd.doctor_flow), 'N') APP_MENU_FLAG, nvl(cfg2.power_start_time, '0000-00-00') power_start_time, nvl(cfg2.power_end_time, '9999-99-99') power_end_time from RES_DOCTOR_SCH_PROCESS rdsp left join sch_arrange_result sar on rdsp.sch_result_flow = sar.result_flow left join res_doctor_recruit rdr on (rdr.DOCTOR_FLOW = sar.DOCTOR_FLOW and rdr.RECORD_STATUS = 'Y' and sar.session_number = rdr.SESSION_NUMBER and rdr.AUDIT_STATUS_ID = 'Passed') left join res_doctor rd on rdsp.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rdsp.user_flow left join sys_user tu on tu.user_flow = rdsp.teacher_user_flow left join jsres_power_cfg cfg2 on cfg2.record_status = 'Y' and cfg2.cfg_code = 'jsres_doctor_data_time_' || rd.doctor_flow where rdsp.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y' and sar.record_status = 'Y' ) a WHERE a.APP_MENU_FLAG = 'Y' AND (a.SCH_START_DATE <= A.power_start_time AND a.SCH_END_DATE >= A.power_END_time OR a.SCH_START_DATE >= A.power_start_time AND a.SCH_START_DATE <= A.power_END_time OR a.SCH_END_DATE >= A.power_start_time AND a.SCH_END_DATE <= A.power_END_time) AND A.DOCTOR_TYPE_ID IN ('Company', 'Social', 'Graduate') and ( exists( select null from SYS_USER_DEPT sud where sud.RECORD_STATUS='Y' and sud.USER_FLOW=#{userFlow} and a.DEPT_FLOW=sud.DEPT_FLOW )
or a.DEPT_FLOW=#{deptFlow}
) and a.SCH_START_DATE <=to_char(sysdate,'yyyy-MM-dd') and a.SCH_END_DATE >=to_char(sysdate,'yyyy-MM-dd')

SELECT DEPT_FLOW,DEPT_NAME,COUNT(DISTINCT a.USER_FLOW) NUM FROM ( SELECT SAR.SESSION_NUMBER, SAR.RESULT_FLOW, RDR.SPE_ID, RDR.SPE_NAME, RDR.CAT_SPE_ID , RDR.CAT_SPE_NAME, RDSP.PROCESS_FLOW, RDSP.USER_FLOW, RDSP.ORG_FLOW, RDSP.ORG_NAME , RDSP.DEPT_FLOW, RDSP.DEPT_NAME, RDSP.SCH_DEPT_FLOW, RDSP.SCH_DEPT_NAME, RDSP.SCH_RESULT_FLOW , RDSP.SCH_START_DATE, RDSP.SCH_END_DATE, RDSP.TEACHER_USER_FLOW, tu.user_name AS TEACHER_USER_NAME, RDSP.HEAD_USER_FLOW , RDSP.HEAD_USER_NAME, RDSP.SCH_FLAG, RDSP.SCH_SCORE, RDSP.IS_CURRENT_FLAG, SU.USER_NAME , SU.USER_HEAD_IMG, RD.DOCTOR_TYPE_ID, nvl(( SELECT CFG_VALUE FROM jsres_power_cfg cfg WHERE cfg.record_status = 'Y' AND cfg.cfg_code = ('jsres_doctor_app_menu_' || rd.doctor_flow) ), 'N') AS APP_MENU_FLAG, nvl(cfg2.power_start_time, '0000-00-00') AS power_start_time, nvl(cfg2.power_end_time, '9999-99-99') AS power_end_time FROM RES_DOCTOR_SCH_PROCESS rdsp LEFT JOIN sch_arrange_result sar ON rdsp.sch_result_flow = sar.result_flow LEFT JOIN res_doctor_recruit rdr ON rdr.DOCTOR_FLOW = sar.DOCTOR_FLOW AND rdr.RECORD_STATUS = 'Y' AND sar.session_number = rdr.SESSION_NUMBER AND rdr.AUDIT_STATUS_ID = 'Passed' LEFT JOIN res_doctor rd ON rdsp.user_flow = rd.doctor_flow LEFT JOIN sys_user su ON su.user_flow = rdsp.user_flow LEFT JOIN sys_user tu ON tu.user_flow = rdsp.teacher_user_flow LEFT JOIN SYS_DEPT SD ON SD.DEPT_FLOW=RDSP.SCH_DEPT_FLOW LEFT JOIN jsres_power_cfg cfg2 ON cfg2.record_status = 'Y' AND cfg2.cfg_code = ('jsres_doctor_data_time_' || rd.doctor_flow) WHERE rdsp.record_status = 'Y' AND rd.record_status = 'Y' AND su.record_status = 'Y' AND tu.record_status = 'Y' AND SAR.RECORD_STATUS = 'Y' AND SD.RECORD_STATUS = 'Y'
AND SD.ORG_FLOW= #{orgFlow}
AND RDSP.SCH_START_DATE >= #{startTime}
and RDSP.SCH_END_DATE <= #{endTime}
) a WHERE a.APP_MENU_FLAG = 'Y' AND (a.SCH_START_DATE <= A.power_start_time AND a.SCH_END_DATE >= A.power_END_time OR a.SCH_START_DATE >= A.power_start_time AND a.SCH_START_DATE <= A.power_END_time OR a.SCH_END_DATE >= A.power_start_time AND a.SCH_END_DATE <= A.power_END_time) AND A.DOCTOR_TYPE_ID IN ('Company', 'Social', 'Graduate') AND (EXISTS (SELECT NULL FROM SYS_USER_DEPT sud WHERE sud.RECORD_STATUS = 'Y' AND a.DEPT_FLOW = sud.DEPT_FLOW))
AND A.DEPT_FLOW= #{deptFlow}
GROUP BY DEPT_FLOW, DEPT_NAME

select count(distinct(a.USER_FLOW)) from ( SELECT SAR.SESSION_NUMBER, SAR.RESULT_FLOW, RDR.SPE_ID, RDR.SPE_NAME, RDR.CAT_SPE_ID, RDR.CAT_SPE_NAME, RDSP.PROCESS_FLOW, RDSP.USER_FLOW, RDSP.ORG_FLOW, RDSP.ORG_NAME, RDSP.DEPT_FLOW, RDSP.DEPT_NAME, RDSP.SCH_DEPT_FLOW, RDSP.SCH_DEPT_NAME, RDSP.SCH_RESULT_FLOW, RDSP.SCH_START_DATE, RDSP.SCH_END_DATE, RDSP.TEACHER_USER_FLOW, tu.user_name TEACHER_USER_NAME, RDSP.HEAD_USER_FLOW, RDSP.HEAD_USER_NAME, RDSP.SCH_FLAG, RDSP.SCH_SCORE, RDSP.IS_CURRENT_FLAG, SU.USER_NAME, SU.USER_HEAD_IMG, RD.DOCTOR_TYPE_ID, nvl((select CFG_VALUE from jsres_power_cfg cfg where cfg.record_status = 'Y' and cfg.cfg_code = 'jsres_doctor_app_menu_' || rd.doctor_flow), 'N') APP_MENU_FLAG, nvl(cfg2.power_start_time, '0000-00-00') power_start_time, nvl(cfg2.power_end_time, '9999-99-99') power_end_time from RES_DOCTOR_SCH_PROCESS rdsp left join sch_arrange_result sar on rdsp.sch_result_flow = sar.result_flow left join res_doctor_recruit rdr on (rdr.DOCTOR_FLOW = sar.DOCTOR_FLOW and rdr.RECORD_STATUS = 'Y' and sar.session_number = rdr.SESSION_NUMBER and rdr.AUDIT_STATUS_ID = 'Passed') left join res_doctor rd on rdsp.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rdsp.user_flow left join sys_user tu on tu.user_flow = rdsp.teacher_user_flow left join jsres_power_cfg cfg2 on cfg2.record_status = 'Y' and cfg2.cfg_code = 'jsres_doctor_data_time_' || rd.doctor_flow where rdsp.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y' and sar.record_status = 'Y' ) a WHERE a.APP_MENU_FLAG = 'Y' AND (a.SCH_START_DATE <= A.power_start_time AND a.SCH_END_DATE >= A.power_END_time OR a.SCH_START_DATE >= A.power_start_time AND a.SCH_START_DATE <= A.power_END_time OR a.SCH_END_DATE >= A.power_start_time AND a.SCH_END_DATE <= A.power_END_time) AND A.DOCTOR_TYPE_ID IN ('Company', 'Social', 'Graduate') and ( exists( select null from SYS_USER_DEPT sud where sud.RECORD_STATUS='Y' and sud.USER_FLOW=#{userFlow} and a.DEPT_FLOW=sud.DEPT_FLOW )
or a.DEPT_FLOW=#{deptFlow}
) and TO_CHAR(SYSDATE, 'yyyy-mm-dd') >= TO_CHAR(TO_DATE(a.SCH_END_DATE,'yyyy-mm-dd')-7,'yyyy-mm-dd') and TO_CHAR(SYSDATE, 'yyyy-mm-dd') <=TO_CHAR(TO_DATE(a.SCH_END_DATE,'yyyy-mm-dd'),'yyyy-mm-dd')

select count(distinct(a.USER_FLOW)) from ( SELECT SAR.SESSION_NUMBER, SAR.RESULT_FLOW, RDR.SPE_ID, RDR.SPE_NAME, RDR.CAT_SPE_ID, RDR.CAT_SPE_NAME, RDSP.PROCESS_FLOW, RDSP.USER_FLOW, RDSP.ORG_FLOW, RDSP.ORG_NAME, RDSP.DEPT_FLOW, RDSP.DEPT_NAME, RDSP.SCH_DEPT_FLOW, RDSP.SCH_DEPT_NAME, RDSP.SCH_RESULT_FLOW, RDSP.SCH_START_DATE, RDSP.SCH_END_DATE, RDSP.TEACHER_USER_FLOW, tu.user_name TEACHER_USER_NAME, RDSP.HEAD_USER_FLOW, RDSP.HEAD_USER_NAME, RDSP.SCH_FLAG, RDSP.SCH_SCORE, RDSP.IS_CURRENT_FLAG, SU.USER_NAME, SU.USER_HEAD_IMG, RD.DOCTOR_TYPE_ID, nvl((select CFG_VALUE from jsres_power_cfg cfg where cfg.record_status = 'Y' and cfg.cfg_code = 'jsres_doctor_app_menu_' || rd.doctor_flow), 'N') APP_MENU_FLAG, nvl(cfg2.power_start_time, '0000-00-00') power_start_time, nvl(cfg2.power_end_time, '9999-99-99') power_end_time from RES_DOCTOR_SCH_PROCESS rdsp left join sch_arrange_result sar on rdsp.sch_result_flow = sar.result_flow left join res_doctor_recruit rdr on (rdr.DOCTOR_FLOW = sar.DOCTOR_FLOW and rdr.RECORD_STATUS = 'Y' and sar.session_number = rdr.SESSION_NUMBER and rdr.AUDIT_STATUS_ID = 'Passed') left join res_doctor rd on rdsp.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rdsp.user_flow left join sys_user tu on tu.user_flow = rdsp.teacher_user_flow left join jsres_power_cfg cfg2 on cfg2.record_status = 'Y' and cfg2.cfg_code = 'jsres_doctor_data_time_' || rd.doctor_flow where rdsp.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y' and sar.record_status = 'Y' ) a WHERE a.APP_MENU_FLAG = 'Y' AND (a.SCH_START_DATE <= A.power_start_time AND a.SCH_END_DATE >= A.power_END_time OR a.SCH_START_DATE >= A.power_start_time AND a.SCH_START_DATE <= A.power_END_time OR a.SCH_END_DATE >= A.power_start_time AND a.SCH_END_DATE <= A.power_END_time) AND A.DOCTOR_TYPE_ID IN ('Company', 'Social', 'Graduate') and ( exists( select null from SYS_USER_DEPT sud where sud.RECORD_STATUS='Y' and sud.USER_FLOW=#{userFlow} and a.DEPT_FLOW=sud.DEPT_FLOW )
or a.DEPT_FLOW=#{deptFlow}
)

SELECT USER_FLOW,COUNT(*) PROCESS_COUNT FROM PDSCI.RES_DOCTOR_SCH_PROCESS WHERE USER_FLOW IN
#{userFlow}
AND RECORD_STATUS = 'Y' GROUP BY USER_FLOW

select A.* from (select p.PROCESS_FLOW, p.USER_FLOW USER_FLOW, P.ORG_FLOW ORG_FLOW, P.ORG_NAME ORG_NAME, P.DEPT_FLOW DEPT_FLOW, P.DEPT_NAME, P.SCH_DEPT_FLOW, P.SCH_DEPT_NAME, P.SCH_RESULT_FLOW, P.SCH_START_DATE SCH_START_DATE, P.SCH_END_DATE SCH_END_DATE, P.TEACHER_USER_FLOW TEACHER_USER_FLOW, P.TEACHER_USER_NAME, P.HEAD_USER_FLOW, P.HEAD_USER_NAME, P.SCH_FLAG, P.SCH_SCORE, P.IS_CURRENT_FLAG, su.USER_Name USER_Name, su.SEX_NAME, su.USER_PHONE, RD.DOCTOR_TYPE_ID, nvl((select CFG_VALUE from sys_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jswjw_'||rd.org_flow||'_P001' ),'N') STU_JD_GC, nvl((select CFG_VALUE from sys_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jswjw_sendSchool_'||rd.Work_Org_Id||'_P007' AND RD.DOCTOR_TYPE_ID='Graduate' ),'Y') STU_SEND_SCHOOL_GC, nvl((select CFG_VALUE from sys_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jswjw_'||rd.org_flow||'_P008' ),'N') STU_JDZS_GC, nvl((select CFG_VALUE from sys_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jswjw_'||TU.org_flow||'_P001' ),'N') TEA_JD_GC, nvl((select CFG_VALUE from sys_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jswjw_'||TU.org_flow||'_P006' ),'N') TEA_APP from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y') A WHERE ( (A.DOCTOR_TYPE_ID !='Graduate' AND A.STU_JD_GC='Y') OR (A.DOCTOR_TYPE_ID ='Graduate' AND A.STU_SEND_SCHOOL_GC='Y') OR (A.DOCTOR_TYPE_ID ='Graduate' AND A.STU_SEND_SCHOOL_GC='N' AND A.STU_JD_GC='Y' AND A.STU_JDZS_GC='Y') )
and a.SCH_START_DATE >= #{schStartDate}
and a.SCH_END_DATE <= #{schEndDate}
and a.USER_NAME like #{name}
and ( exists( select null from SYS_USER_DEPT sud where sud.RECORD_STATUS='Y' and sud.USER_FLOW=#{userFlow} and a.DEPT_FLOW=sud.DEPT_FLOW )
or a.DEPT_FLOW=#{deptFlow}
) ORDER BY a.SCH_START_DATE DESC nulls last

select A.* from (select p.PROCESS_FLOW, p.USER_FLOW, p.ORG_FLOW, p.ORG_NAME, p.DEPT_FLOW, p.DEPT_NAME, p.SCH_DEPT_FLOW, p.SCH_DEPT_NAME, p.SCH_RESULT_FLOW, p.SCH_START_DATE, p.SCH_END_DATE, p.TEACHER_USER_FLOW, p.TEACHER_USER_NAME, p.HEAD_USER_FLOW, p.HEAD_USER_NAME, p.SCH_FLAG, p.SCH_SCORE, p.IS_CURRENT_FLAG , su.USER_Name, su.SEX_NAME, su.USER_PHONE, RD.DOCTOR_TYPE_ID, nvl((select CFG_VALUE from sys_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jswjw_'||rd.org_flow||'_P001' ),'N') STU_JD_GC, nvl((select CFG_VALUE from sys_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jswjw_sendSchool_'||rd.Work_Org_Id||'_P007' AND RD.DOCTOR_TYPE_ID='Graduate' ),'Y') STU_SEND_SCHOOL_GC, nvl((select CFG_VALUE from sys_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jswjw_'||rd.org_flow||'_P008' ),'N') STU_JDZS_GC, nvl((select CFG_VALUE from sys_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jswjw_'||TU.org_flow||'_P001' ),'N') TEA_JD_GC, nvl((select CFG_VALUE from sys_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jswjw_'||TU.org_flow||'_P006' ),'N') TEA_APP from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y') A WHERE ( (A.DOCTOR_TYPE_ID !='Graduate' AND A.STU_JD_GC='Y') OR (A.DOCTOR_TYPE_ID ='Graduate' AND A.STU_SEND_SCHOOL_GC='Y') OR (A.DOCTOR_TYPE_ID ='Graduate' AND A.STU_SEND_SCHOOL_GC='N' AND A.STU_JD_GC='Y' AND A.STU_JDZS_GC='Y') )
and a.TEACHER_USER_FLOW = #{teacherUserFlow}
and a.SCH_START_DATE >= #{schStartDate}
and a.SCH_END_DATE <= #{schEndDate}
and a.user_Name like #{name}
and ( exists( select null from res_rec rr where rr.RECORD_STATUS='Y' and rr.PROCESS_FLOW = a.PROCESS_FLOW and rr.AUDIT_STATUS_ID is null and rr.rec_type_id in ('CaseRegistry','DiseaseRegistry','SkillRegistry','OperationRegistry','CampaignRegistry') ) or not exists( select null from RES_SCH_PROCESS_EXPRESS rr where rr.RECORD_STATUS='Y' and rr.PROCESS_FLOW = a.PROCESS_FLOW and rr.rec_type_id = 'AfterEvaluation' ) )
ORDER BY a.process_flow DESC nulls last

SELECT RDSP.*
FROM PDSCI.RES_DOCTOR_SCH_PROCESS RDSP
left join PDSCI.RES_DOCTOR RD
on RDSP.USER_FLOW = RD.DOCTOR_FLOW
left join RES_POWER_CFG RPC
on 'res_doctor_web_'||RDSP.USER_FLOW = RPC.CFG_CODE
left join sch_arrange_result sar on sar.result_flow = rdsp.sch_result_flow
left join sch_rotation_dept srd on srd.standard_dept_id = sar.standard_dept_id
and sar.STANDARD_GROUP_FLOW = srd.group_flow
and srd.org_Flow IS NULL
and srd.RECORD_STATUS = 'Y'
WHERE RDSP.RECORD_STATUS = 'Y'
AND RD.RECORD_STATUS = 'Y'
AND sar.RECORD_STATUS = 'Y'
and rd.org_Flow is not null

	and srd.Practic_Or_Theory = #{basicPractice}



		AND RD.DOCTOR_NAME LIKE '%${doctor.doctorName}%'


		AND RD.TRAINING_SPE_ID = #{doctor.trainingSpeId}


		AND RD.SESSION_NUMBER = #{doctor.sessionNumber}




		AND RDSP.TEACHER_USER_FLOW = #{process.teacherUserFlow}


		AND RDSP.HEAD_USER_FLOW = #{process.headUserFlow}


		AND RDSP.IS_CURRENT_FLAG = #{process.isCurrentFlag}


		AND RDSP.START_DATE >= #{process.startDate}


		AND RDSP.START_DATE <= #{process.endDate}


		AND RDSP.SCH_DEPT_FLOW = #{process.schDeptFlow}




	AND RPC.CFG_VALUE = 'Y'
	AND RPC.RECORD_STATUS = 'Y'
	AND
	(
	(RPC.POWER_START_TIME BETWEEN RDSP.SCH_START_DATE AND RDSP.SCH_END_DATE)
	OR
	(RPC.POWER_END_TIME BETWEEN RDSP.SCH_START_DATE AND RDSP.SCH_END_DATE)
	OR
	(RDSP.SCH_START_DATE >= RPC.POWER_START_TIME AND RDSP.SCH_END_DATE <= RPC.POWER_END_TIME)
	)





			AND ( RDSP.SCH_DEPT_FLOW IN (
			SELECT SCH_DEPT_FLOW
			FROM PDSCI.SCH_DEPT
			WHERE RECORD_STATUS = 'Y'
			AND DEPT_FLOW IN (
			SELECT DEPT_FLOW
			FROM SYS_USER_DEPT
			WHERE RECORD_STATUS = 'Y'
			AND USER_FLOW = #{roleFlagMap.val}
			)
			) or rdsp.head_user_flow=#{roleFlagMap.val} )



				and rd.training_spe_id=#{trainingSpeId}

			AND RD.ORG_FLOW IN (
			SELECT ORG_FLOW
			FROM PDSCI.SYS_USER
			WHERE USER_FLOW = #{roleFlagMap.val}
			)


			AND RDSP.ORG_FLOW = #{roleFlagMap.val}



				AND RDSP.ORG_FLOW = #{roleFlagMap.val}




ORDER by RD.session_Number DESC,RD.TRAINING_SPE_ID desc,rd.doctor_flow

SELECT RDSP.* FROM PDSCI.RES_DOCTOR_SCH_PROCESS RDSP left join PDSCI.RES_DOCTOR RD on RDSP.USER_FLOW = RD.DOCTOR_FLOW left join RES_POWER_CFG RPC on 'res_doctor_web_'||RDSP.USER_FLOW = RPC.CFG_CODE left join sch_arrange_result sar on sar.result_flow = rdsp.sch_result_flow left join sch_rotation_dept srd on srd.standard_dept_id = sar.standard_dept_id and sar.STANDARD_GROUP_FLOW = srd.group_flow and srd.org_Flow IS NULL and srd.RECORD_STATUS = 'Y' WHERE RDSP.RECORD_STATUS = 'Y' AND RD.RECORD_STATUS = 'Y' AND sar.RECORD_STATUS = 'Y' and rd.org_Flow is not null
and rd.DOCTOR_TYPE_ID in #{item}
and srd.Practic_Or_Theory = #{basicPractice}
AND RD.DOCTOR_NAME LIKE '%${doctor.doctorName}%'
AND RD.TRAINING_SPE_ID = #{doctor.trainingSpeId}
AND RD.SESSION_NUMBER = #{doctor.sessionNumber}
AND RDSP.TEACHER_USER_FLOW = #{process.teacherUserFlow}
AND RDSP.HEAD_USER_FLOW = #{process.headUserFlow}
AND RDSP.IS_CURRENT_FLAG = #{process.isCurrentFlag}
AND RDSP.START_DATE >= #{process.startDate}
AND RDSP.START_DATE <= #{process.endDate}
AND RDSP.SCH_DEPT_FLOW = #{process.schDeptFlow}
AND RPC.CFG_VALUE = 'Y' AND RPC.RECORD_STATUS = 'Y' AND ( (RPC.POWER_START_TIME BETWEEN RDSP.SCH_START_DATE AND RDSP.SCH_END_DATE) OR (RPC.POWER_END_TIME BETWEEN RDSP.SCH_START_DATE AND RDSP.SCH_END_DATE) OR (RDSP.SCH_START_DATE >= RPC.POWER_START_TIME AND RDSP.SCH_END_DATE <= RPC.POWER_END_TIME) )
AND ( RDSP.SCH_DEPT_FLOW IN ( SELECT SCH_DEPT_FLOW FROM PDSCI.SCH_DEPT WHERE RECORD_STATUS = 'Y' AND DEPT_FLOW IN ( SELECT DEPT_FLOW FROM SYS_USER_DEPT WHERE RECORD_STATUS = 'Y' AND USER_FLOW = #{roleFlagMap.val} ) ) or rdsp.head_user_flow=#{roleFlagMap.val} )
AND RDSP.SCH_DEPT_FLOW IN ( SELECT SCH_DEPT_FLOW FROM PDSCI.SCH_DEPT WHERE RECORD_STATUS = 'Y' AND DEPT_FLOW IN ( SELECT DEPT_FLOW FROM SYS_USER_DEPT WHERE RECORD_STATUS = 'Y' AND USER_FLOW = #{roleFlagMap.val} ) )
and rd.training_spe_id=#{trainingSpeId}
AND RD.ORG_FLOW IN ( SELECT ORG_FLOW FROM PDSCI.SYS_USER WHERE USER_FLOW = #{roleFlagMap.val} )
and rd.training_spe_id=#{roleFlagMap.trainingSpeId}
AND RD.ORG_FLOW IN ( SELECT ORG_FLOW FROM PDSCI.SYS_USER WHERE USER_FLOW = #{roleFlagMap.val} )
AND RDSP.ORG_FLOW = #{roleFlagMap.val}
AND RDSP.ORG_FLOW = #{roleFlagMap.val}
and (
not exists( SELECT 1 FROM Res_Sch_Process_Express a WHERE a.RECORD_STATUS = 'Y' AND a.REC_TYPE_ID =#{item} AND a.PROCESS_FLOW = rdsp.process_flow )
or
exists ( select 1 FROM Res_Sch_Process_Express a WHERE a.RECORD_STATUS = 'Y' AND a.REC_TYPE_ID IN
#{item}
AND a.PROCESS_FLOW = rdsp.process_flow and a.AUDIT_STATUS_ID is null )
or
not exists ( select 1 FROM Res_Sch_Process_Express a WHERE a.RECORD_STATUS = 'Y' AND a.REC_TYPE_ID IN
#{item}
AND a.PROCESS_FLOW = rdsp.process_flow )
)
and exists( SELECT 1 FROM Res_Sch_Process_Express a WHERE a.RECORD_STATUS = 'Y'
AND a.REC_TYPE_ID IN
#{item}
AND a.PROCESS_FLOW = rdsp.process_flow and a.AUDIT_STATUS_ID is not null AND a.HEAD_AUDIT_STATUS_ID IS NULL )
and exists( SELECT 1 FROM Res_Sch_Process_Express a WHERE a.RECORD_STATUS = 'Y'
AND a.REC_TYPE_ID IN
#{item}
AND a.PROCESS_FLOW = rdsp.process_flow and a.AUDIT_STATUS_ID is not null AND a.HEAD_AUDIT_STATUS_ID IS NULL )
and exists( SELECT 1 FROM Res_Sch_Process_Express a WHERE a.RECORD_STATUS = 'Y'
AND a.REC_TYPE_ID IN
#{item}
AND a.PROCESS_FLOW = rdsp.process_flow and a.AUDIT_STATUS_ID is not null AND a.HEAD_AUDIT_STATUS_ID IS not NULL AND a.manager_AUDIT_STATUS_ID IS NULL )
and exists( SELECT 1 FROM Res_Sch_Process_Express a WHERE a.RECORD_STATUS = 'Y'
AND a.REC_TYPE_ID IN
#{item}
AND a.PROCESS_FLOW = rdsp.process_flow and a.AUDIT_STATUS_ID is not null AND a.HEAD_AUDIT_STATUS_ID IS not NULL AND a.manager_AUDIT_STATUS_ID IS NULL )
and exists( SELECT 1 FROM Res_Sch_Process_Express a WHERE a.RECORD_STATUS = 'Y'
AND a.REC_TYPE_ID IN
#{item}
AND a.PROCESS_FLOW = rdsp.process_flow and a.AUDIT_STATUS_ID is not null AND a.HEAD_AUDIT_STATUS_ID IS not NULL AND a.manager_AUDIT_STATUS_ID IS not NULL AND a.admin_AUDIT_STATUS_ID IS NULL )
and exists( SELECT null FROM RES_REC a WHERE a.RECORD_STATUS = 'Y' AND a.OPER_USER_FLOW=rdsp.user_flow AND a.PROCESS_FLOW = rdsp.process_flow and a.sch_dept_flow=rdsp.sch_dept_flow
AND a.REC_TYPE_ID IN
#{item}
AND a.AUDIT_STATUS_ID IS NULL
AND a.HEAD_AUDIT_STATUS_ID IS NULL
)
ORDER by rdsp.sch_start_date desc nulls last ,RD.session_Number DESC,RD.TRAINING_SPE_ID desc,rd.doctor_flow

select sar.sch_Month,rdsp.* from RES_DOCTOR_SCH_PROCESS rdsp left join SCH_ARRANGE_RESULT sar on rdsp.sch_result_flow = sar.result_flow and rdsp.user_flow = sar.doctor_flow and rdsp.dept_flow = sar.dept_flow and rdsp.dept_flow = sar.dept_flow where RDSP.RECORD_STATUS = 'Y'and sar.record_status ='Y'
and sar.doctor_flow = #{doctorFlow}
and sar.dept_flow = #{deptFlow}

SELECT rd.*
FROM res_doctor rd
left JOIN sys_user su ON rd.DOCTOR_FLOW = su.USER_FLOW
left join RES_POWER_CFG RPC
on 'res_doctor_web_'||su.USER_FLOW = RPC.CFG_CODE
where su.RECORD_STATUS = 'Y'
AND rd.RECORD_STATUS = 'Y'

	and rd.session_number is not null


	and  doctor_Type_Id in

		#{item}



	AND rd.GRADUATION_YEAR = #{doctor.graduationYear}



		AND RD.DOCTOR_NAME LIKE '%${doctor.doctorName}%'


		AND RD.DOCTOR_CATEGORY_ID = #{doctor.doctorCategoryId}


		AND RD.TRAINING_SPE_ID = #{doctor.trainingSpeId}


		AND RD.SESSION_NUMBER = #{doctor.sessionNumber}


		AND RD.work_Org_Id = #{doctor.workOrgId}



	AND RPC.CFG_VALUE = 'Y'
	AND RPC.RECORD_STATUS = 'Y'




		and instr('${roleFlagMap.medicineTypeId}',su.medicine_Type_Id)>0



			AND exists(
				select 1 from SCH_ARRANGE_RESULT a
				left join	res_doctor_sch_process rdsp
				on a.result_flow=rdsp.sch_result_flow
				and rdsp.record_status='Y'
				where a.doctor_flow=su.user_flow
				and a.record_status='Y'

					AND (
						EXISTS (SELECT NULL
						FROM SYS_USER_DEPT sud
						WHERE sud.RECORD_STATUS = 'Y'
						AND sud.USER_FLOW = #{roleFlagMap.val}
						AND a.DEPT_FLOW = sud.DEPT_FLOW)

							OR	a.DEPT_FLOW = #{roleFlagMap.deptFlow}

					)


					AND RDSP.TEACHER_USER_FLOW = #{roleFlagMap.val}



						AND RDSP.DEPT_FLOW = #{process.deptFlow}


						AND RDSP.TEACHER_USER_FLOW = #{process.teacherUserFlow}


						AND RDSP.HEAD_USER_FLOW = #{process.headUserFlow}


						AND RDSP.IS_CURRENT_FLAG = #{process.isCurrentFlag}


						AND RDSP.START_DATE >= #{process.startDate}


						AND RDSP.START_DATE <= #{process.endDate}


						AND RDSP.SCH_DEPT_FLOW = #{process.schDeptFlow}



					AND
					(
					(RPC.POWER_START_TIME BETWEEN a.SCH_START_DATE AND a.SCH_END_DATE)
					OR
					(RPC.POWER_END_TIME BETWEEN a.SCH_START_DATE AND a.SCH_END_DATE)
					OR
					(a.SCH_START_DATE >= RPC.POWER_START_TIME AND a.SCH_END_DATE <= RPC.POWER_END_TIME)
					)

			)



				and rd.training_spe_id=#{trainingSpeId}

			AND RD.ORG_FLOW IN (
				SELECT ORG_FLOW
				FROM PDSCI.SYS_USER
				WHERE USER_FLOW = #{roleFlagMap.val}
			)


			AND rd.ORG_FLOW = #{roleFlagMap.val}



				AND rd.ORG_FLOW = #{roleFlagMap.val}






		AND exists(
		select 1 from SCH_ARRANGE_RESULT a
		left join res_doctor_sch_process rdsp
		on a.result_flow=rdsp.sch_result_flow
		and rdsp.record_status='Y'
		where a.doctor_flow=su.user_flow
		and a.record_status='Y'
		AND RDSP.IS_CURRENT_FLAG = #{process.isCurrentFlag}
		)


AND (rd.DOCTOR_STATUS_ID NOT IN ('UnPassed', 'Passed', 'UnSubmit')
OR rd.DOCTOR_STATUS_ID IS NULL)
AND rd.org_flow IS NOT NULL
AND rd.org_flow != '20160914888888'
AND rd.org_flow != '566c9f36cf014f63a569b453efa284d7'
AND rd.org_flow != '00000000000000000000000000000000'

ORDER BY nlssort(su.user_name, 'NLS_SORT=SCHINESE_PINYIN_M'), rd.session_number DESC, su.user_code, rd.CREATE_TIME

	SELECT RDSP.*
	FROM PDSCI.RES_DOCTOR_SCH_PROCESS RDSP,PDSCI.RES_DOCTOR RD
	WHERE RDSP.RECORD_STATUS = 'Y'
	AND RD.RECORD_STATUS = 'Y'
	AND RDSP.USER_FLOW = RD.DOCTOR_FLOW


			AND RD.DOCTOR_NAME LIKE '%${doctor.doctorName}%'


			AND RD.TRAINING_SPE_ID = #{doctor.trainingSpeId}


			AND RD.SESSION_NUMBER = #{doctor.sessionNumber}




			AND RDSP.TEACHER_USER_FLOW = #{process.teacherUserFlow}


			AND RDSP.HEAD_USER_FLOW = #{process.headUserFlow}


			AND RDSP.IS_CURRENT_FLAG = #{process.isCurrentFlag}


			AND RDSP.START_DATE >= #{process.startDate}


			AND RDSP.START_DATE <= #{process.endDate}


			AND RDSP.SCH_DEPT_FLOW = #{process.schDeptFlow}





				AND ( RDSP.SCH_DEPT_FLOW IN (
					SELECT SCH_DEPT_FLOW 
					FROM PDSCI.SCH_DEPT
					WHERE RECORD_STATUS = 'Y'
					AND DEPT_FLOW IN (
						SELECT DEPT_FLOW 
						FROM SYS_USER_DEPT 
						WHERE RECORD_STATUS = 'Y'
						AND USER_FLOW = #{roleFlagMap.val}
					)
				) or rdsp.head_user_flow=#{roleFlagMap.val} )



					and rd.training_spe_id=#{trainingSpeId}

				AND RD.ORG_FLOW IN (
    				SELECT ORG_FLOW
    				FROM PDSCI.SYS_USER
    				WHERE USER_FLOW = #{roleFlagMap.val}
    			)


				AND RDSP.ORG_FLOW = #{roleFlagMap.val}



					AND RDSP.ORG_FLOW = #{roleFlagMap.val}




	ORDER by RD.session_Number DESC,RD.TRAINING_SPE_ID desc

SELECT RDSP.*
