SELECT DISTINCT *
FROM RES_DOCTOR_SCH_PROCESS RDSP
LEFT JOIN sys_user su on rdsp.user_flow=su.user_flow
WHERE RDSP.RECORD_STATUS = 'Y' and su.RECORD_STATUS='Y'
and rdsp.teacher_User_Flow = #{userFlow}
AND RDSP.IS_CURRENT_FLAG = #{isCurrentFlag}

SELECT RDSP.* FROM PDSCI.RES_DOCTOR_SCH_PROCESS RDSP LEFT JOIN RES_POWER_CFG RPC ON 'res_doctor_web_'||RDSP.USER_FLOW = RPC.CFG_CODE left join sys_user su on su.user_flow=rdsp.user_flow left join res_doctor rd on rd.doctor_flow=rdsp.user_flow WHERE RDSP.RECORD_STATUS = 'Y' and su.RECORD_STATUS = 'Y' and rd.RECORD_STATUS = 'Y'
AND RDSP.IS_CURRENT_FLAG = #{isCurrentFlag}
AND su.user_name like '%${doctorName}%'
AND rd.training_Spe_Id = #{trainingSpeId}
AND rd.session_Number = #{sessionNumber}
AND rd.training_Years = #{trainingYears}
AND ( rd.work_org_name like '%${workDeptName}%' or rd.DEPART_MENT_NAME like '%${workDeptName}%' )
and rd.DOCTOR_TYPE_ID in #{item}
and( (#{startDate}>=rdsp.SCH_START_DATE and rdsp.SCH_END_DATE>=#{startDate}) or (rdsp.SCH_START_DATE>=#{startDate} and #{endDate}>=rdsp.SCH_START_DATE) )
AND RPC.CFG_VALUE = 'Y' AND RPC.RECORD_STATUS = 'Y' AND ( (RPC.POWER_START_TIME BETWEEN RDSP.SCH_START_DATE AND RDSP.SCH_END_DATE) OR (RPC.POWER_END_TIME BETWEEN RDSP.SCH_START_DATE AND RDSP.SCH_END_DATE) OR (RDSP.SCH_START_DATE >= RPC.POWER_START_TIME AND RDSP.SCH_END_DATE <= RPC.POWER_END_TIME) )
AND ( RDSP.SCH_DEPT_FLOW IN ( SELECT SCH_DEPT_FLOW FROM PDSCI.SCH_DEPT WHERE RECORD_STATUS = 'Y' AND DEPT_FLOW IN ( SELECT DEPT_FLOW FROM SYS_USER_DEPT WHERE RECORD_STATUS = 'Y' AND USER_FLOW =#{userFlow} ) ) or rdsp.head_user_flow=#{userFlow} )

SELECT a.* FROM PDSCI.SCH_ARRANGE_RESULT a left join res_doctor rd on a.doctor_flow=rd.doctor_flow left join sys_user su on a.doctor_flow=su.user_flow LEFT JOIN RES_POWER_CFG RPC ON 'res_doctor_web_'||su.USER_FLOW = RPC.CFG_CODE left join res_doctor_sch_process rdsp on rdsp.sch_result_flow=a.result_flow and rdsp.record_status='Y' WHERE a.RECORD_STATUS = 'Y' and rd.record_status='Y' and su.record_status='Y'
AND RDSP.IS_CURRENT_FLAG = #{isCurrentFlag}
AND su.user_name like '%${doctorName}%'
AND rd.training_Spe_Id = #{trainingSpeId}
AND rd.session_Number = #{sessionNumber}
AND rd.training_Years = #{trainingYears}
AND ( rd.work_org_name like '%${workDeptName}%' or rd.DEPART_MENT_NAME like '%${workDeptName}%' )
and rd.DOCTOR_TYPE_ID in #{item}
and( (#{startDate}>=rdsp.SCH_START_DATE and rdsp.SCH_END_DATE>=#{startDate}) or (rdsp.SCH_START_DATE>=#{startDate} and #{endDate}>=rdsp.SCH_START_DATE) )
AND RPC.CFG_VALUE = 'Y' AND RPC.RECORD_STATUS = 'Y' AND ( (RPC.POWER_START_TIME BETWEEN a.SCH_START_DATE AND a.SCH_END_DATE) OR (RPC.POWER_END_TIME BETWEEN a.SCH_START_DATE AND a.SCH_END_DATE) OR (a.SCH_START_DATE >= RPC.POWER_START_TIME AND a.SCH_END_DATE <= RPC.POWER_END_TIME) )
AND ( EXISTS (SELECT NULL FROM SYS_USER_DEPT sud WHERE sud.RECORD_STATUS = 'Y' AND sud.USER_FLOW =#{userFlow} AND a.DEPT_FLOW = sud.DEPT_FLOW)
OR a.DEPT_FLOW = #{deptFlow}
) ORDER BY rd.session_number,rd.training_spe_id,rd.doctor_flow,a.SCH_START_DATE

SELECT SAR.SCH_DEPT_NAME,SAR.RESULT_FLOW,RDSP.PROCESS_FLOW, SAR.SCH_DEPT_FLOW,SAR.SCH_START_DATE,SAR.SCH_END_DATE,RDSP.START_DATE,RDSP.END_DATE, RDSP.IS_CURRENT_FLAG,RDSP.SCH_FLAG,SU.USER_FLOW,SU.SEX_NAME,SU.USER_NAME,SU.USER_HEAD_IMG, RD.DOCTOR_CATEGORY_NAME,RD.DOCTOR_TYPE_NAME,RD.TRAINING_YEARS,RD.TRAINING_SPE_NAME,RD.SESSION_NUMBER, RD.ORG_FLOW,RDSP.SCH_SCORE FROM SCH_ARRANGE_RESULT SAR LEFT JOIN RES_DOCTOR_SCH_PROCESS RDSP ON RDSP.SCH_RESULT_FLOW=SAR.RESULT_FLOW left join sys_user su on su.user_flow=rdsp.user_flow left join res_doctor rd on rd.doctor_flow=rdsp.user_flow LEFT JOIN RES_POWER_CFG RPC ON 'res_doctor_web_'||SU.USER_FLOW = RPC.CFG_CODE WHERE RDSP.RECORD_STATUS = 'Y' and su.RECORD_STATUS = 'Y' and rd.RECORD_STATUS = 'Y' and (rdsp.SCH_FLAG='Y' OR IS_CURRENT_FLAG='Y')
AND RDSP.IS_CURRENT_FLAG = #{isCurrentFlag}
AND su.user_name like '%${doctorName}%'
AND rd.training_Spe_Id = #{trainingSpeId}
AND rd.session_Number = #{sessionNumber}
AND rd.training_Years = #{trainingYears}
and rd.DOCTOR_TYPE_ID in #{item}
and( (#{startDate}>=rdsp.SCH_START_DATE and rdsp.SCH_END_DATE>=#{startDate}) or (rdsp.SCH_START_DATE>=#{startDate} and #{endDate}>=rdsp.SCH_START_DATE) )
AND RPC.CFG_VALUE = 'Y' AND RPC.RECORD_STATUS = 'Y' AND ( (RPC.POWER_START_TIME BETWEEN RDSP.SCH_START_DATE AND RDSP.SCH_END_DATE) OR (RPC.POWER_END_TIME BETWEEN RDSP.SCH_START_DATE AND RDSP.SCH_END_DATE) OR (RDSP.SCH_START_DATE >= RPC.POWER_START_TIME AND RDSP.SCH_END_DATE <= RPC.POWER_END_TIME) )
AND EXISTS ( SELECT NULL FROM SYS_USER_DEPT SUD, PDSCI.SCH_DEPT SD WHERE SUD.RECORD_STATUS = 'Y' AND SD.RECORD_STATUS = 'Y' AND SUD.DEPT_FLOW = SD.DEPT_FLOW AND SUD.USER_FLOW = #{userFlow} AND SD.SCH_DEPT_FLOW = SAR.SCH_DEPT_FLOW )
AND RDSP.TEACHER_USER_FLOW=#{userFlow}
order by SAR.SCH_START_DATE desc nulls last,SAR.RESULT_FLOW

SELECT RDSP.* FROM PDSCI.RES_DOCTOR_SCH_PROCESS RDSP,PDSCI.RES_DOCTOR RD,PDSCI.RES_USER_SPE RUS WHERE RDSP.RECORD_STATUS = 'Y' AND RD.RECORD_STATUS = 'Y' AND RUS.RECORD_STATUS = 'Y' AND RDSP.USER_FLOW = RD.DOCTOR_FLOW AND RD.TRAINING_SPE_ID = RUS.TRAINING_SPE_ID
AND RDSP.IS_CURRENT_FLAG = #{isCurrentFlag}
AND RUS.USER_FLOW = #{userFlow} AND RD.ORG_FLOW IN ( SELECT ORG_FLOW FROM PDSCI.SYS_USER WHERE RECORD_STATUS = 'Y' AND USER_FLOW = #{userFlow} )

SELECT rd.DOCTOR_FLOW, rdsp.SCH_DEPT_NAME, rdsp.TEACHER_USER_FLOW, rdsp.TEACHER_USER_NAME, rdsp.SCH_DEPT_FLOW, rd.DOCTOR_NAME, rd.SESSION_NUMBER, rd.TRAINING_SPE_NAME, rdsp.SCH_START_DATE, rdsp.SCH_END_DATE, rr.REC_CONTENT, rr.REC_TYPE_ID FROM RES_DOCTOR rd INNER JOIN RES_DOCTOR_SCH_PROCESS rdsp ON rd.DOCTOR_FLOW = rdsp.USER_FLOW INNER JOIN SCH_ARRANGE_RESULT sar ON rdsp.SCH_RESULT_FLOW = sar.RESULT_FLOW INNER JOIN SCH_ROTATION_DEPT srd ON srd.GROUP_FLOW = sar.STANDARD_GROUP_FLOW AND srd.STANDARD_DEPT_ID = sar.STANDARD_DEPT_ID LEFT JOIN RES_REC rr ON rr.OPER_USER_FLOW = rdsp.USER_FLOW WHERE rdsp.RECORD_STATUS = 'Y' AND sar.RECORD_STATUS = 'Y' AND srd.RECORD_STATUS = 'Y' AND rd.RECORD_STATUS = 'Y' AND rr.RECORD_STATUS = 'Y' AND srd.RECORD_FLOW = rr.SCH_ROTATION_DEPT_FLOW AND EXISTS (SELECT NULL FROM RES_DOCTOR_RECRUIT rdr WHERE rdr.DOCTOR_FLOW = rd.DOCTOR_FLOW AND rdr.RECORD_STATUS='Y' AND rdr.AUDIT_STATUS_ID = 'Passed') AND rr.CREATE_TIME> (SELECT NVL(MAX(rr2.CREATE_TIME),'0') FROM RES_REC rr2 WHERE rd.DOCTOR_FLOW=rr2.OPER_USER_FLOW AND rr2.REC_TYPE_ID='ReturnTraining')
AND rdsp.ORG_FLOW=#{orgFlow}
AND rdsp.SCH_END_DATE like #{currDate}
ORDER BY rd.DOCTOR_FLOW,rdsp.SCH_START_DATE

select dept.STANDARD_DEPT_ID, dept.STANDARD_DEPT_NAME, rdsp.SCH_DEPT_FLOW, rdsp.SCH_DEPT_NAME, rdsp.SCH_START_DATE, to_char(CEIL((TO_DATE(rdsp.SCH_END_DATE, 'YYYY-MM-DD') - TO_DATE(rdsp.SCH_START_DATE, 'YYYY-MM-DD')) / 30 * 10) / 10,'FM90.09') F_MONTH, rdsp.SCH_END_DATE, rdsp.TEACHER_USER_FLOW, RDSP.SCH_FLAG, rdsp.TEACHER_USER_NAME, rdsp.HEAD_USER_NAME, nvl((SELECT SUM(srdr.REQ_NUM) FROM SCH_ROTATION_DEPT_REQ srdr WHERE dept.RECORD_FLOW = srdr.REL_RECORD_FLOW AND srdr.RECORD_STATUS = 'Y' ),0) REQ_NUM, nvl( (SELECT COUNT(1) FROM RES_REC rr
WHERE rr.RECORD_STATUS = 'Y' AND rr.SCH_ROTATION_DEPT_FLOW = dept.RECORD_FLOW AND rr.PROCESS_FLOW = rdsp.PROCESS_FLOW AND rr.REC_TYPE_ID IN ('CaseRegistry','DiseaseRegistry','SkillRegistry','OperationRegistry','CampaignRegistry')),0) COMPLETE_NUM, nvl((SELECT COUNT(1) FROM RES_SCH_PROCESS_EXPRESS rr WHERE rr.RECORD_STATUS = 'Y' AND rr.PROCESS_FLOW = rdsp.PROCESS_FLOW AND rr.SCH_ROTATION_DEPT_FLOW = dept.RECORD_FLOW AND rr.REC_TYPE_ID = 'AfterEvaluation'),0) EVALUATION_NUM FROM sch_rotation_dept dept left join sch_arrange_result r on dept.group_flow=r.standard_group_flow AND r.STANDARD_DEPT_ID = dept.STANDARD_DEPT_ID and r.record_status='Y'
AND r.doctor_flow= #{userFlow}
left JOIN RES_DOCTOR_SCH_PROCESS rdsp ON rdsp.SCH_RESULT_FLOW = r.RESULT_FLOW and rdsp.RECORD_STATUS = 'Y' WHERE dept.record_status = 'Y' AND dept.org_flow IS NULL AND EXISTS (SELECT 1 FROM sch_rotation_group g WHERE 1=1
and rotation_flow = #{rotationFlow}
AND record_status = 'Y' AND org_flow IS NULL AND g.group_flow = dept.group_flow) ORDER BY r.CREATE_TIME asc

SELECT A.DOCTOR_FLOW, A.RECRUIT_FLOW, A.ORG_NAME, A.ORG_FLOW, A.USER_NAME, A.TRAINING_SPE_ID, A.TRAINING_SPE_NAME, A.SESSION_NUMBER, A.REQ_NUM, A.COMPLETE_NUM, A.AUDIT_NUM, A.ROTATION_NUM, A.YLZ_NUM, A.SCH_NUM, A.AFTER_NUM, A.COMPLETE_BI, A.AUDIT_BI FROM (SELECT rd.DOCTOR_FLOW, DR.RECRUIT_FLOW, DR.ORG_NAME, DR.ORG_FLOW, SU.USER_NAME, DR.SPE_ID TRAINING_SPE_ID, DR.SPE_NAME TRAINING_SPE_NAME, DR.SESSION_NUMBER, NVL(JDW.REQ_NUM,0) REQ_NUM, NVL(JDW.COMPLETE_NUM, 0) COMPLETE_NUM, NVL(JDW.AUDIT_NUM, 0) AUDIT_NUM, NVL(JDW.YLZ_NUM, 0) YLZ_NUM, NVL(JDW.SCH_NUM, 0) SCH_NUM, NVL(JDW.AFTER_NUM, 0) AFTER_NUM, to_number(NVL(JDW.COMPLETE_BI,0)) COMPLETE_BI, to_number(NVL(JDW.AUDIT_BI,0)) AUDIT_BI, NVL(JDW.rotation_Num,0) rotation_Num FROM RES_DOCTOR_RECRUIT dr LEFT JOIN RES_DOCTOR RD ON DR.DOCTOR_FLOW = RD.DOCTOR_FLOW INNER JOIN SYS_USER SU ON RD.DOCTOR_FLOW = SU.USER_FLOW LEFT JOIN JSRES_DOCTOR_WORK JDW ON RD.DOCTOR_FLOW =JDW.DOCTOR_FLOW and dr.org_flow=jdw.org_flow AND DR.RECRUIT_FLOW =JDW.RECRUIT_FLOW WHERE RD.RECORD_STATUS = 'Y' AND DR.RECORD_STATUS='Y' AND SU.RECORD_STATUS = 'Y' AND dr.AUDIT_STATUS_ID = 'Passed'
and rd.DOCTOR_TYPE_ID in #{item}
AND RD.SECOND_ORG_FLOW =#{userOrgFlow}
AND (DR.ORG_FLOW IN ( SELECT ORG_FLOW FROM SYS_ORG ORG WHERE ORG.RECORD_STATUS = 'Y' AND ORG.ORG_PROV_ID = '320000' AND ORG.ORG_TYPE_ID = 'Hospital'
AND ORG.ORG_LEVEL_ID = #{orgLevel}
AND ORG.ORG_FLOW in #{item}
) or DR.JOINT_ORG_FLOW IN ( SELECT ORG_FLOW FROM SYS_ORG ORG WHERE ORG.RECORD_STATUS = 'Y' AND ORG.ORG_PROV_ID = '320000' AND ORG.ORG_TYPE_ID = 'Hospital'
AND ORG.ORG_LEVEL_ID = #{orgLevel}
AND ORG.ORG_FLOW in #{item}
))
and (
OR rd.work_org_id = #{workOrgId} OR rd.work_org_name = #{workOrgName}
)
AND rd.TRAINING_TYPE_ID = #{typeId}
AND rd.SCHOOL = #{school}
AND rd.TRAINING_SPE_ID = #{speId}
AND DR.SESSION_NUMBER = #{sessionNumber}
AND SU.USER_NAME LIKE #{name}
AND su.ID_NO LIKE #{idNo}
AND DR.GRADUATION_YEAR = #{graduationYear}
) A
ORDER BY COMPLETE_BI DESC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M')
ORDER BY COMPLETE_BI ASC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M')
ORDER BY AUDIT_BI DESC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M')
ORDER BY AUDIT_BI ASC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M')

SELECT su.user_flow, su.user_name, su.dept_flow, su.dept_name, su.user_code, su.org_flow, su.org_name FROM sys_user su WHERE su.record_status = 'Y'
AND su.ORG_FLOW = #{orgFlow}
AND su.ORG_FLOW in #{item}
and su.DEPT_FLOW in
#{item}
AND su.DEPT_FLOW = #{schDept.deptFlow}
AND su.USER_NAME LIKE #{name}
AND EXISTS (SELECT 1 FROM sys_user_role sur WHERE sur.user_flow = su.user_flow AND sur.record_status = 'Y' AND sur.ROLE_FLOW = #{roleFlow}) AND NOT EXISTS (SELECT NULL FROM res_doctor rd WHERE su.user_flow = rd.doctor_flow AND rd.RECORD_STATUS = 'Y') GROUP BY su.user_flow, su.user_name, su.dept_flow, su.dept_name, su.user_code, su.org_flow, su.org_name ORDER BY dept_flow,user_flow

SELECT teacher_user_flow KEY,nvl(COUNT(1),0) QTY
FROM RES_DOCTOR_SCH_PROCESS A
JOIN RES_REC RR
ON RR.PROCESS_FLOW = A.PROCESS_FLOW
