select doctor_flow,doctor_name,org_name,training_years,training_type_id,training_type_name,doctor_category_id,doctor_category_name, training_spe_id,training_spe_name,session_number,graduation_Year,reqnum,completenum,checknum,rotationnum,lunzhuannum,chukenum from( SELECT rd.DOCTOR_FLOW, su.USER_NAME doctor_name, rd.training_years, rd.org_flow, rd.org_name, rd.training_type_id, rd.training_type_name, rd.TRAINING_SPE_ID, rd.TRAINING_SPE_NAME, rd.DOCTOR_CATEGORY_ID, rd.DOCTOR_CATEGORY_NAME, rd.SESSION_NUMBER, rd.graduation_Year, NVL((select sum(req.REQ_NUM) from SCH_ROTATION_DEPT_REQ req where req.record_status = 'Y' AND REQ.ROTATION_FLOW = RD.ROTATION_FLOW),0) reqNum, NVL((select COUNT(1) from RES_REC RR WHERE RR.RECORD_STATUS = 'Y' AND RR.OPER_USER_FLOW = RD.DOCTOR_FLOW and rr.process_flow in (select process_flow from RES_DOCTOR_SCH_PROCESS where record_status='Y' and user_flow=rd.doctor_flow)
AND RR.REC_TYPE_ID IN
#{item}
),0) completeNum, NVL(( select count(1) from res_rec r where r.record_status = 'Y' AND r.OPER_USER_FLOW = RD.DOCTOR_FLOW and r.process_flow in (select process_flow from RES_DOCTOR_SCH_PROCESS where record_status='Y' and user_flow=rd.doctor_flow)
AND R.REC_TYPE_ID IN
#{item}
AND r.audit_status_id is not null ), 0) AS checkNum, NVL((select COUNT(1) from sch_rotation_dept dept where record_status = 'Y' and dept.org_flow is null and exists (select 1 from sch_rotation_group g where rotation_flow = RD.ROTATION_FLOW and record_status = 'Y' and org_flow is null and g.group_flow = dept.group_flow)),0) rotationNum, NVL(( select count(1) from res_doctor_sch_process where record_status = 'Y' and user_flow = rd.doctor_flow and to_char(sysdate, 'yyyy-mm-dd' )>= sch_start_date ),0) lunzhuanNum, NVL((SELECT COUNT(1) FROM RES_DOCTOR_SCH_PROCESS A WHERE A.RECORD_STATUS = 'Y' AND A.SCH_FLAG = 'Y' AND A.USER_FLOW = RD.DOCTOR_FLOW ),0) chukeNum FROM RES_DOCTOR rd INNER JOIN SYS_USER SU ON RD.DOCTOR_FLOW = SU.USER_FLOW WHERE rd.RECORD_STATUS = 'Y' and su.record_status='Y'
AND rd.ORG_FLOW = #{orgFlow}
and rd.org_flow in #{item}
AND rd.TRAINING_SPE_ID = #{speId}
and rd.DOCTOR_CATEGORY_ID = #{doctorCategoryId}
and rd.DOCTOR_TYPE_ID in #{item}
and rd.DOCTOR_FLOW IN
#{flow}
AND rd.SESSION_NUMBER = #{sessionNumber}
AND su.USER_NAME LIKE #{name}
AND su.ID_NO LIKE #{cardNo}
AND rd.training_Spe_Id = #{trainingSpeId}
and rd.work_Org_Id = #{workOrgId}
and rd.graduation_Year = #{graduationYear}
and rd.training_Years = #{trainingYears}
AND RD.DOCTOR_STATUS_ID NOT IN ('UnPassed', 'Passed', 'UnSubmit') AND RD.org_flow is not null AND RD.org_flow != '20160914888888' AND RD.org_flow != '566c9f36cf014f63a569b453efa284d7' AND RD.org_flow != '00000000000000000000000000000000' order by NLSSORT(su.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M') )
order by to_number(decode(reqnum,0,-1,round(completenum/reqnum*10000))) ${order eq 'wcdx'?'desc':''} , SESSION_NUMBER DESC ,doctor_name
order by to_number(decode(completenum,0,-1,round(checknum/completenum*10000))) ${order eq 'shdx'?'desc':''} , SESSION_NUMBER DESC ,doctor_name

SELECT sar.STANDARD_DEPT_ID, sar.STANDARD_DEPT_NAME, sar.SCH_DEPT_FLOW, sar.SCH_DEPT_NAME, sar.SCH_START_DATE , rdsp.PROCESS_FLOW, sar.sch_month F_MONTH , sar.SCH_END_DATE, rdsp.TEACHER_USER_FLOW, RDSP.SCH_FLAG , rdsp.TEACHER_USER_NAME, rdsp.HEAD_USER_NAME, NVL(( SELECT SUM(srdr.REQ_NUM) FROM SCH_ROTATION_DEPT srd INNER JOIN SCH_ROTATION_DEPT_REQ srdr ON srd.RECORD_FLOW = srdr.REL_RECORD_FLOW WHERE srd.RECORD_STATUS = 'Y' AND srdr.RECORD_STATUS = 'Y' AND srd.STANDARD_DEPT_ID = sar.STANDARD_DEPT_ID AND srd.GROUP_FLOW = sar.STANDARD_GROUP_FLOW ),0) REQ_NUM, NVL(( SELECT COUNT(1) FROM RES_REC rr WHERE rr.RECORD_STATUS = 'Y' AND rr.PROCESS_FLOW = rdsp.PROCESS_FLOW
AND RR.REC_TYPE_ID IN
#{item}
),0) COMPLETE_NUM, NVL(( select count(1) from res_rec r where r.record_status = 'Y' AND r.PROCESS_FLOW = rdsp.PROCESS_FLOW
AND R.REC_TYPE_ID IN
#{item}
AND r.audit_status_id is not null ), 0) AS CHECK_NUM, RDSP.IS_CURRENT_FLAG, decode((select count(1) from dept_teacher_grade_info dtgi where record_status='Y' and rdsp.process_flow=dtgi.process_flow),2,'','') evaluation_flag FROM SCH_ARRANGE_RESULT sar left JOIN RES_DOCTOR_SCH_PROCESS rdsp ON rdsp.SCH_RESULT_FLOW = sar.RESULT_FLOW and rdsp.RECORD_STATUS = 'Y' WHERE sar.RECORD_STATUS = 'Y'
AND sar.doctor_flow = #{userFlow}
ORDER BY sar.CREATE_TIME DESC

select u.USER_NAME user_name, dr.DOCTOR_CATEGORY_NAME DOCTOR_CATEGORY_NAME,dr.TRAINING_SPE_NAME training_spe_name,dr.DOCTOR_Flow doctor_flow,dr.org_name,
dr.SESSION_NUMBER session_number, dr.TRAINING_YEARS training_years,dr.TRAINING_SPE_ID training_spe_id,dr.training_type_id training_type_id,dr.training_type_name training_type_name
from RES_DOCTOR dr
inner join SYS_USER u
on dr.DOCTOR_FLOW = u.USER_FLOW
where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y'
AND (dr.DOCTOR_STATUS_ID NOT IN ('UnPassed', 'Passed', 'UnSubmit')
OR dr.DOCTOR_STATUS_ID IS NULL)
AND dr.org_flow IS NOT NULL
AND dr.org_flow != '20160914888888'
AND dr.org_flow != '566c9f36cf014f63a569b453efa284d7'
AND dr.org_flow != '00000000000000000000000000000000'

	and dr.TRAINING_SPE_ID = #{trainingSpeId}


	AND dr.DOCTOR_CATEGORY_ID = #{doctorCategoryId}


	and dr.SESSION_NUMBER = #{sessionNumber}


	and dr.TRAINING_YEARS = #{trainingYears}


	and dr.work_Org_Id = #{workOrgId}



	AND  u.USER_NAME LIKE #{name}



	AND u.ID_NO LIKE #{idNo}


	and (
	dr.ORG_FLOW = #{orgFlow}
	)


	and dr.DOCTOR_TYPE_ID in
	#{item}


	and dr.DOCTOR_FLOW IN

		#{flow}



	and instr('${medicineTypeId}',u.medicine_Type_Id)>0

order by dr.SESSION_NUMBER desc,dr.TRAINING_SPE_ID,dr.create_time,dr.doctor_flow

SELECT U.USER_NAME USER_NAME, DR.TRAINING_SPE_NAME TRAINING_SPE_NAME, DR.DOCTOR_FLOW DOCTOR_FLOW, DR.SESSION_NUMBER SESSION_NUMBER, DR.TRAINING_YEARS TRAINING_YEARS, DR.TRAINING_SPE_ID TRAINING_SPE_ID, P.SCH_DEPT_FLOW, P.SCH_DEPT_NAME, P.SCH_SCORE FROM RES_DOCTOR DR INNER JOIN SYS_USER U ON DR.DOCTOR_FLOW = U.USER_FLOW LEFT JOIN RES_DOCTOR_SCH_PROCESS P ON P.USER_FLOW = U.USER_FLOW where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y' and p.record_status='Y'
and dr.TRAINING_SPE_ID = #{trainingSpeId}
and dr.SESSION_NUMBER = #{sessionNumber}
and dr.TRAINING_YEARS = #{trainingYears}
and( (p.SCH_START_DATE>=#{startDate} and #{endDate}>=p.SCH_START_DATE) or (p.SCH_END_DATE>=#{startDate} and #{endDate}>=p.SCH_END_DATE) )
AND u.USER_NAME LIKE #{name}
AND u.ID_NO LIKE #{idNo}
and ( dr.ORG_FLOW = #{orgFlow} or exists( select null from res_joint_org rjo where rjo.record_status = 'Y' and rjo.org_flow = #{orgFlow} and dr.org_flow = rjo.joint_org_flow ) )
order by NLSSORT(u.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M')

select ar.*
from SCH_ARRANGE_RESULT ar,RES_DOCTOR dr,SYS_USER u
where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y' and ar.RECORD_STATUS= 'Y' and dr.DOCTOR_FLOW = u.USER_FLOW and
dr.DOCTOR_FLOW = ar.DOCTOR_FLOW

	and(
	(ar.SCH_START_DATE>=#{startDate} and #{endDate}>=ar.SCH_START_DATE)
	or
	(ar.SCH_END_DATE>=#{startDate} and #{endDate}>=ar.SCH_END_DATE)
	)



	AND  u.USER_NAME LIKE #{name}



	AND u.ID_NO LIKE #{idNo}


	and dr.TRAINING_SPE_ID = #{trainingSpeId}


	and dr.SESSION_NUMBER = #{sessionNumber}


	and dr.TRAINING_YEARS = #{trainingYears}


	and (
	dr.ORG_FLOW = #{orgFlow}
	or exists(
	select null from res_joint_org rjo
	where rjo.record_status = 'Y'
	and rjo.org_flow = #{orgFlow}
	and dr.org_flow = rjo.joint_org_flow
	)
	)

order by NLSSORT(u.USER_NAME,'NLS_SORT = SCHINESE_PINYIN_M')

select pro.SCH_DEPT_FLOW , pro.SCH_DEPT_NAME , pro.DEPT_FLOW, PRO.DEPT_NAME, U.USER_FLOW, u.USER_NAME , RD.ORG_NAME, RD.ORG_FLOW, pro.TEACHER_USER_NAME , pro.SCH_START_DATE, pro.SCH_END_DATE from res_doctor_sch_process pro left join sys_user u on pro.user_flow=u.user_flow LEFT JOIN RES_DOCTOR RD ON RD.DOCTOR_FLOW=U.USER_FLOW where pro.org_flow=#{orgFlow} and pro.record_status = 'Y' and u.record_status = 'Y' and RD.record_status = 'Y'
AND pro.SCH_START_DATE like #{currDate}
AND pro.SCH_END_DATE like #{currDate1}
AND RD.TRAINING_TYPE_ID= #{trainingTypeId}
order by pro.SCH_DEPT_NAME,pro.SCH_START_DATE,pro.SCH_END_DATE

select distinct pro.SCH_DEPT_FLOW, pro.SCH_DEPT_NAME, PRO.DEPT_FLOW, PRO.DEPT_NAME, RD.ORG_FLOW, RD.ORG_NAME, PRO.PROCESS_FLOW, u.USER_FLOW, u.USER_NAME, pro.TEACHER_USER_NAME, pro.SCH_PER, pro.SCH_END_DATE, pro.sch_start_date from res_doctor_sch_process pro ,sys_user u,res_rec r,RES_DOCTOR RD where pro.org_flow=#{orgFlow} and pro.USER_FLOW=u.user_flow AND RD.DOCTOR_FLOW=U.USER_FLOW and r.process_flow=pro.process_flow and pro.record_status = 'Y' and RD.record_status = 'Y' and u.record_status = 'Y' and r.record_status = 'Y'
AND pro.SCH_START_DATE like #{currDate}
and r.rec_type_id in ('CaseRegistry','DiseaseRegistry','OperationRegistry','SkillRegistry','CampaignRegistry') order by pro.SCH_DEPT_NAME,pro.sch_start_date

SELECT DISTINCT pro.SCH_DEPT_FLOW, pro.SCH_DEPT_NAME, PRO.DEPT_FLOW, PRO.DEPT_NAME, RD.ORG_FLOW, RD.ORG_NAME, PRO.PROCESS_FLOW, u.USER_FLOW, u.USER_NAME, pro.TEACHER_USER_NAME, pro.SCH_PER , pro.SCH_END_DATE, r.PROCESS_FLOW AS AUDIT_STATUS_ID, pro.SCH_RESULT_FLOW AS SCH_RESULT_FLOW FROM res_doctor_sch_process pro left join sys_user u on pro.USER_FLOW = u.user_flow AND u.record_status = 'Y' left join RES_SCH_PROCESS_EXPRESS r on r.oper_user_flow = u.user_flow AND r.rec_type_id = 'AfterEvaluation' AND r.record_status = 'Y' AND r.process_flow = pro.process_flow LEFT JOIN RES_DOCTOR RD ON RD.DOCTOR_FLOW=U.USER_FLOW WHERE pro.org_flow = #{orgFlow} AND RD.record_status = 'Y' AND pro.record_status = 'Y'
AND pro.SCH_END_DATE like #{currDate}
AND RD.TRAINING_TYPE_ID= #{trainingTypeId}
ORDER BY pro.SCH_DEPT_NAME, pro.SCH_END_DATE

select count(distinct(a.USER_FLOW)) from ( select P.TEACHER_USER_FLOW TEACHER_USER_FLOW, p.PROCESS_FLOW PROCESS_FLOW, su.user_flow user_flow, p.SCH_START_DATE SCH_START_DATE, p.SCH_END_DATE SCH_END_DATE, RD.DOCTOR_TYPE_ID, nvl((select CFG_VALUE from jsres_power_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jsres_doctor_app_menu_'||rd.doctor_flow),'N')APP_MENU_FLAG, nvl(cfg2.power_start_time,'0000-00-00') power_start_time, nvl(cfg2.power_end_time,'9999-99-99') power_end_time from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow left join jsres_power_cfg cfg2 on cfg2.record_status='Y' and cfg2.cfg_code = 'jsres_doctor_data_time_'||rd.doctor_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y'
and rd.doctor_Type_Id in
#{item}
) A WHERE a.APP_MENU_FLAG='Y' AND ( ( a.SCH_START_DATE <= A.power_start_time AND a.SCH_END_DATE >= A.power_END_time) or (a.SCH_START_DATE >= A.power_start_time AND a.SCH_START_DATE <= A.power_END_time) or (a.SCH_END_DATE >= A.power_start_time AND a.SCH_END_DATE <= A.power_END_time))
AND( ( a.SCH_START_DATE <= #{startDate} AND a.SCH_END_DATE >= #{endDate}) or (a.SCH_START_DATE >= #{startDate} AND a.SCH_START_DATE <= #{endDate}) or (a.SCH_END_DATE >= #{startDate} AND a.SCH_END_DATE <= #{endDate}))
and a.TEACHER_USER_FLOW='${teacherUserFlow}'

select * from sys_user where user_Flow in( select distinct(a.USER_FLOW) from ( select P.TEACHER_USER_FLOW TEACHER_USER_FLOW, p.PROCESS_FLOW PROCESS_FLOW, su.user_flow user_flow, p.SCH_START_DATE SCH_START_DATE, p.SCH_END_DATE SCH_END_DATE, RD.DOCTOR_TYPE_ID, nvl((select CFG_VALUE from jsres_power_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jsres_doctor_app_menu_'||rd.doctor_flow),'N')APP_MENU_FLAG, nvl(cfg2.power_start_time,'0000-00-00') power_start_time, nvl(cfg2.power_end_time,'9999-99-99') power_end_time from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow left join jsres_power_cfg cfg2 on cfg2.record_status='Y' and cfg2.cfg_code = 'jsres_doctor_data_time_'||rd.doctor_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y'
and rd.doctor_Type_Id in
#{item}
) A WHERE a.APP_MENU_FLAG='Y' AND ( ( a.SCH_START_DATE <= A.power_start_time AND a.SCH_END_DATE >= A.power_END_time) or (a.SCH_START_DATE >= A.power_start_time AND a.SCH_START_DATE <= A.power_END_time) or (a.SCH_END_DATE >= A.power_start_time AND a.SCH_END_DATE <= A.power_END_time))
AND( ( a.SCH_START_DATE <= #{startDate} AND a.SCH_END_DATE >= #{endDate}) or (a.SCH_START_DATE >= #{startDate} AND a.SCH_START_DATE <= #{endDate}) or (a.SCH_END_DATE >= #{startDate} AND a.SCH_END_DATE <= #{endDate}))
and a.TEACHER_USER_FLOW='${teacherUserFlow}' ) order by user_name,user_flow

select A.* from (select p.PROCESS_FLOW, p.USER_FLOW USER_FLOW, P.ORG_FLOW ORG_FLOW, P.ORG_NAME ORG_NAME, P.DEPT_FLOW DEPT_FLOW, P.DEPT_NAME, P.SCH_DEPT_FLOW, P.SCH_DEPT_NAME, P.SCH_RESULT_FLOW, P.SCH_START_DATE SCH_START_DATE, P.SCH_END_DATE SCH_END_DATE, P.TEACHER_USER_FLOW TEACHER_USER_FLOW, P.TEACHER_USER_NAME, P.HEAD_USER_FLOW, P.HEAD_USER_NAME, P.SCH_FLAG, P.SCH_SCORE, P.IS_CURRENT_FLAG, su.USER_Name USER_Name, su.SEX_NAME, su.USER_PHONE, RD.DOCTOR_TYPE_ID, RD.DOCTOR_TYPE_Name, nvl((select CFG_VALUE from jsres_power_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jsres_doctor_app_menu_'||rd.doctor_flow),'N')APP_MENU_FLAG, nvl(cfg2.power_start_time,'0000-00-00') power_start_time, nvl(cfg2.power_end_time,'9999-99-99') power_end_time from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow left join jsres_power_cfg cfg2 on cfg2.record_status='Y' and cfg2.cfg_code = 'jsres_doctor_data_time_'||rd.doctor_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y' AND P.DEPT_FLOW IN ( SELECT DEPT_FLOW FROM SYS_USER_DEPT D WHERE D.RECORD_STATUS = 'Y' AND (D.USER_FLOW = #{userFlow}
OR D.DEPT_FLOW = #{deptFlow}
))
and su.USER_NAME like #{name}
and rd.DOCTOR_TYPE_ID in #{item}
) A WHERE a.APP_MENU_FLAG='Y' AND ( ( a.SCH_START_DATE <= A.power_start_time AND a.SCH_END_DATE >= A.power_END_time) or (a.SCH_START_DATE >= A.power_start_time AND a.SCH_START_DATE <= A.power_END_time) or (a.SCH_END_DATE >= A.power_start_time AND a.SCH_END_DATE <= A.power_END_time))
and a.SCH_START_DATE >= #{schStartDate}
and a.SCH_END_DATE <= #{schEndDate}
AND a.SCH_DEPT_FLOW= #{schDeptFlow}
and exists ( SELECT 1 FROM RES_SCH_PROCESS_EXPRESS RR WHERE RR.RECORD_STATUS = 'Y' AND RR.PROCESS_FLOW =a.PROCESS_FLOW AND RR.REC_TYPE_ID = 'AfterEvaluation' AND RR.MANAGER_AUDIT_USER_FLOW IS NOT NULL
AND RR.HEAD_AUDIT_STATUS_ID IS NULL
)
ORDER BY a.SCH_START_DATE DESC nulls last

select p.PROCESS_FLOW, p.USER_FLOW USER_FLOW, P.ORG_FLOW ORG_FLOW, P.ORG_NAME ORG_NAME, P.DEPT_FLOW DEPT_FLOW, P.MODIFY_TIME, P.DEPT_NAME, P.SCH_DEPT_FLOW, P.SCH_DEPT_NAME, P.SCH_RESULT_FLOW, P.SCH_START_DATE SCH_START_DATE, P.SCH_END_DATE SCH_END_DATE, P.TEACHER_USER_FLOW TEACHER_USER_FLOW, P.TEACHER_USER_NAME, P.HEAD_USER_FLOW, P.HEAD_USER_NAME, P.SCH_FLAG, P.SCH_SCORE, P.IS_CURRENT_FLAG, su.USER_Name USER_Name, su.SEX_NAME, su.USER_PHONE, RD.DOCTOR_TYPE_ID, RD.DOCTOR_TYPE_Name from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y' AND P.DEPT_FLOW IN ( SELECT DEPT_FLOW FROM SYS_USER_DEPT D WHERE D.RECORD_STATUS = 'Y' AND (D.USER_FLOW = #{userFlow}
OR D.DEPT_FLOW = #{deptFlow}
