and rd.DOCTOR_TYPE_ID in #{item}
AND P.TEMPORARY_OUT = 'Y' AND P.TEMPORARY_AUDIT_STATUS_ID = 'Auditing' ORDER BY P.MODIFY_TIME DESC

select p.PROCESS_FLOW, p.USER_FLOW USER_FLOW, P.ORG_FLOW ORG_FLOW, P.ORG_NAME ORG_NAME, P.DEPT_FLOW DEPT_FLOW, P.MODIFY_TIME, P.DEPT_NAME, P.SCH_DEPT_FLOW, P.SCH_DEPT_NAME, P.SCH_RESULT_FLOW, P.SCH_START_DATE SCH_START_DATE, P.SCH_END_DATE SCH_END_DATE, P.TEACHER_USER_FLOW TEACHER_USER_FLOW, P.TEACHER_USER_NAME, P.HEAD_USER_FLOW, P.HEAD_USER_NAME, P.SCH_FLAG, P.SCH_SCORE, P.IS_CURRENT_FLAG, P.TEMPORARY_AUDIT_STATUS_ID, P.TEMPORARY_AUDIT_STATUS_NAME, su.USER_Name USER_Name, su.SEX_NAME, su.USER_PHONE, RD.DOCTOR_TYPE_ID, RD.DOCTOR_TYPE_Name from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y'
and su.USER_FLOW = #{userFlow}
and rd.ORG_FLOW = #{orgFlow}
AND P.DEPT_FLOW IN ( SELECT DEPT_FLOW FROM SYS_USER_DEPT D WHERE D.RECORD_STATUS = 'Y' AND (D.USER_FLOW = #{userFlow}
OR D.DEPT_FLOW = #{deptFlow}
))
and su.USER_NAME like #{name}
and rd.DOCTOR_TYPE_ID in #{item}
AND p.TEMPORARY_AUDIT_STATUS_ID = #{temporaryAuditStatusId}
AND rd.training_type_id = #{trainingTypeId}
AND P.TEMPORARY_OUT = 'Y' ORDER BY P.MODIFY_TIME DESC

select * from ( select to_number(substr(p.SCH_START_DATE, 1, 4)) sy, substr(p.SCH_START_DATE, 6, 2) sm, to_number(substr(p.SCH_END_DATE, 1, 4)) ey, substr(p.SCH_END_DATE, 6, 2) em, p.PROCESS_FLOW, p.USER_FLOW , DR.ORG_FLOW , DR.ORG_NAME , DR.CAT_SPE_ID, DR.CAT_SPE_NAME, DR.SPE_ID, DR.SPE_NAME, DR.TRAIN_YEAR, DR.SESSION_NUMBER, DR.GRADUATION_YEAR, DR.DOCTOR_STATUS_ID, P.DEPT_FLOW, P.DEPT_NAME, P.SCH_DEPT_FLOW, P.SCH_DEPT_NAME, P.SCH_RESULT_FLOW, P.SCH_START_DATE, P.SCH_END_DATE, P.TEACHER_USER_FLOW, P.TEACHER_USER_NAME, P.HEAD_USER_FLOW, P.HEAD_USER_NAME, su.USER_Name USER_Name, su.ID_NO, su.SEX_NAME, su.USER_PHONE, RD.DOCTOR_TYPE_ID, RD.DOCTOR_TYPE_NAME, nvl((select CFG_VALUE from jsres_power_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jsres_doctor_app_menu_'||rd.doctor_flow),'N')APP_MENU_FLAG, nvl(cfg2.power_start_time,'0000-00-00') power_start_time, nvl(cfg2.power_end_time,'9999-99-99') power_end_time from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow left join sch_arrange_result r on p.sch_result_flow=r.result_flow left join res_doctor_recruit dr on rd.doctor_flow=dr.doctor_flow and dr.session_number=r.session_number and dr.org_flow=r.org_flow left join jsres_power_cfg cfg2 on cfg2.record_status='Y' and cfg2.cfg_code = 'jsres_doctor_data_time_'||rd.doctor_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y' and r.record_status='Y' and dr.record_status='Y' and dr.audit_status_id='Passed' ) a where A.org_flow in( SELECT ORG_FLOW FROM PDSCI.SYS_ORG WHERE PDSCI.SYS_ORG.RECORD_STATUS='Y'
AND ORG_PROV_ID = #{org.orgProvId}
AND ORG_CITY_ID = #{org.orgCityId}
AND ORG_LEVEL_ID = #{org.orgLevelId}
AND ORG_TYPE_ID = #{org.orgTypeId}
) and A.APP_MENU_FLAG='Y' AND ( ( a.SCH_START_DATE <= A.power_start_time AND a.SCH_END_DATE >= A.power_END_time) or (a.SCH_START_DATE >= A.power_start_time AND a.SCH_START_DATE <= A.power_END_time) or (a.SCH_END_DATE >= A.power_start_time AND a.SCH_END_DATE <= A.power_END_time))
AND A.org_flow = #{orgFlow}
and A.org_flow in #{item}
and A.DOCTOR_TYPE_ID in #{item}
and A.SPE_ID in #{item}
and A.CAT_SPE_ID = #{trainingTypeId}
and A.SPE_ID = #{trainingSpeId}
and A.DOCTOR_STATUS_ID = #{doctorStatusId}
and A.SESSION_NUMBER = #{sessionNumber}
and A.SCH_DEPT_FLOW = #{schDeptFlow}
and A.GRADUATION_YEAR = #{graduationYear}
and A.USER_NAME like #{userName}
and A.ID_NO like #{idNo}
and ( exists( select null from SYS_USER_DEPT sud where sud.RECORD_STATUS='Y' and sud.USER_FLOW=#{userFlow} and A.DEPT_FLOW=sud.DEPT_FLOW )
or A.DEPT_FLOW=#{deptFlow}
)
(case when a.sy = a.ey and a.sm <= #{schMonth} and a.em >= #{schMonth} then 1 when a.sy + 1 = a.ey AND (a.sm <= a.em OR (a.em >= #{schMonth} OR a.sm < #{schMonth})) then 1 when a.sy + 1 < a.ey then 1 else 0 end)
ORDER BY A.SCH_START_DATE DESC

select a.PROCESS_FLOW, a.EVAL_MONTH, a.START_TIME, a.END_TIME, eval.RECORD_FLOW, eval.EVAL_SCORE, eval.IS_FORM, eval.attendance, eval.leave, eval.absenteeism from (
SELECT #{item.processFlow} PROCESS_FLOW ,#{item.evlMonth} EVAL_MONTH,#{item.startTime} START_TIME,#{item.endTime} END_TIME FROM DUAL
) a left join RES_DOCTOR_SCH_PROCESS_EVAL eval on eval.process_flow=a.process_flow and eval.EVAL_MONTH = a.EVAL_MONTH and eval.record_status='Y' order by a.EVAL_MONTH

select A.* from (select p.PROCESS_FLOW, p.USER_FLOW, p.ORG_FLOW, p.ORG_NAME, p.DEPT_FLOW, p.DEPT_NAME, p.SCH_DEPT_FLOW, p.SCH_DEPT_NAME, p.SCH_RESULT_FLOW, p.SCH_START_DATE, p.SCH_END_DATE, p.TEACHER_USER_FLOW, p.TEACHER_USER_NAME, p.HEAD_USER_FLOW, p.HEAD_USER_NAME, p.SCH_FLAG, p.SCH_SCORE, p.IS_CURRENT_FLAG , su.USER_Name, su.SEX_NAME, su.USER_PHONE, rd.TRAINING_SPE_ID, rd.TRAINING_SPE_NAME, nvl(d.eval_num,0) eval_num, rd.DOCTOR_TYPE_ID, rd.DOCTOR_TYPE_Name, nvl((select CFG_VALUE from jsres_power_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jsres_doctor_app_menu_'||rd.doctor_flow),'N')APP_MENU_FLAG, nvl(cfg2.power_start_time,'0000-00-00') power_start_time, nvl(cfg2.power_end_time,'9999-99-99') power_end_time from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow left join jsres_power_cfg cfg2 on cfg2.record_status='Y' and cfg2.cfg_code = 'jsres_doctor_data_time_'||rd.doctor_flow left join (select c.process_flow,count(c.record_flow) eval_num from res_doctor_sch_process_eval c where c.record_status='Y' group by c.process_flow)d on d.process_flow = p.process_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y') A WHERE a.APP_MENU_FLAG='Y' AND ( ( a.SCH_START_DATE <= A.power_start_time AND a.SCH_END_DATE >= A.power_END_time) or (a.SCH_START_DATE >= A.power_start_time AND a.SCH_START_DATE <= A.power_END_time) or (a.SCH_END_DATE >= A.power_start_time AND a.SCH_END_DATE <= A.power_END_time))
and a.TEACHER_USER_FLOW = #{teacherUserFlow}
and a.SCH_START_DATE >= #{schStartDate}
and a.SCH_END_DATE <= #{schEndDate}
and a.user_Name like #{name}
and a.DOCTOR_TYPE_ID in #{item}
and ( exists( select null from res_rec rr where rr.RECORD_STATUS='Y' and rr.PROCESS_FLOW = a.PROCESS_FLOW and rr.AUDIT_STATUS_ID is null and rr.rec_type_id in ('CaseRegistry','DiseaseRegistry','SkillRegistry','OperationRegistry','CampaignRegistry') ) or not exists( select null from res_sch_process_express rr where rr.RECORD_STATUS='Y' and rr.PROCESS_FLOW = a.PROCESS_FLOW and rr.rec_type_id = 'AfterEvaluation' ) )
and( not exists( select null from res_sch_process_express rr where rr.RECORD_STATUS='Y' and rr.PROCESS_FLOW = a.PROCESS_FLOW and rr.rec_type_id in ('DOPS') ) or not exists( select null from res_sch_process_express rr where rr.RECORD_STATUS='Y' and rr.PROCESS_FLOW = a.PROCESS_FLOW and rr.rec_type_id in ('Mini_CEX') ) )
and a.TRAINING_SPE_ID = #{trainingSpeId}
and (MONTHS_BETWEEN(to_date(substr(a.sch_end_date,0,7),'yyyy-MM'),to_date(substr(a.sch_start_date,0,7),'yyyy-MM'))+1 )!=a.eval_num
ORDER BY a.process_flow DESC nulls last

SELECT rd.DOCTOR_FLOW, SU.USER_NAME, rd.TRAINING_SPE_ID, rd.TRAINING_SPE_NAME, rd.SESSION_NUMBER, nvl((SELECT SUM(req.REQ_NUM) FROM SCH_ROTATION_DEPT_REQ req WHERE req.record_status = 'Y' and rd.org_flow is not null AND REQ.ROTATION_FLOW = RD.ROTATION_FLOW and req.rec_type_id in ('CaseRegistry', 'DiseaseRegistry', 'SkillRegistry', 'OperationRegistry', 'CampaignRegistry')),0) REQ_NUM, NVL((SELECT COUNT(1) FROM RES_REC rr INNER JOIN SCH_ROTATION_DEPT srd ON rr.SCH_ROTATION_DEPT_FLOW = srd.RECORD_FLOW WHERE rr.RECORD_STATUS = 'Y' AND srd.RECORD_STATUS = 'Y' and rd.org_flow is not null AND RR.OPER_USER_FLOW = RD.DOCTOR_FLOW AND rr.REC_TYPE_ID IN ('CaseRegistry', 'DiseaseRegistry', 'SkillRegistry', 'OperationRegistry', 'CampaignRegistry')), 0) COMPLETE_NUM, NVL(( SELECT COUNT(1) FROM RES_REC rr INNER JOIN SCH_ROTATION_DEPT srd ON rr.SCH_ROTATION_DEPT_FLOW = srd.RECORD_FLOW WHERE rr.RECORD_STATUS = 'Y' AND srd.RECORD_STATUS = 'Y' AND RR.AUDIT_STATUS_ID IS NOT NULL AND RR.OPER_USER_FLOW = RD.DOCTOR_FLOW AND rr.REC_TYPE_ID IN ('CaseRegistry','DiseaseRegistry','SkillRegistry','OperationRegistry','CampaignRegistry') ), 0) AS AUDIT_NUM, NVL((SELECT COUNT(1) FROM sch_rotation_dept dept WHERE record_status = 'Y' AND dept.org_flow IS NULL AND EXISTS (SELECT 1 FROM sch_rotation_group g WHERE rotation_flow = RD.ROTATION_FLOW and rd.org_flow is not null AND record_status = 'Y' AND org_flow IS NULL AND g.group_flow = dept.group_flow)), 0) AS rotationNum, NVL((select count(1) from (SELECT count(rr.rec_type_id), sar.result_flow, rdsp.user_flow FROM SCH_ARRANGE_RESULT sar
inner JOIN RES_DOCTOR_SCH_PROCESS rdsp ON rdsp.SCH_RESULT_FLOW = sar.RESULT_FLOW inner join RES_SCH_PROCESS_EXPRESS rr on rr.PROCESS_FLOW = rdsp.PROCESS_FLOW INNER JOIN SCH_ROTATION_DEPT srd ON rr.SCH_ROTATION_DEPT_FLOW = srd.RECORD_FLOW WHERE rr.RECORD_STATUS = 'Y' AND srd.RECORD_STATUS = 'Y' and sar.RECORD_STATUS = 'Y' AND rdsp.RECORD_STATUS = 'Y' and rd.org_flow is not null AND rdsp.ORG_FLOW = rd.org_flow AND RR.OPER_USER_FLOW = rd.doctor_flow AND rr.REC_TYPE_ID = 'AfterEvaluation' group by sar.result_flow, rdsp.user_flow having count(rr.rec_type_id) > 0) t), 0) schNum, NVL((select count(1) from (SELECT rr.process_flow, count(rr.rec_flow) from RES_DOCTOR_SCH_PROCESS rdsp left join RES_REC rr on rr.process_flow = rdsp.process_flow WHERE rr.RECORD_STATUS = 'Y' and rdsp.record_status = 'Y' AND rdsp.ORG_FLOW = rd.org_flow AND RR.OPER_USER_FLOW = rd.doctor_flow and rd.org_flow is not null AND rr.REC_TYPE_ID IN('CaseRegistry', 'DiseaseRegistry', 'SkillRegistry', 'OperationRegistry', 'CampaignRegistry') group by rr.process_flow having count(rr.rec_flow) > 0)), 0) ylz FROM RES_DOCTOR rd INNER JOIN SYS_USER SU ON RD.DOCTOR_FLOW = SU.USER_FLOW LEFT JOIN (SELECT MAX(GRADUATION_YEAR) AS GRADUATION_YEAR, DOCTOR_FLOW FROM RES_DOCTOR_RECRUIT GROUP BY DOCTOR_FLOW) rdr ON rdr.DOCTOR_FLOW = rd.DOCTOR_FLOW WHERE rd.RECORD_STATUS = 'Y' and rd.org_flow is not null and (
OR rd.work_org_id = #{workOrgId} OR rd.work_org_name = #{workOrgName}
)
and rd.DOCTOR_TYPE_ID = #{doctorTypeId}
AND rd.ORG_FLOW = #{orgFlow}
AND rd.TRAINING_SPE_ID = #{speId}
AND rd.SESSION_NUMBER = #{sessionNumber}
AND SU.USER_NAME LIKE #{name}
AND su.ID_NO = #{cardNo}
AND rd.training_type_id = #{trainType}
AND rdr.GRADUATION_YEAR = #{graduationYear}

SELECT * FROM (
	SELECT ORG_FLOW,ORG_NAME,SUM(COMPLETE_NUM) COMPLETE_NUM ,SUM(AUDIT_NUM) AUDIT_NUM ,SUM(NOT_AUDIT_NUM)NOT_AUDIT_NUM  FROM (
		SELECT *
		from JSRES_org_teacher_audit_TEMP where
	   (

			OR work_org_id = #{workOrgId}
			OR work_org_name = #{workOrgName}

		)

			AND SESSION_NUMBER = #{sessionNumber}

		)
		  A
	GROUP BY ORG_FLOW,ORG_NAME
	) B
	ORDER BY B.COMPLETE_NUM DESC

SELECT A.DOCTOR_FLOW, A.ORG_NAME, A.ORG_FLOW, A.USER_NAME, A.RECRUIT_FLOW, A.TRAINING_SPE_ID, A.TRAINING_SPE_NAME, A.SESSION_NUMBER, A.REQ_NUM, A.COMPLETE_NUM, A.AUDIT_NUM, A.ROTATION_NUM, A.YLZ_NUM, A.SCH_NUM, A.AFTER_NUM, A.COMPLETE_BI, A.AUDIT_BI
FROM (SELECT rd.DOCTOR_FLOW, DR.ORG_FLOW, ORG2.ORG_CODE, SU.USER_NAME, SU.USER_FLOW, DR.SPE_ID TRAINING_SPE_ID, DR.SPE_NAME TRAINING_SPE_NAME, DR.SESSION_NUMBER, DR.RECRUIT_FLOW, u.ORG_NAME, JDW.REQ_NUM, NVL(JDW.COMPLETE_NUM, 0) COMPLETE_NUM, NVL(JDW.AUDIT_NUM, 0) AUDIT_NUM, NVL(JDW.YLZ_NUM, 0) YLZ_NUM, NVL(JDW.SCH_NUM, 0) SCH_NUM, NVL(JDW.AFTER_NUM, 0) AFTER_NUM, to_number(NVL(JDW.COMPLETE_BI,0)) COMPLETE_BI, to_number(NVL(JDW.AUDIT_BI,0)) AUDIT_BI, JDW.rotation_Num FROM RES_DOCTOR_RECRUIT dr left join pdsci.SYS_USER u on dr.DOCTOR_FLOW = u.USER_FLOW LEFT JOIN RES_DOCTOR RD ON DR.DOCTOR_FLOW = RD.DOCTOR_FLOW INNER JOIN SYS_USER SU ON RD.DOCTOR_FLOW = SU.USER_FLOW LEFT JOIN JSRES_DOCTOR_WORK JDW ON RD.DOCTOR_FLOW =JDW.DOCTOR_FLOW and dr.org_flow=jdw.org_flow AND DR.RECRUIT_FLOW =JDW.RECRUIT_FLOW LEFT JOIN SYS_ORG ORG2 ON ORG2.ORG_FLOW=DR.ORG_FLOW WHERE RD.RECORD_STATUS = 'Y' AND DR.RECORD_STATUS='Y' AND SU.RECORD_STATUS = 'Y' AND dr.AUDIT_STATUS_ID = 'Passed'
and rd.DOCTOR_TYPE_ID in #{item}
AND DR.ORG_FLOW IN ( SELECT ORG_FLOW FROM SYS_ORG ORG WHERE ORG.RECORD_STATUS = 'Y' AND ORG.ORG_PROV_ID = '320000'
AND ORG.ORG_CITY_ID = #{orgCityId}
AND ORG.ORG_TYPE_ID = 'Hospital'
AND (
ORG.ORG_LEVEL_ID = #{orgLevel}
AND ORG.ORG_FLOW = #{orgFlow}
ORG.ORG_FLOW = #{orgFlow}
OR org.org_flow in ( select joint_org_flow from RES_JOINT_ORG JOINT WHERE RECORD_STATUS='Y'
AND JOINT.ORG_FLOW = #{orgFlow}
)
)
AND ORG.ORG_LEVEL_ID = #{orgLevel}
AND ORG.ORG_FLOW = #{orgFlow}
)
AND rd.TRAINING_TYPE_ID = #{typeId}
AND rd.TRAINING_SPE_ID = #{speId}
and DR.SESSION_NUMBER in #{item}
AND DR.SESSION_NUMBER = #{sessionNumber}
AND SU.USER_NAME LIKE #{name}
AND su.ID_NO LIKE #{idNo}
AND DR.GRADUATION_YEAR = #{graduationYear}
) A
ORDER BY A.ORG_CODE , NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M'),A.USER_FLOW
ORDER BY COMPLETE_BI DESC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M'),A.USER_FLOW
ORDER BY COMPLETE_BI ASC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M'),A.USER_FLOW
ORDER BY AUDIT_BI DESC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M'),A.USER_FLOW
ORDER BY AUDIT_BI ASC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M'),A.USER_FLOW

SELECT T.RECRUIT_FLOW,NVL(AVG_COMPLETE,0) AVG_COMPLETE,NVL(AVG_IN_COMPLETE,0) AVG_IN_COMPLETE,NVL(AVG_OUT_COMPLETE,0) AVG_OUT_COMPLETE,NVL(AVG_AUDIT,0) AVG_AUDIT
		  FROM jsres_graduation_apply T
		 WHERE  T.RECORD_STATUS = 'Y'
		 and T.RECRUIT_FLOW = #{recruitFlow}
		 and t.apply_year=#{applyYear}

select a.* from (select rd.DOCTOR_NAME, rd.TRAINING_SPE_NAME, rd.SESSION_NUMBER, rdsp.SCH_DEPT_NAME, rdsp.SCH_DEPT_FLOW, rdsp.TEACHER_USER_NAME, to_char((select count(0) from RES_SCH_PROCESS_EXPRESS rspe2 where rspe2.process_flow = rdsp.process_flow and rspe2.rec_type_id = 'AfterSummary' and rspe2.RECORD_STATUS = 'Y' and rdsp.RECORD_STATUS = 'Y')) SUBMIT_NUMBER, to_char(to_date(substr(rspe.CREATE_TIME,1,8),'YYYYMMDD'),'yyyy.mm.dd') CREATE_TIME, rspe.AUDIT_STATUS_NAME, to_char(to_date(substr(rspe.AUDIT_TIME,1,8),'YYYYMMDD'),'yyyy.mm.dd') AUDIT_TIME, rspe.HEAD_AUDIT_STATUS_NAME, to_char(to_date(substr(rspe.HEAD_AUDIT_TIME,1,8),'YYYYMMDD'),'yyyy.mm.dd') HEAD_AUDIT_TIME, rspe.REC_TYPE_ID, rdsp.PROCESS_FLOW from RES_DOCTOR_SCH_PROCESS rdsp left join RES_DOCTOR rd on rdsp.user_flow = rd.doctor_flow and rd.RECORD_STATUS = 'Y' left join RES_SCH_PROCESS_EXPRESS rspe on rspe.process_flow = rdsp.process_flow and rspe.rec_type_id = 'AfterSummary' and rspe.RECORD_STATUS = 'Y' where rdsp.RECORD_STATUS = 'Y'
and rdsp.org_flow = #{orgFlow}
and rdsp.SCH_DEPT_FLOW = #{schDeptFlow}
and rd.doctor_Type_Id in
#{item}
and rd.session_Number = #{sessionNumber}
and rspe.AUDIT_STATUS_ID = #{isAudit}
and rspe.AUDIT_STATUS_ID is null
and rspe.HEAD_AUDIT_STATUS_ID = #{isHeadAudit}
and rspe.HEAD_AUDIT_STATUS_ID is null
and rd.doctor_Name like '%${doctorName}%'
and rd.training_Spe_Id = #{trainingSpeId}
order by rspe.HEAD_AUDIT_STATUS_ID,rspe.AUDIT_STATUS_ID,SUBMIT_NUMBER desc,CREATE_TIME,rd.doctor_flow ) a where 1=1
and a.submit_number = #{isSubmit}

select su.user_name,su.user_flow,rd.session_number,rd.dept_name,rd.training_spe_name,rd.training_years,rdsp.sch_dept_name,
	rdsp.SCH_START_DATE,rdsp.SCH_END_DATE,rd.GRADUATION_YEAR,rd.IN_HOS_DATE,su.user_code,zhkm.KQ_DATE,
    rd.DOCTOR_TYPE_ID,rd.DEPART_MENT_NAME,rd.WORK_ORG_NAME,
	zhkm.M01,zhkm.M02,zhkm.M03,zhkm.M04,zhkm.M05,zhkm.M06,zhkm.M07,zhkm.M08,zhkm.M09,zhkm.M10,zhkm.M11,zhkm.M12,zhkm.M13,
	zhkm.M14,zhkm.M15,zhkm.M16,zhkm.M17,zhkm.M18,zhkm.M19,zhkm.M20,zhkm.M21,zhkm.M22,zhkm.M23,zhkm.M24,zhkm.M25,zhkm.M26,
	zhkm.M27,zhkm.M28,zhkm.M29,zhkm.M30,zhkm.M31,zhkm.is_submit,zhkm.MONTH_FLOW
from RES_DOCTOR_SCH_PROCESS rdsp
left join res_doctor rd on rdsp.user_flow = rd.doctor_flow
left join sys_user su on rdsp.user_flow = su.user_flow
left join ZSEY_HR_KQ_MONTH zhkm on rdsp.user_flow = zhkm.user_flow

	and zhkm.Kq_Date = #{KqDate}

and zhkm.RECORD_STATUS='Y'
where rd.RECORD_STATUS='Y' and rdsp.RECORD_STATUS='Y' and su.RECORD_STATUS='Y'
and #{KqDate} >= substr(rdsp.SCH_START_DATE,0,7)
and substr(rdsp.SCH_end_DATE,0,7) >= #{KqDate}

	and su.user_name like '%${userName}%'


	and rd.session_Number = #{sessionNumber}


	and rd.depart_ment_flow = #{deptFlow}


	and rd.training_Spe_Id = #{trainingSpeId}


	and rd.training_years = #{trainingYears}


	and rdsp.sch_dept_flow = #{schDeptFlow}


	and rd.GRADUATION_YEAR = #{graduationYear}


	and rd.IN_HOS_DATE like '%${inHosDate}%'


	and rd.doctor_Type_Id in

		#{item}



	and rdsp.sch_DEPT_FLOW in

		#{item}




		and

			(zhkm.${item} = '20' or zhkm.${item} = '21')





		and

			(zhkm.${item} is not null and zhkm.${item} != '20' and zhkm.${item} != '21' and zhkm.${item} != '00')



order by su.user_code

select DISTINCT su.user_name,rd.session_number,rd.dept_name,rd.training_spe_name,rd.training_years,su.user_code,
rd.GRADUATION_YEAR,rd.IN_HOS_DATE,zhkm.KQ_DATE,rd.doctor_Type_Id,rd.depart_Ment_Name,rd.work_Org_Name,
zhkm.M01,zhkm.M02,zhkm.M03,zhkm.M04,zhkm.M05,zhkm.M06,zhkm.M07,zhkm.M08,zhkm.M09,zhkm.M10,zhkm.M11,zhkm.M12,zhkm.M13,
zhkm.M14,zhkm.M15,zhkm.M16,zhkm.M17,zhkm.M18,zhkm.M19,zhkm.M20,zhkm.M21,zhkm.M22,zhkm.M23,zhkm.M24,zhkm.M25,zhkm.M26,
zhkm.M27,zhkm.M28,zhkm.M29,zhkm.M30,zhkm.M31,zhkm.is_submit,zhkm.MONTH_FLOW
from res_doctor rd
left join ZSEY_HR_KQ_MONTH zhkm on zhkm.USER_FLOW = rd.doctor_flow and zhkm.RECORD_STATUS='Y'

	and zhkm.KQ_DATE in

		#{item}


left join sys_user su on rd.doctor_flow = su.user_flow
left join res_doctor_sch_process rdsp on rd.doctor_flow = rdsp.user_flow
where rd.RECORD_STATUS='Y' and su.RECORD_STATUS='Y' and rdsp.RECORD_STATUS='Y'

	and su.user_flow = #{userFlow}


