SELECT A.USER_FLOW, A.USER_NAME, A.USER_CODE,WM_CONCAT(TRIM(SUD.DEPT_FLOW)) DEPT_FLOW ,WM_CONCAT(TRIM(SUD.DEPT_NAME)) DEPT_NAME , A.activityNum,A.currStudentHe,A.currStudentCount from ( SELECT su.user_flow, su.user_name, su.dept_flow, su.dept_name, su.user_code, nvl(tai.activityNum,0) activityNum, nvl(p.currStudentHe,0) currStudentHe, nvl(p.currStudentCount,0) currStudentCount FROM sys_user su LEFT JOIN ( SELECT SPEAKER_FLOW, COUNT(1) AS activityNum FROM TEACHING_ACTIVITY_INFO WHERE RECORD_STATUS = 'Y' AND IS_EFFECTIVE = 'Y'
AND((START_TIME <= #{newStartDate} AND END_TIME >= #{newEndDate}) or (START_TIME >= #{newStartDate} AND START_TIME <= #{newEndDate}) or (END_TIME >= #{newStartDate} AND END_TIME <= #{newEndDate}))
GROUP BY SPEAKER_FLOW ) tai ON tai.speaker_flow = su.user_flow LEFT JOIN ( SELECT RESP.TEACHER_USER_FLOW, COUNT(DISTINCT resp.user_flow) currStudentHe, sum(months_between(to_date(substr(
CASE WHEN resp.SCH_END_DATE >= '${endDate1}' THEN '${endDate1}' ELSE resp.SCH_END_DATE END
resp.SCH_END_DATE
,0,7),'yyyy-mm'), to_date(substr(
CASE WHEN resp.SCH_START_DATE <= '${startDate1}' THEN '${startDate1}' ELSE resp.SCH_START_DATE END
resp.SCH_START_DATE , 0,7),'yyyy-mm')) + 1) currStudentCount from res_doctor_sch_process resp left join sys_user du on resp.user_flow = du.user_flow LEFT JOIN res_doctor rd ON du.user_flow = rd.doctor_flow left join jsres_power_cfg sc on sc.record_status='Y' and sc.cfg_code = ('jsres_doctor_data_time_' || rd.doctor_flow) left join jsres_power_cfg ec on ec.record_status='Y' and ec.cfg_code = ('jsres_doctor_app_menu_' || rd.doctor_flow) WHERE du.record_status = 'Y' AND resp.record_status = 'Y' AND rd.record_status = 'Y'
and rd.doctor_Type_Id in
#{item}
AND resp.user_flow = du.user_flow AND (resp.SCH_START_DATE <= nvl( sc.power_start_time, '0000-00-00') AND resp.SCH_END_DATE >= nvl( sc.power_end_time, '9999-99-99') OR resp.SCH_START_DATE >= nvl( sc.power_start_time, '0000-00-00') AND resp.SCH_START_DATE <= nvl(sc.power_end_time, '9999-99-99') OR resp.SCH_END_DATE >= nvl( sc.power_start_time, '0000-00-00') AND resp.SCH_END_DATE <= nvl(sc.power_end_time, '9999-99-99')) AND ec.CFG_VALUE = 'Y'
AND( ( resp.SCH_START_DATE <= #{startDate1} AND resp.SCH_END_DATE >= #{endDate1}) or (resp.SCH_START_DATE >= #{startDate1} AND resp.SCH_START_DATE <= #{endDate1}) or (resp.SCH_END_DATE >= #{startDate1} AND resp.SCH_END_DATE <= #{endDate1}))
and exists( select 1 from sys_user where user_flow= resp.teacher_user_flow and org_flow=#{orgFlow} )
group by resp.teacher_user_flow) p ON p.teacher_user_flow = su.user_flow WHERE su.record_status = 'Y' and su.IS_EXAM_TEA is null
and su.ORG_FLOW=#{orgFlow}
and su.DEPT_FLOW like #{sysUser.deptFlow}
and su.USER_NAME like #{userName}
and exists( select 1 from sys_user_role sur where sur.user_flow=su.user_flow AND sur.record_status = 'Y'
and sur.ROLE_FLOW=#{roleFlow}
) AND NOT EXISTS (SELECT 1 FROM res_doctor rd WHERE su.user_flow = rd.doctor_flow AND rd.RECORD_STATUS = 'Y')
)A LEFT JOIN SYS_USER_DEPT SUD ON SUD.USER_FLOW=A.USER_FLOW WHERE SUD.RECORD_STATUS='Y' GROUP BY A.USER_FLOW,A.USER_NAME,A.USER_CODE,A.ACTIVITYNUM,A.CURRSTUDENTHE,A.CURRSTUDENTCOUNT
ORDER BY A. ${orderItem} ${sortName},A.USER_FLOW

SELECT su.user_flow, su.user_name, su.dept_flow, su.dept_name, su.user_code, COUNT(DISTINCT tai.activity_flow) AS activityNum, nvl(p.currStudentHe,0) currStudentHe, nvl(p.currStudentCount,0) currStudentCount, ( select wm_concat(A.DOCTOR_NAME) from ( select P.TEACHER_USER_FLOW TEACHER_USER_FLOW, p.PROCESS_FLOW PROCESS_FLOW, su.user_flow user_flow, p.SCH_START_DATE SCH_START_DATE, p.SCH_END_DATE SCH_END_DATE, RD.DOCTOR_TYPE_ID, RD.DOCTOR_FLOW, p.SCH_DEPT_NAME, p.SCH_DEPT_FLOW, RD.DOCTOR_NAME, RD.SESSION_NUMBER, RD.TRAINING_SPE_NAME, nvl((select CFG_VALUE from jsres_power_cfg cfg where cfg.record_status='Y' and cfg.cfg_code = 'jsres_doctor_app_menu_'||rd.doctor_flow),'N')APP_MENU_FLAG, nvl(cfg2.power_start_time,'0000-00-00') power_start_time, nvl(cfg2.power_end_time,'9999-99-99') power_end_time from res_doctor_sch_process p left join res_doctor rd on p.user_flow = rd.doctor_flow left join sys_user su on su.user_flow = rd.doctor_flow left join sys_user tu on tu.user_flow = p.teacher_user_flow left join jsres_power_cfg cfg2 on cfg2.record_status='Y' and cfg2.cfg_code = 'jsres_doctor_data_time_'||rd.doctor_flow where p.record_status = 'Y' and rd.record_status = 'Y' and su.record_status = 'Y' and tu.record_status = 'Y'
and rd.doctor_Type_Id in
#{item}
) A WHERE a.APP_MENU_FLAG='Y' AND ( ( a.SCH_START_DATE <= A.power_start_time AND a.SCH_END_DATE >= A.power_END_time) or (a.SCH_START_DATE >= A.power_start_time AND a.SCH_START_DATE <= A.power_END_time) or (a.SCH_END_DATE >= A.power_start_time AND a.SCH_END_DATE <= A.power_END_time))
AND( ( a.SCH_START_DATE <= #{doctorStartDate} AND a.SCH_END_DATE >= #{doctorEndDate}) or (a.SCH_START_DATE >= #{doctorStartDate} AND a.SCH_START_DATE <= #{doctorEndDate}) or (a.SCH_END_DATE >= #{doctorStartDate} AND a.SCH_END_DATE <= #{doctorEndDate}))
and a.TEACHER_USER_FLOW = su.user_flow ) as doctorList FROM sys_user su LEFT JOIN teaching_activity_info tai ON tai.record_status = 'Y' AND tai.speaker_flow = su.user_flow
AND( ( tai.START_TIME <= #{newStartDate} AND tai.END_TIME >= #{newEndDate}) or (tai.START_TIME >= #{newStartDate} AND tai.START_TIME <= #{newEndDate}) or (tai.END_TIME >= #{newStartDate} AND tai.END_TIME <= #{newEndDate}))
LEFT JOIN (select resp.teacher_user_flow, COUNT(DISTINCT resp.user_flow) currStudentHe, sum(months_between(to_date(substr(
CASE WHEN resp.SCH_END_DATE >= '${endDate1}' THEN '${endDate1}' ELSE resp.SCH_END_DATE END
resp.SCH_END_DATE
, 0, 7), 'yyyy-mm'), to_date(substr(
CASE WHEN resp.SCH_START_DATE <= '${startDate1}' THEN '${startDate1}' ELSE resp.SCH_START_DATE END
resp.SCH_START_DATE , 0, 7), 'yyyy-mm')) + 1) currStudentCount from res_doctor_sch_process resp left join sys_user du on resp.user_flow = du.user_flow LEFT JOIN res_doctor rd ON du.user_flow = rd.doctor_flow left join jsres_power_cfg sc on sc.record_status='Y' and sc.cfg_code = ('jsres_doctor_data_time_' || rd.doctor_flow) left join jsres_power_cfg ec on ec.record_status='Y' and ec.cfg_code = ('jsres_doctor_app_menu_' || rd.doctor_flow) WHERE du.record_status = 'Y' AND resp.record_status = 'Y' AND rd.record_status = 'Y'
and rd.doctor_Type_Id in
#{item}
AND resp.user_flow = du.user_flow AND (resp.SCH_START_DATE <= nvl( sc.power_start_time, '0000-00-00') AND resp.SCH_END_DATE >= nvl( sc.power_end_time, '9999-99-99') OR resp.SCH_START_DATE >= nvl( sc.power_start_time, '0000-00-00') AND resp.SCH_START_DATE <= nvl(sc.power_end_time, '9999-99-99') OR resp.SCH_END_DATE >= nvl( sc.power_start_time, '0000-00-00') AND resp.SCH_END_DATE <= nvl(sc.power_end_time, '9999-99-99')) AND ec.CFG_VALUE = 'Y'
AND( ( resp.SCH_START_DATE <= #{startDate1} AND resp.SCH_END_DATE >= #{endDate1}) or (resp.SCH_START_DATE >= #{startDate1} AND resp.SCH_START_DATE <= #{endDate1}) or (resp.SCH_END_DATE >= #{startDate1} AND resp.SCH_END_DATE <= #{endDate1}))
and exists( select 1 from sys_user where user_flow= resp.teacher_user_flow and org_flow=#{orgFlow} )
group by resp.teacher_user_flow) p ON p.teacher_user_flow = su.user_flow WHERE su.record_status = 'Y' and su.IS_EXAM_TEA is null
and su.ORG_FLOW=#{orgFlow}
and su.DEPT_FLOW like #{sysUser.deptFlow}
and su.USER_NAME like #{userName}
and exists( select 1 from sys_user_role sur where sur.user_flow=su.user_flow AND sur.record_status = 'Y'
and sur.ROLE_FLOW=#{roleFlow}
) AND NOT EXISTS (SELECT NULL FROM res_doctor rd WHERE su.user_flow = rd.doctor_flow AND rd.RECORD_STATUS = 'Y') GROUP BY su.user_flow, su.user_name, su.dept_flow, su.dept_name, su.user_code, p.currStudentHe, p.currStudentCount
ORDER BY ${orderItem} ${sortName} ,user_flow

select tai.* from teaching_activity_info tai where tai.record_status='Y'
and tai.speaker_flow=#{speakerFlow}
AND( ( tai.START_TIME <= #{startTime} AND tai.END_TIME >= #{endTime}) or (tai.START_TIME >= #{startTime} AND tai.START_TIME <= #{endTime}) or (tai.END_TIME >= #{startTime} AND tai.END_TIME <= #{endTime}))

SELECT rdsp.*
FROM res_doctor_sch_process rdsp
left join sch_arrange_result sar on rdsp.sch_result_flow = sar.result_flow
where rdsp.record_status = 'Y'
and sar.record_status = 'Y'

	and sar.Standard_Dept_Id = #{standardDeptId}


	and sar.standard_group_flow = #{standardGroupFlow}


	and sar.doctor_flow  = #{doctorFlow}

SELECT RDSP.*
FROM PDSCI.RES_DOCTOR_SCH_PROCESS RDSP
left join PDSCI.RES_DOCTOR RD
on RDSP.USER_FLOW = RD.DOCTOR_FLOW
WHERE RDSP.RECORD_STATUS = 'Y'
AND RD.RECORD_STATUS = 'Y'
AND RD.DOCTOR_STATUS_NAME in( '','')


	AND RDSP.DEPT_FLOW = #{deptFlow}


		AND RDSP.DEPT_FLOW IN (

			#{deptFlow}
		)

AND RDSP.ORG_FLOW =  #{orgFlow}


	AND RDSP.TEACHER_USER_FLOW = #{teacherUserFlow}


	and rd.DOCTOR_TYPE_ID in
	#{item}



		AND RD.DOCTOR_NAME LIKE '%${doctor.doctorName}%'


		AND RD.TRAINING_SPE_ID = #{doctor.trainingSpeId}


		AND RD.SESSION_NUMBER = #{doctor.sessionNumber}




		AND EXISTS (SELECT 1 FROM DEPT_TEACHER_GRADE_INFO DTGI
		WHERE DTGI.RECORD_STATUS='Y'
		AND DTGI.PROCESS_FLOW=RDSP.PROCESS_FLOW

			AND DTGI.REC_TYPE_ID='NurseDoctorGrade'


			AND DTGI.REC_TYPE_ID='TeacherDoctorGrade'

		AND DTGI.OPER_USER_FLOW = #{userFlow}
		)


		AND not EXISTS (SELECT 1 FROM DEPT_TEACHER_GRADE_INFO DTGI
		WHERE DTGI.RECORD_STATUS='Y'
		AND DTGI.PROCESS_FLOW=RDSP.PROCESS_FLOW

			AND DTGI.REC_TYPE_ID='NurseDoctorGrade'


			AND DTGI.REC_TYPE_ID='TeacherDoctorGrade'

		AND DTGI.OPER_USER_FLOW = #{userFlow}
		)


ORDER by RD.session_Number DESC,RD.TRAINING_SPE_ID desc,rd.doctor_flow

SELECT su.user_flow, su.user_name, su.dept_flow, su.dept_name, su.user_code, su.org_name, nvl(tai.activityNum, 0) activityNum, nvl(p.currStudentHe,0) currStudentHe, nvl(p.currStudentCount,0) currStudentCount FROM sys_user su LEFT JOIN ( SELECT speaker_flow, COUNT(1) AS activityNum FROM TEACHING_ACTIVITY_INFO WHERE RECORD_STATUS = 'Y' AND IS_EFFECTIVE = 'Y'
AND((START_TIME <= #{newStartDate} AND END_TIME >= #{newEndDate}) or (START_TIME >= #{newStartDate} AND START_TIME <= #{newEndDate}) or (END_TIME >= #{newStartDate} AND END_TIME <= #{newEndDate}))
GROUP BY SPEAKER_FLOW ) tai on TAI.SPEAKER_FLOW = su.user_flow LEFT JOIN (select resp.teacher_user_flow, COUNT(DISTINCT resp.user_flow) currStudentHe, sum(months_between(to_date(substr(
CASE WHEN resp.SCH_END_DATE >= '${endDate}' THEN '${endDate}' ELSE resp.SCH_END_DATE END
resp.SCH_END_DATE
, 0, 7), 'yyyy-mm'), to_date(substr(
CASE WHEN resp.SCH_START_DATE <= '${startDate}' THEN '${startDate}' ELSE resp.SCH_START_DATE END
resp.SCH_START_DATE , 0, 7), 'yyyy-mm')) + 1) currStudentCount from res_doctor_sch_process resp , ( SELECT DOCTOR_FLOW FROM RES_DOCTOR WHERE RECORD_STATUS = 'Y' AND DOCTOR_TYPE_ID IN
#{item}
AND WORK_ORG_ID = #{workOrgId} ) rd left join jsres_power_cfg sc on sc.record_status='Y' and sc.cfg_code = ('jsres_doctor_data_time_' || rd.doctor_flow) left join jsres_power_cfg ec on ec.record_status='Y' AND ec.CFG_VALUE = 'Y' and ec.cfg_code = ('jsres_doctor_app_menu_' || rd.doctor_flow) WHERE resp.user_flow = rd.doctor_flow AND resp.record_status = 'Y' AND (resp.SCH_START_DATE <= nvl( sc.power_start_time, '0000-00-00') AND resp.SCH_END_DATE >= nvl( sc.power_end_time, '9999-99-99') OR resp.SCH_START_DATE >= nvl( sc.power_start_time, '0000-00-00') AND resp.SCH_START_DATE <= nvl(sc.power_end_time, '9999-99-99') OR resp.SCH_END_DATE >= nvl( sc.power_start_time, '0000-00-00') AND resp.SCH_END_DATE <= nvl(sc.power_end_time, '9999-99-99'))
AND( ( resp.SCH_START_DATE <= #{startDate} AND resp.SCH_END_DATE >= #{endDate}) or (resp.SCH_START_DATE >= #{startDate} AND resp.SCH_START_DATE <= #{endDate}) or (resp.SCH_END_DATE >= #{startDate} AND resp.SCH_END_DATE <= #{endDate}))
AND EXISTS ( SELECT 1 FROM sys_user WHERE user_flow = resp.teacher_user_flow AND org_flow in #{item} )
AND EXISTS ( SELECT 1 FROM sys_user WHERE user_flow = resp.teacher_user_flow AND org_flow = #{orgFlow} )
group by resp.teacher_user_flow) p ON p.teacher_user_flow = su.user_flow WHERE su.record_status = 'Y' and su.IS_EXAM_TEA is null
and su.ORG_FLOW in #{item}
and su.org_flow = #{orgFlow}
and su.DEPT_FLOW = #{sysUser.deptFlow}
and su.USER_NAME like #{userName}
and exists( SELECT 1 FROM sys_user_role sur WHERE sur.user_flow = su.user_flow AND sur.record_status = 'Y'
and sur.ROLE_FLOW = #{roleFlow}
) AND NOT EXISTS ( SELECT 1 FROM res_doctor rd WHERE su.user_flow = rd.doctor_flow AND rd.RECORD_STATUS = 'Y' )
ORDER BY ${orderItem} ${sortName} ,user_flow

SELECT A.DOCTOR_FLOW, A.RECRUIT_FLOW, A.ORG_NAME, A.ORG_FLOW, A.USER_NAME, A.TRAINING_SPE_ID, A.TRAINING_SPE_NAME, A.SESSION_NUMBER, A.REQ_NUM, A.COMPLETE_NUM, A.AUDIT_NUM, A.ROTATION_NUM, A.YLZ_NUM, A.SCH_NUM, A.AFTER_NUM, A.COMPLETE_BI, A.AUDIT_BI FROM (SELECT rd.DOCTOR_FLOW, DR.RECRUIT_FLOW, DR.ORG_NAME, DR.ORG_FLOW, SU.USER_NAME, DR.SPE_ID TRAINING_SPE_ID, DR.SPE_NAME TRAINING_SPE_NAME, DR.SESSION_NUMBER, NVL(JDW.REQ_NUM,0) REQ_NUM, NVL(JDW.COMPLETE_NUM, 0) COMPLETE_NUM, NVL(JDW.AUDIT_NUM, 0) AUDIT_NUM, NVL(JDW.YLZ_NUM, 0) YLZ_NUM, NVL(JDW.SCH_NUM, 0) SCH_NUM, NVL(JDW.AFTER_NUM, 0) AFTER_NUM, to_number(NVL(JDW.COMPLETE_BI,0)) COMPLETE_BI, to_number(NVL(JDW.AUDIT_BI,0)) AUDIT_BI, NVL(JDW.rotation_Num,0) rotation_Num FROM RES_DOCTOR_RECRUIT dr LEFT JOIN RES_DOCTOR RD ON DR.DOCTOR_FLOW = RD.DOCTOR_FLOW INNER JOIN SYS_USER SU ON RD.DOCTOR_FLOW = SU.USER_FLOW LEFT JOIN JSRES_DOCTOR_WORK JDW ON RD.DOCTOR_FLOW =JDW.DOCTOR_FLOW and dr.org_flow=jdw.org_flow AND DR.RECRUIT_FLOW =JDW.RECRUIT_FLOW WHERE RD.RECORD_STATUS = 'Y' AND DR.RECORD_STATUS='Y' AND SU.RECORD_STATUS = 'Y' AND dr.AUDIT_STATUS_ID = 'Passed'
and rd.DOCTOR_TYPE_ID in #{item}
and rd.DOCTOR_FLOW IN
#{flow}
AND rd.TRAINING_TYPE_ID = #{typeId}
AND rd.TRAINING_SPE_ID = #{speId}
AND DR.SESSION_NUMBER = #{sessionNumber}
AND SU.USER_NAME LIKE #{name}
AND su.ID_NO LIKE #{idNo}
AND DR.GRADUATION_YEAR = #{graduationYear}
) A
ORDER BY COMPLETE_BI DESC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M')
ORDER BY COMPLETE_BI ASC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M')
ORDER BY AUDIT_BI DESC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M')
ORDER BY AUDIT_BI ASC, NLSSORT(A.USER_NAME, 'NLS_SORT = SCHINESE_PINYIN_M')

select u.USER_NAME user_name, dr.DOCTOR_CATEGORY_NAME DOCTOR_CATEGORY_NAME,dr.TRAINING_SPE_NAME training_spe_name,dr.DOCTOR_Flow doctor_flow,dr.org_name,
dr.SESSION_NUMBER session_number, dr.TRAINING_YEARS training_years,dr.TRAINING_SPE_ID training_spe_id,dr.training_type_id training_type_id,dr.training_type_name training_type_name
from RES_DOCTOR dr
inner join SYS_USER u
on dr.DOCTOR_FLOW = u.USER_FLOW
where dr.RECORD_STATUS= 'Y' and u.RECORD_STATUS= 'Y'
AND (dr.DOCTOR_STATUS_ID NOT IN ('UnPassed', 'Passed', 'UnSubmit')
OR dr.DOCTOR_STATUS_ID IS NULL)
AND dr.org_flow IS NOT NULL
AND dr.org_flow != '20160914888888'
AND dr.org_flow != '566c9f36cf014f63a569b453efa284d7'
AND dr.org_flow != '00000000000000000000000000000000'

	and dr.TRAINING_SPE_ID = #{trainingSpeId}


	AND dr.DOCTOR_CATEGORY_ID = #{doctorCategoryId}


	and dr.SESSION_NUMBER = #{sessionNumber}


	and dr.TRAINING_YEARS = #{trainingYears}


	and dr.work_Org_Id = #{workOrgId}



	AND  u.USER_NAME LIKE #{name}



	AND u.ID_NO LIKE #{idNo}


	and dr.DOCTOR_TYPE_ID in
	#{item}


	and dr.DOCTOR_FLOW IN

		#{flow}



	and instr('${medicineTypeId}',u.medicine_Type_Id)>0

order by dr.SESSION_NUMBER desc,dr.TRAINING_SPE_ID,dr.create_time,dr.doctor_flow

SELECT COUNT(*) AS ALL_NUM
FROM RES_DOCTOR_SCH_PROCESS S
LEFT JOIN RES_DOCTOR_RECRUIT R
ON S.USER_FLOW=R.DOCTOR_FLOW
WHERE SUBSTR(S.SCH_START_DATE, 0, 7)  <=  #{searchTime}
AND SUBSTR(s.SCH_END_DATE, 0, 7)  >=  #{searchTime}
AND S.RECORD_STATUS = 'Y'
AND r.RECORD_STATUS = 'Y'
AND s.ORG_FLOW =  #{orgFlow}
AND s.SCH_DEPT_FLOW = #{deptFlow}
AND R.CAT_SPE_ID= #{trainingTypeId}
UNION ALL
SELECT COUNT(*) AS HAVE_NUM
FROM (
SELECT OPER_USER_FLOW, SCH_DEPT_FLOW
FROM RES_REC
WHERE PROCESS_FLOW IN (
	SELECT S.PROCESS_FLOW
	FROM RES_DOCTOR_SCH_PROCESS S
	LEFT JOIN RES_DOCTOR_RECRUIT R
	ON S.USER_FLOW=R.DOCTOR_FLOW
	WHERE SUBSTR(S.sCH_START_DATE, 0, 7)  <=  #{searchTime}
	AND SUBSTR(S.SCH_END_DATE, 0, 7)  >=  #{searchTime}
	AND S.RECORD_STATUS = 'Y'
	AND R.RECORD_STATUS = 'Y'
	AND S.ORG_FLOW =  #{orgFlow}
	AND S.SCH_DEPT_FLOW = #{deptFlow}
	AND R.CAT_SPE_ID=#{trainingTypeId}
)
GROUP BY OPER_USER_FLOW, SCH_DEPT_FLOW
)
UNION ALL
SELECT COUNT(*) AS ALL_CK
FROM RES_DOCTOR_SCH_PROCESS S
LEFT JOIN RES_DOCTOR_RECRUIT R
ON S.USER_FLOW=R.DOCTOR_FLOW
WHERE S.RECORD_STATUS = 'Y'
AND R.RECORD_STATUS = 'Y'
AND SUBSTR(S.SCH_END_DATE, 0, 7) = #{searchTime}
AND S.ORG_FLOW =  #{orgFlow}
AND S.SCH_DEPT_FLOW = #{deptFlow}
AND R.CAT_SPE_ID=#{trainingTypeId}
UNION ALL
SELECT COUNT(*) AS HAVE_CK
FROM PDSCI.RES_SCH_PROCESS_EXPRESS
WHERE AUDIT_STATUS_ID = 'TeacherAuditY'
AND REC_TYPE_ID = 'AfterEvaluation'
AND RECORD_STATUS = 'Y'
AND PROCESS_FLOW IN (
	SELECT s.PROCESS_FLOW
	FROM RES_DOCTOR_SCH_PROCESS S
	LEFT JOIN RES_DOCTOR_RECRUIT R
	ON S.USER_FLOW=R.DOCTOR_FLOW
	WHERE S.RECORD_STATUS = 'Y'
	AND R.RECORD_STATUS = 'Y'
	AND SUBSTR(S.SCH_END_DATE, 0, 7) = #{searchTime}
	AND S.ORG_FLOW =  #{orgFlow}
	AND S.SCH_DEPT_FLOW = #{deptFlow}
	AND R.CAT_SPE_ID= #{trainingTypeId}
)
