mapper.select:
		SELECT *
        FROM SCH_ROTATION_DEPT_REQ
		WHERE RECORD_STATUS = 'Y'
		ROTATION_FLOW IN

  			#{rotationFlow}

 		AND REC_TYPE_ID = #{recTypeId}

 			AND ITEM_NAME LIKE CONCAT(CONCAT('%', #{itemName}),'%')

		AND STANDARD_DEPT_ID IN

  			#{schDeptFlow}

SELECT SRD.ROTATION_FLOW||SRD.GROUP_FLOW||SRD.STANDARD_DEPT_ID||SRDR.REC_TYPE_ID REQ_KEY,SUM(REQ_NUM) REQ_SUM FROM SCH_ROTATION_DEPT_REQ SRDR, SCH_ROTATION_DEPT SRD WHERE SRDR.RECORD_STATUS = 'Y' AND SRD.RECORD_STATUS = 'Y' AND SRDR.REL_RECORD_FLOW = SRD.RECORD_FLOW AND SRD.ROTATION_FLOW IN
#{rotationFlow}
AND SRD.STANDARD_DEPT_ID IN
#{standardDeptId}
and SRD.Org_flow is null GROUP BY SRD.ROTATION_FLOW,SRD.GROUP_FLOW,SRD.STANDARD_DEPT_ID,SRDR.REC_TYPE_ID
